import{_ as i}from"./plugin-vue_export-helper.21dcd24c.js";import{o as p,c as e,e as a}from"./app.0aadd796.js";const l={},r=a('<p>\u6301\u7EED\u96C6\u6210 (Continuous Integration) \u6307\u7684\u662F\uFF0C\u9891\u7E41\u5730 (\u4E00\u5929\u591A\u6B21) \u5C06\u4EE3\u7801\u96C6\u6210\u5230\u4E3B\u5E72\uFF0C\u6BCF\u6B21\u96C6\u6210\u90FD\u901A\u8FC7\u81EA\u52A8\u5316\u7684\u6784\u5EFA (\u5305\u62EC\u7F16\u8BD1\uFF0C\u53D1\u5E03\uFF0C\u81EA\u52A8\u5316\u6D4B\u8BD5) \u6765\u9A8C\u8BC1\uFF0C\u4ECE\u800C\u5C3D\u65E9\u5730\u53D1\u73B0\u96C6\u6210\u9519\u8BEF\u3002</p><p>\u6301\u7EED\u96C6\u6210\u7684\u76EE\u7684\u8BA9\u4EA7\u54C1\u53EF\u4EE5\u5FEB\u901F\u8FED\u4EE3\uFF0C\u540C\u65F6\u8FD8\u80FD\u4FDD\u6301\u9AD8\u8D28\u91CF\u3002\u5C31\u662F\u8BF4\u6BCF\u5B8C\u6210\u4E00\u4E2A\u5B8C\u6574\u7684\u90E8\u5206\uFF0C\u5C31\u5411\u4E0B\u4E2A\u73AF\u8282\u4EA4\u4ED8\uFF0C\u53D1\u73B0\u95EE\u9898\u53EF\u4EE5\u9A6C\u4E0A\u8C03\u6574\uFF0C\u4F7F\u5F97\u95EE\u9898\u4E0D\u4F1A\u653E\u5927\u5230\u5176\u4ED6\u90E8\u5206\u548C\u540E\u9762\u7684\u73AF\u8282\u3002</p><h2 id="\u4ECB\u7ECD" tabindex="-1"><a class="header-anchor" href="#\u4ECB\u7ECD" aria-hidden="true">#</a> \u4ECB\u7ECD</h2><p>\u6301\u7EED\u96C6\u6210 (CI) \u662F\u4E00\u79CD\u9700\u8981\u9891\u7E41\u63D0\u4EA4\u4EE3\u7801\u5230\u5171\u4EAB\u4ED3\u5E93\u7684\u8F6F\u4EF6\u5B9E\u8DF5\u3002\u9891\u7E41\u63D0\u4EA4\u4EE3\u7801\u80FD\u8F83\u65E9\u68C0\u6D4B\u5230\u9519\u8BEF\uFF0C\u51CF\u5C11\u5728\u67E5\u627E\u9519\u8BEF\u6765\u6E90\u65F6\u5F00\u53D1\u8005\u9700\u8981\u8C03\u8BD5\u7684\u4EE3\u7801\u91CF\u3002 \u9891\u7E41\u7684\u4EE3\u7801\u66F4\u65B0\u4E5F\u66F4\u4FBF\u4E8E\u4ECE\u8F6F\u4EF6\u5F00\u53D1\u56E2\u961F\u7684\u4E0D\u540C\u6210\u5458\u5408\u5E76\u66F4\u6539\u3002 \u8FD9\u5BF9\u5F00\u53D1\u8005\u975E\u5E38\u6709\u76CA\uFF0C\u4ED6\u4EEC\u53EF\u4EE5\u5C06\u66F4\u591A\u65F6\u95F4\u7528\u4E8E\u7F16\u5199\u4EE3\u7801\uFF0C\u800C\u51CF\u5C11\u5728\u8C03\u8BD5\u9519\u8BEF\u6216\u89E3\u51B3\u5408\u5E76\u51B2\u7A81\u4E0A\u6240\u82B1\u7684\u65F6\u95F4\u3002</p><p>\u63D0\u4EA4\u4EE3\u7801\u5230\u4ED3\u5E93\u65F6\uFF0C\u53EF\u4EE5\u6301\u7EED\u521B\u5EFA\u5E76\u6D4B\u8BD5\u4EE3\u7801\uFF0C\u4EE5\u786E\u4FDD\u63D0\u4EA4\u672A\u5F15\u5165\u9519\u8BEF\u3002 \u60A8\u7684\u6D4B\u8BD5\u53EF\u4EE5\u5305\u62EC\u4EE3\u7801\u8BED\u6CD5\u68C0\u67E5 (\u68C0\u67E5\u6837\u5F0F\u683C\u5F0F) \u3001\u5B89\u5168\u6027\u68C0\u67E5\u3001\u4EE3\u7801\u8986\u76D6\u7387\u3001\u529F\u80FD\u6D4B\u8BD5\u53CA\u5176\u4ED6\u81EA\u5B9A\u4E49\u68C0\u67E5\u3002</p><p>\u521B\u5EFA\u548C\u6D4B\u8BD5\u4EE3\u7801\u9700\u8981\u670D\u52A1\u5668\u3002 \u60A8\u53EF\u4EE5\u5728\u63A8\u9001\u4EE3\u7801\u5230\u4ED3\u5E93\u4E4B\u524D\u5728\u672C\u5730\u521B\u5EFA\u5E76\u6D4B\u8BD5\u66F4\u65B0\uFF0C\u4E5F\u53EF\u4EE5\u4F7F\u7528 CI \u670D\u52A1\u5668\u68C0\u67E5\u4ED3\u5E93\u4E2D\u7684\u65B0\u4EE3\u7801\u63D0\u4EA4\u3002</p><h3 id="\u8981\u70B9" tabindex="-1"><a class="header-anchor" href="#\u8981\u70B9" aria-hidden="true">#</a> \u8981\u70B9</h3><p>\u5B83\u7684\u6838\u5FC3\u63AA\u65BD\u662F\uFF0C\u4EE3\u7801\u96C6\u6210\u5230\u4E3B\u5E72\u4E4B\u524D\uFF0C\u5FC5\u987B\u901A\u8FC7\u81EA\u52A8\u5316\u6D4B\u8BD5\u3002\u53EA\u8981\u6709\u4E00\u4E2A\u6D4B\u8BD5\u7528\u4F8B\u5931\u8D25\uFF0C\u5C31\u4E0D\u80FD\u96C6\u6210\u3002</p><h3 id="\u4F18\u70B9" tabindex="-1"><a class="header-anchor" href="#\u4F18\u70B9" aria-hidden="true">#</a> \u4F18\u70B9</h3><ol><li><p>\u63D0\u9AD8\u5F00\u53D1\u6548\u7387</p></li><li><p>\u5FEB\u901F\u53D1\u73B0\u5E76\u5B9A\u4F4D Bugs</p></li><li><p>\u66F4\u5FEB\u901F\u53D1\u5E03\u66F4\u65B0</p></li></ol><hr><p>\u4E0E\u6301\u7EED\u96C6\u6210\u76F8\u5173\u7684\uFF0C\u8FD8\u6709\u4E24\u4E2A\u6982\u5FF5\uFF0C\u5206\u522B\u662F\u6301\u7EED\u4EA4\u4ED8\u548C\u6301\u7EED\u90E8\u7F72\u3002</p><h2 id="\u6301\u7EED\u4EA4\u4ED8" tabindex="-1"><a class="header-anchor" href="#\u6301\u7EED\u4EA4\u4ED8" aria-hidden="true">#</a> \u6301\u7EED\u4EA4\u4ED8</h2><p><strong>\u6301\u7EED\u4EA4\u4ED8 (Continuous Delivery) \u6307\u7684\u662F\uFF0C\u9891\u7E41\u5730\u5C06\u8F6F\u4EF6\u7684\u65B0\u7248\u672C\uFF0C\u4EA4\u4ED8\u7ED9\u8D28\u91CF\u56E2\u961F\u6216\u8005\u7528\u6237\uFF0C\u4EE5\u4F9B\u8BC4\u5BA1\u3002</strong> \u5982\u679C\u8BC4\u5BA1\u901A\u8FC7\uFF0C\u4EE3\u7801\u5C31\u8FDB\u5165\u751F\u4EA7\u9636\u6BB5\u3002</p><p>\u6301\u7EED\u4EA4\u4ED8\u53EF\u4EE5\u770B\u4F5C\u6301\u7EED\u96C6\u6210\u7684\u4E0B\u4E00\u6B65\u3002\u5B83\u5F3A\u8C03\u7684\u662F\uFF0C\u4E0D\u7BA1\u600E\u4E48\u66F4\u65B0\uFF0C\u8F6F\u4EF6\u662F\u968F\u65F6\u968F\u5730\u53EF\u4EE5\u4EA4\u4ED8\u7684\u3002</p><p>\u6301\u7EED\u4EA4\u4ED8\u610F\u5473\u7740\u6240\u6709\u7684\u53D8\u66F4\u90FD\u53EF\u4EE5\u88AB\u90E8\u7F72\u5230\u751F\u4EA7\u73AF\u5883\u4E2D\uFF0C\u5982\u679C\u4EE3\u7801\u6CA1\u6709\u95EE\u9898\uFF0C\u53EF\u4EE5\u7EE7\u7EED\u624B\u52A8\u90E8\u7F72\u5230\u751F\u4EA7\u73AF\u5883\u4E2D\u3002</p><h2 id="\u6301\u7EED\u90E8\u7F72" tabindex="-1"><a class="header-anchor" href="#\u6301\u7EED\u90E8\u7F72" aria-hidden="true">#</a> \u6301\u7EED\u90E8\u7F72</h2><p><strong>\u6301\u7EED\u90E8\u7F72 (Continuous Deployment) \u662F\u6301\u7EED\u4EA4\u4ED8\u7684\u4E0B\u4E00\u6B65\uFF0C\u6307\u7684\u662F\u4EE3\u7801\u901A\u8FC7\u8BC4\u5BA1\u4EE5\u540E\uFF0C\u81EA\u52A8\u90E8\u7F72\u5230\u751F\u4EA7\u73AF\u5883\u3002</strong></p><p>\u6301\u7EED\u90E8\u7F72\u7684\u76EE\u6807\u662F\uFF0C\u4EE3\u7801\u5728\u4EFB\u4F55\u65F6\u523B\u90FD\u662F\u53EF\u90E8\u7F72\u7684\uFF0C\u53EF\u4EE5\u8FDB\u5165\u751F\u4EA7\u9636\u6BB5\u3002</p><h2 id="\u6D41\u7A0B" tabindex="-1"><a class="header-anchor" href="#\u6D41\u7A0B" aria-hidden="true">#</a> \u6D41\u7A0B</h2><p>\u4E00\u4E2A\u5927\u578B\u9879\u76EE\u4E25\u683C\u7684\u6D41\u7A0B\u5982\u4E0B:</p><ol><li><p>\u63D0\u4EA4</p><p>\u6D41\u7A0B\u7684\u7B2C\u4E00\u6B65\uFF0C\u662F\u5F00\u53D1\u8005\u5411\u4EE3\u7801\u4ED3\u5E93\u63D0\u4EA4\u4EE3\u7801\u3002\u6240\u6709\u540E\u9762\u7684\u6B65\u9AA4\u90FD\u59CB\u4E8E\u672C\u5730\u4EE3\u7801\u7684\u4E00\u6B21\u63D0\u4EA4 (commit)\u3002</p></li><li><p>\u6D4B\u8BD5 (\u7B2C\u4E00\u8F6E)</p><p>\u4EE3\u7801\u4ED3\u5E93\u5BF9 commit \u64CD\u4F5C\u914D\u7F6E\u4E86\u94A9\u5B50 (hook) \uFF0C\u53EA\u8981\u63D0\u4EA4\u4EE3\u7801\u6216\u8005\u5408\u5E76\u8FDB\u4E3B\u5E72\uFF0C\u5C31\u4F1A\u8DD1\u81EA\u52A8\u5316\u6D4B\u8BD5\u3002</p><p>\u6D4B\u8BD5\u5206\u4E3A\u597D\u51E0\u79CD,</p><ul><li>\u5355\u5143\u6D4B\u8BD5: \u9488\u5BF9\u51FD\u6570\u6216\u6A21\u5757\u7684\u6D4B\u8BD5</li><li>\u96C6\u6210\u6D4B\u8BD5: \u9488\u5BF9\u6574\u4F53\u4EA7\u54C1\u7684\u67D0\u4E2A\u529F\u80FD\u7684\u6D4B\u8BD5\uFF0C\u53C8\u79F0\u529F\u80FD\u6D4B\u8BD5</li><li>\u7AEF\u5BF9\u7AEF\u6D4B\u8BD5: \u4ECE\u7528\u6237\u754C\u9762\u76F4\u8FBE\u6570\u636E\u5E93\u7684\u5168\u94FE\u8DEF\u6D4B\u8BD5</li></ul><p>\u7B2C\u4E00\u8F6E\u81F3\u5C11\u8981\u8DD1\u5355\u5143\u6D4B\u8BD5\u3002</p></li><li><p>\u6784\u5EFA</p><p>\u901A\u8FC7\u7B2C\u4E00\u8F6E\u6D4B\u8BD5\uFF0C\u4EE3\u7801\u5C31\u53EF\u4EE5\u5408\u5E76\u8FDB\u4E3B\u5E72\uFF0C\u5C31\u7B97\u53EF\u4EE5\u4EA4\u4ED8\u4E86\u3002</p><p>\u4EA4\u4ED8\u540E\uFF0C\u5C31\u5148\u8FDB\u884C\u6784\u5EFA (build)\uFF0C\u518D\u8FDB\u5165\u7B2C\u4E8C\u8F6E\u6D4B\u8BD5\u3002\u6240\u8C13\u6784\u5EFA\uFF0C\u6307\u7684\u662F\u5C06\u6E90\u7801\u8F6C\u6362\u4E3A\u53EF\u4EE5\u8FD0\u884C\u7684\u5B9E\u9645\u4EE3\u7801\uFF0C\u6BD4\u5982\u5B89\u88C5\u4F9D\u8D56\uFF0C\u914D\u7F6E\u5404\u79CD\u8D44\u6E90 (\u6837\u5F0F\u8868\u3001JS \u811A\u672C\u3001\u56FE\u7247) \u7B49\u7B49\u3002</p><p>\u5E38\u7528\u7684\u6784\u5EFA\u5DE5\u5177\u5982\u4E0B,</p><ul><li>Jenkins</li><li>Travis</li><li>Codeship</li><li>Strider</li></ul><p>Jenkins \u548C Strider \u662F\u5F00\u6E90\u8F6F\u4EF6\uFF0C Travis \u548C Codeship \u5BF9\u4E8E\u5F00\u6E90\u9879\u76EE\u53EF\u4EE5\u514D\u8D39\u4F7F\u7528\u3002\u5B83\u4EEC\u90FD\u4F1A\u5C06\u6784\u5EFA\u548C\u6D4B\u8BD5\uFF0C\u5728\u4E00\u6B21\u8FD0\u884C\u4E2D\u6267\u884C\u5B8C\u6210\u3002</p></li><li><p>\u6D4B\u8BD5 (\u7B2C\u4E8C\u8F6E)</p><p>\u6784\u5EFA\u5B8C\u6210\uFF0C\u5C31\u8981\u8FDB\u884C\u7B2C\u4E8C\u8F6E\u6D4B\u8BD5\u3002\u5982\u679C\u7B2C\u4E00\u8F6E\u5DF2\u7ECF\u6DB5\u76D6\u4E86\u6240\u6709\u6D4B\u8BD5\u5185\u5BB9\uFF0C\u7B2C\u4E8C\u8F6E\u53EF\u4EE5\u7701\u7565\uFF0C\u5F53\u7136\uFF0C\u8FD9\u65F6\u6784\u5EFA\u6B65\u9AA4\u4E5F\u8981\u79FB\u5230\u7B2C\u4E00\u8F6E\u6D4B\u8BD5\u524D\u9762\u3002</p><p>\u7B2C\u4E8C\u8F6E\u662F\u5168\u9762\u6D4B\u8BD5\uFF0C\u5355\u5143\u6D4B\u8BD5\u548C\u96C6\u6210\u6D4B\u8BD5\u90FD\u4F1A\u8DD1\uFF0C\u6709\u6761\u4EF6\u7684\u8BDD\uFF0C\u4E5F\u8981\u505A\u7AEF\u5BF9\u7AEF\u6D4B\u8BD5\u3002\u6240\u6709\u6D4B\u8BD5\u4EE5\u81EA\u52A8\u5316\u4E3A\u4E3B\uFF0C\u5C11\u6570\u65E0\u6CD5\u81EA\u52A8\u5316\u7684\u6D4B\u8BD5\u7528\u4F8B\uFF0C\u5C31\u8981\u4EBA\u5DE5\u8DD1\u3002</p><p>\u9700\u8981\u5F3A\u8C03\u7684\u662F\uFF0C\u65B0\u7248\u672C\u7684\u6BCF\u4E00\u4E2A\u66F4\u65B0\u70B9\u90FD\u5FC5\u987B\u6D4B\u8BD5\u5230\u3002\u5982\u679C\u6D4B\u8BD5\u7684\u8986\u76D6\u7387\u4E0D\u9AD8\uFF0C\u8FDB\u5165\u540E\u9762\u7684\u90E8\u7F72\u9636\u6BB5\u540E\uFF0C\u5F88\u53EF\u80FD\u4F1A\u51FA\u73B0\u4E25\u91CD\u7684\u95EE\u9898\u3002</p></li><li><p>\u90E8\u7F72</p><p>\u901A\u8FC7\u4E86\u7B2C\u4E8C\u8F6E\u6D4B\u8BD5\uFF0C\u5F53\u524D\u4EE3\u7801\u5C31\u662F\u4E00\u4E2A\u53EF\u4EE5\u76F4\u63A5\u90E8\u7F72\u7684\u7248\u672C (artifact)\u3002\u5C06\u8FD9\u4E2A\u7248\u672C\u7684\u6240\u6709\u6587\u4EF6\u6253\u5305 (tar filename.tar *) \u5B58\u6863\uFF0C\u53D1\u5230\u751F\u4EA7\u670D\u52A1\u5668\u3002</p><p>\u751F\u4EA7\u670D\u52A1\u5668\u5C06\u6253\u5305\u6587\u4EF6\uFF0C\u89E3\u5305\u6210\u672C\u5730\u7684\u4E00\u4E2A\u76EE\u5F55\uFF0C\u518D\u5C06\u8FD0\u884C\u8DEF\u5F84\u7684\u7B26\u53F7\u94FE\u63A5 (symlink) \u6307\u5411\u8FD9\u4E2A\u76EE\u5F55\uFF0C\u7136\u540E\u91CD\u65B0\u542F\u52A8\u5E94\u7528\u3002\u8FD9\u65B9\u9762\u7684\u90E8\u7F72\u5DE5\u5177\u6709 Ansible\uFF0C Chef\uFF0C Puppet \u7B49\u3002</p></li><li><p>\u56DE\u6EDA</p><p>\u4E00\u65E6\u5F53\u524D\u7248\u672C\u53D1\u751F\u95EE\u9898\uFF0C\u5C31\u8981\u56DE\u6EDA\u5230\u4E0A\u4E00\u4E2A\u7248\u672C\u7684\u6784\u5EFA\u7ED3\u679C\u3002\u6700\u7B80\u5355\u7684\u505A\u6CD5\u5C31\u662F\u4FEE\u6539\u4E00\u4E0B\u7B26\u53F7\u94FE\u63A5\uFF0C\u6307\u5411\u4E0A\u4E00\u4E2A\u7248\u672C\u7684\u76EE\u5F55\u3002</p></li></ol>',22),t=[r];function n(h,o){return p(),e("div",null,t)}var c=i(l,[["render",n],["__file","ci.html.vue"]]);export{c as default};
