import{_ as n}from"./plugin-vue_export-helper.21dcd24c.js";import{o as i,c as a,e}from"./app.8c8575b4.js";const p={},t=e('<h1 id="\u684C\u9762\u7CFB\u7EDF" tabindex="-1"><a class="header-anchor" href="#\u684C\u9762\u7CFB\u7EDF" aria-hidden="true">#</a> \u684C\u9762\u7CFB\u7EDF</h1><h2 id="kde-\u684C\u9762\u7CFB\u7EDF" tabindex="-1"><a class="header-anchor" href="#kde-\u684C\u9762\u7CFB\u7EDF" aria-hidden="true">#</a> KDE \u684C\u9762\u7CFB\u7EDF</h2><p>KDE \u662F K Desktop Environment \u7684\u7F29\u5199\uFF0C\u4E2D\u6587\u8BD1\u4E3A\u201CK \u684C\u9762\u73AF\u5883\u201D\u3002</p><p>KDE \u662F\u57FA\u4E8E\u5927\u540D\u9F0E\u9F0E\u7684 Qt \u7684\uFF0C\u6700\u521D\u4E8E 1996 \u5E74\u4F5C\u4E3A\u5F00\u6E90\u9879\u76EE\u516C\u5E03\uFF0C\u5E76\u5728 1998 \u5E74\u53D1\u5E03\u4E86\u7B2C\u4E00\u4E2A\u7248\u672C\uFF0C\u73B0\u5728 KDE \u51E0\u4E4E\u662F\u6392\u540D\u7B2C\u4E00\u7684\u684C\u9762\u73AF\u5883\u4E86\u3002</p><p>\u8BB8\u591A\u6D41\u884C\u7684 Linux \u53D1\u884C\u7248\u90FD\u63D0\u4F9B\u4E86 KDE \u684C\u9762\u73AF\u5883\uFF0C\u6BD4\u5982 Ubuntu\u3001Linux Mint\u3001OpenSUSE\u3001Fedora\u3001Kubuntu\u3001PC Linux OS \u7B49\u3002</p><p>KDE \u548C Windows \u6BD4\u8F83\u7C7B\u4F3C\uFF0C\u5404\u4F4D\u521D\u5B66\u8005\u76F8\u4FE1\u90FD\u662F Windows \u7684\u7528\u6237\uFF0C\u6240\u4EE5\u5207\u6362\u5230 KDE \u4E5F\u4E0D\u4F1A\u6709\u592A\u5927\u7684\u969C\u788D\u3002</p><p>KDE \u5141\u8BB8\u60A8\u628A\u5E94\u7528\u7A0B\u5E8F\u56FE\u6807\u548C\u6587\u4EF6\u56FE\u6807\u653E\u7F6E\u5728\u684C\u9762\u7684\u7279\u5B9A\u4F4D\u7F6E\u4E0A\u3002\u5355\u51FB\u5E94\u7528\u7A0B\u5E8F\u56FE\u6807\uFF0CLinux \u7CFB\u7EDF\u5C31\u4F1A\u8FD0\u884C\u8BE5\u5E94\u7528\u7A0B\u5E8F\u3002\u5355\u51FB\u6587\u4EF6\u56FE\u6807\uFF0CKDE \u684C\u9762\u5C31\u4F1A\u786E\u5B9A\u4F7F\u7528\u54EA\u79CD\u5E94\u7528\u7A0B\u5E8F\u6765\u5904\u7406\u8BE5\u6587\u4EF6\u3002</p><p>KDE \u662F\u6240\u6709\u684C\u9762\u73AF\u5883\u4E2D\u6700\u5BB9\u6613\u5B9A\u5236\u7684\u3002\u5728\u5176\u4ED6\u684C\u9762\u73AF\u5883\u4E2D\uFF0C\u60A8\u9700\u8981\u51E0\u4E2A\u63D2\u4EF6\u3001\u7A97\u53E3\u7EC4\u4EF6\u548C\u8C03\u6574\u5DE5\u5177\u624D\u53EF\u4EE5\u5B9A\u5236\u73AF\u5883\uFF0CKDE \u5C06\u6240\u6709\u5DE5\u5177\u548C\u7A97\u53E3\u7EC4\u4EF6\u90FD\u585E\u5165\u5230\u7CFB\u7EDF\u8BBE\u7F6E\u4E2D\u3002\u501F\u52A9\u5148\u8FDB\u7684\u8BBE\u7F6E\u7BA1\u7406\u5668\uFF0C\u53EF\u4EE5\u63A7\u5236\u4E00\u5207\uFF0C\u4E0D\u9700\u8981\u4EFB\u4F55\u7B2C\u4E09\u65B9\u5DE5\u5177\uFF0C\u5C31\u53EF\u4EE5\u6839\u636E\u7528\u6237\u7684\u559C\u597D\u548C\u8981\u6C42\u6765\u7F8E\u5316\u53CA\u8C03\u6574\u684C\u9762\u3002</p><p>KDE \u9879\u76EE\u7EC4\u8FD8\u8FD8\u53D1\u4E86\u5927\u91CF\u7684\u53EF\u8FD0\u884C\u5728 KDE \u73AF\u5883\u4E2D\u7684\u5E94\u7528\u7A0B\u5E8F\uFF0C\u5305\u62EC Dolphin(\u6587\u4EF6\u7BA1\u7406\u5DE5\u5177)\u3001Konsole(\u7EC8\u7AEF)\u3001Kate(\u6587\u672C\u7F16\u8F91\u5DE5\u5177)\u3001Gwenview(\u56FE\u7247\u67E5\u770B\u5DE5\u5177)\u3001Okular(\u6587\u6863\u53CA PDF \u67E5\u770B\u5DE5\u5177)\u3001Digikam(\u7167\u7247\u7F16\u8F91\u548C\u6574\u7406\u5DE5\u5177)\u3001KMail(\u7535\u5B50\u90AE\u4EF6\u5BA2\u6237\u8F6F\u4EF6)\u3001Quassel(IRC \u5BA2\u6237\u8F6F\u4EF6)\u3001K3b(DVD \u523B\u5F55\u7A0B\u5E8F)\u3001Krunner(\u542F\u52A8\u5668)\u7B49\uFF0C\u5B83\u4EEC\u90FD\u662F\u9ED8\u8BA4\u5B89\u88C5\u7684\u3002</p><p>\u5BF9 KDE \u4F18\u7F3A\u70B9\u7684\u603B\u7ED3:</p><ul><li>\u4F18\u70B9: KDE \u51E0\u4E4E\u662F\u6700\u5148\u8FDB\u6700\u5F3A\u5927\u7684\u684C\u9762\u73AF\u5883\uFF0C\u5B83\u5916\u89C2\u4F18\u7F8E\u3001\u9AD8\u5EA6\u53EF\u5B9A\u5236\u3001\u517C\u5BB9\u6BD4\u8F83\u65E7\u7684\u786C\u4EF6\u8BBE\u5907</li><li>\u7F3A\u70B9: Kmail \u7B49\u4E00\u4E9B\u7EC4\u4EF6\u7684\u914D\u7F6E\u5BF9\u65B0\u624B\u6765\u8BF4\u8FC7\u4E8E\u590D\u6742\u3002</li></ul><h2 id="gnome-\u684C\u9762\u73AF\u5883" tabindex="-1"><a class="header-anchor" href="#gnome-\u684C\u9762\u73AF\u5883" aria-hidden="true">#</a> GNOME \u684C\u9762\u73AF\u5883</h2><p>GNOME \u662F the GNU Network Object Model Environment \u7684\u7F29\u5199\uFF0C\u4E2D\u6587\u8BD1\u4E3A\u201CGNU \u7F51\u7EDC\u5BF9\u8C61\u6A21\u578B\u73AF\u5883\u201D\u3002</p><p>GNOME \u4E8E 1999 \u5E74\u9996\u6B21\u53D1\u5E03\uFF0C\u73B0\u5DF2\u6210\u4E3A\u8BB8\u591A Linux \u53D1\u884C\u7248\u9ED8\u8BA4\u7684\u684C\u9762\u73AF\u5883(\u4E0D\u8FC7\u7528\u5F97\u6700\u591A\u7684\u662F Red Hat Linux)\u3002</p><p>GNOME \u7684\u7279\u70B9\u662F\u7B80\u6D01\u3001\u8FD0\u884C\u901F\u5EA6\u5FEB\uFF0C\u4F46\u662F\u6CA1\u6709\u592A\u591A\u7684\u5B9A\u5236\u9009\u9879\uFF0C\u7528\u6237\u9700\u8981\u5B89\u88C5\u7B2C\u4E09\u65B9\u5DE5\u5177\u6765\u5B9E\u73B0\u3002</p><p>GNOME \u751A\u81F3\u4E0D\u5305\u62EC\u4E00\u4E9B\u7B80\u5355\u7684\u8C03\u6574\u9009\u9879\uFF0C\u6BD4\u5982\u66F4\u6539\u4E3B\u9898\u3001\u66F4\u6539\u5B57\u4F53\u7B49\uFF0C\u5C31\u8FD9\u4E24\u79CD\u57FA\u672C\u7684\u8C03\u6574\u800C\u8A00\uFF0C\u7528\u6237\u90FD\u9700\u8981\u5B89\u88C5\u7B2C\u4E09\u65B9\u5DE5\u5177\u3002\u6240\u4EE5\uFF0CGONME \u9002\u5408\u90A3\u4E9B\u4E0D\u9700\u8981\u9AD8\u5EA6\u5B9A\u5236\u754C\u9762\u7684\u7528\u6237\u3002</p><p>GNOME \u88AB\u7528\u4F5C Fedora \u4E2D\u7684\u9ED8\u8BA4\u684C\u9762\u73AF\u5883\uFF0C\u63D0\u4F9B\u5728\u51E0\u6B3E\u6D41\u884C\u7684 Linux \u53D1\u884C\u7248\u4E2D\uFF0C\u6BD4\u5982 Ubuntu\u3001Debian\u3001OpenSUSE \u7B49\u3002</p><p>2011 \u5E74\uFF0CGNOME 3 \u8FDB\u884C\u4E86\u91CD\u5927\u66F4\u65B0\uFF0C\u4E0D\u518D\u91C7\u7528\u4F20\u7EDF\u7684 Windows \u98CE\u683C\u7684\u754C\u9762\uFF0C\u800C\u662F\u8FDB\u884C\u4E86\u5168\u65B0\u7684\u8BBE\u8BA1\uFF0C\u60CA\u8273\u4E86\u5F88\u591A\u7528\u6237\u3002GNOME 3 \u7684\u8FD9\u79CD\u884C\u4E3A\u4E5F\u5BFC\u81F4\u90E8\u5206\u7528\u6237\u548C\u5F00\u53D1\u4EBA\u5458\u4E0D\u6EE1\uFF0C\u4ED6\u4EEC\u53C8\u5F00\u53D1\u4E86\u591A\u6B3E\u5176\u4ED6\u7684\u684C\u9762\u73AF\u5883\uFF0C\u6BD4\u5982 MATE \u548C Cinnamon\u3002</p><p>\u5BF9 GNOME \u4F18\u7F3A\u70B9\u7684\u603B\u7ED3:</p><ul><li>\u4F18\u70B9: \u7B80\u5355\u6613\u7528\uFF0C\u53EF\u901A\u8FC7\u63D2\u4EF6\u6765\u6269\u5C55\u529F\u80FD\u3002</li><li>\u7F3A\u70B9: \u5BF9\u63D2\u4EF6\u7684\u7BA1\u7406\u80FD\u529B\u6BD4\u8F83\u5DEE\uFF0C\u4E5F\u7F3A\u5C11\u5176\u5B83\u684C\u9762\u73AF\u5883\u62E5\u6709\u7684\u8BB8\u591A\u529F\u80FD\u3002</li></ul><h2 id="unity" tabindex="-1"><a class="header-anchor" href="#unity" aria-hidden="true">#</a> Unity</h2><p>Unity \u662F\u7531 Ubuntu \u7684\u6BCD\u516C\u53F8 Canonical \u5F00\u53D1\u7684\u4E00\u6B3E\u5916\u58F3\u3002\u4E4B\u6240\u4EE5\u8BF4\u5B83\u662F\u5916\u58F3\uFF0C\u662F\u56E0\u4E3A Unity \u8FD0\u884C\u5728 GNOME \u684C\u9762\u73AF\u5883\u4E4B\u4E0A\uFF0C\u4F7F\u7528\u4E86\u6240\u6709 GNOME \u7684\u6838\u5FC3\u5E94\u7528\u7A0B\u5E8F\u3002</p><p>2010 \u5E74\uFF0CUnity \u7B2C\u4E00\u4E2A\u7248\u672C\u53D1\u5E03\uFF0C\u6B64\u540E\u7ECF\u8FC7\u6570\u6B21\u6539\u8FDB\uFF0C\u5982\u4ECA\u548C\u5176\u5B83\u7684\u684C\u9762\u73AF\u5883\u4E00\u6837\uFF0C\u4E5F\u53EF\u4EE5\u5B89\u88C5\u5230\u5176\u5B83\u7684 Linux \u53D1\u884C\u7248\u4E0A\u4E86\u3002</p><p>Unity \u4F7F\u7528\u4E86\u4E0D\u540C\u7684\u754C\u9762\u98CE\u683C\uFF0C\u5982\u679C\u60A8\u7528\u7684\u662F Ubuntu Linux \u53D1\u884C\u7248\uFF0C\u60A8\u4F1A\u6CE8\u610F\u5230 Unity \u4E0E KDE \u548C GNOME \u684C\u9762\u73AF\u5883\u6709\u4E9B\u4E0D\u4E00\u6837\u3002</p><p>Unity \u5728\u5DE6\u8FB9\u6709\u4E00\u4E2A\u542F\u52A8\u5668\uFF0C\u4F4D\u4E8E\u542F\u52A8\u5668\u9876\u90E8\u7684\u662F\u641C\u7D22\u56FE\u6807\uFF0C\u53C8\u53EB\u201CDash\u201D\u3002\u5728 Dash \u4E0A\u641C\u7D22\u6587\u4EF6\u65F6\uFF0C\u4E0D\u4EC5\u4F1A\u7ED9\u51FA\u6765\u81EA\u786C\u76D8\u7684\u641C\u7D22\u7ED3\u679C\uFF0C\u8FD8\u4F1A\u7ED9\u51FA\u6765\u81EA\u5728\u7EBF\u6765\u6E90\u7684\u641C\u7D22\u7ED3\u679C\uFF0C\u6BD4\u5982 Google Drive\u3001Facebook\u3001Picasa\u3001Flick \u53CA\u5176\u4ED6\u3002</p><p>Unity \u8FD8\u63D0\u4F9B\u4E86\u9690\u85CF\u542F\u52A8\u5668\u3001\u89E6\u6478\u4FA7\u8FB9\u680F\u5C31\u663E\u793A\u7684\u9009\u9879\uFF0C\u7528\u6237\u8FD8\u53EF\u4EE5\u8C03\u9AD8/\u8C03\u4F4E\u663E\u793A\u542F\u52A8\u5668\u83DC\u5355\u7684\u7075\u654F\u5EA6\u3002</p><p>Unity \u5F88\u7B80\u5355\u3001\u8FD0\u884C\u901F\u5EA6\u5FEB\uFF0C\u4F46 Unity \u5728\u7CFB\u7EDF\u8BBE\u7F6E\u4E0B\u5374\u6CA1\u6709\u5B9A\u5236\u684C\u9762\u7684\u592A\u591A\u9009\u9879\uFF0C\u8981\u60F3\u5B89\u88C5\u4E3B\u9898\u6216\u8005\u5B9A\u5236\u53E6\u5916\u4E0D\u540C\u7684\u9009\u9879\uFF0C\u6BD4\u5982\u7CFB\u7EDF\u83DC\u5355\u662F\u5426\u5E94\u8BE5\u603B\u662F\u53EF\u89C1\uFF0C\u6216\u8005\u201C\u4ECE\u542F\u52A8\u5668\u56FE\u6807\u4E00\u6B21\u70B9\u51FB\u6700\u5C0F\u5316\u201D\uFF0C\u7528\u6237\u9700\u8981\u5B89\u88C5\u7B2C\u4E09\u65B9\u5DE5\u5177\u3002</p><p>CCSM \u548C Unity Tweak Tool \u662F\u9762\u5411 Unity \u684C\u9762\u73AF\u5883\u7684\u975E\u5E38\u6D41\u884C\u7684\u5B9A\u5236\u5DE5\u5177\u3002</p><p>\u5BF9 Unity \u4F18\u7F3A\u70B9\u7684\u603B\u7ED3:</p><ul><li>\u4F18\u70B9: \u754C\u9762\u7B80\u6D01\u76F4\u89C2\uFF0C\u53EF\u4EE5\u901A\u8FC7\u7B2C\u4E09\u65B9\u5DE5\u5177\u6765\u6DF1\u5EA6\u5B9A\u5236\uFF0C\u800C\u4E14\u4F7F\u7528\u4E86\u5E73\u89C6\u663E\u793A\u5668(HUD)\u7B49\u65B0\u6280\u672F\u3002</li><li>\u7F3A\u70B9: \u9ED8\u8BA4\u7684\u5B9A\u5236\u529F\u80FD\u6BD4\u8F83\u5DEE\u52B2\uFF0C\u901A\u77E5\u673A\u5236\u4E00\u822C\u3002</li></ul><h2 id="mate" tabindex="-1"><a class="header-anchor" href="#mate" aria-hidden="true">#</a> MATE</h2><p>\u4E0A\u9762\u6211\u4EEC\u63D0\u5230\uFF0CGNOME 3 \u8FDB\u884C\u4E86\u5168\u65B0\u7684\u754C\u9762\u8BBE\u8BA1\uFF0C\u8FD9\u62DB\u81F4\u4E00\u4E9B\u7528\u6237\u7684\u4E0D\u6EE1\uFF0C\u4ED6\u4EEC\u63A8\u51FA\u4E86\u5176\u5B83\u7684\u684C\u9762\u73AF\u5883\uFF0CMATE \u5C31\u662F\u5176\u4E2D\u4E4B\u4E00\u3002</p><p>MATE \u662F\u4E00\u79CD\u4ECE\u73B0\u5728\u65E0\u4EBA\u7EF4\u62A4\u7684 GNOME 2 \u4EE3\u7801\u5E93\u6D3E\u751F\u51FA\u6765\u7684\u684C\u9762\u73AF\u5883\u3002</p><p>MATE \u8BA9\u4EBA\u89C9\u5F97\u5728\u4F7F\u7528\u65E7\u7684\u684C\u9762\u73AF\u5883\uFF0C\u4F46\u662F\u7ED3\u5408\u4E86\u5386\u5E74\u6765\u754C\u9762\u65B9\u9762\u7684\u8BF8\u591A\u6539\u8FDB\u3002MATE \u8FD8\u975E\u5E38\u9002\u7528\u4E8E\u4F4E\u914D\u8BA1\u7B97\u673A\uFF0C\u6240\u4EE5\u5982\u679C\u60A8\u6709\u4E00\u53F0\u65E7\u7684\u6216\u901F\u5EA6\u8F83\u6162\u7684\u8BA1\u7B97\u673A\uFF0C\u53EF\u4EE5\u4F7F\u7528 MATE\u3002</p><p>MATE \u8FD8\u662F\u8BB8\u591A\u6D41\u884C\u7684 Linux \u53D1\u884C\u7248\u968F\u5E26\u7684\uFF0C\u6BD4\u5982 Ubuntu\u3001Linux Mint\u3001Mageia\u3001Debian \u53CA\u53E6\u5916\u66F4\u591A\u53D1\u884C\u7248\u3002Ubuntu MATE \u5934\u4E00\u56DE\u662F\u5B98\u65B9\u7248\u672C\u3002</p><p>\u201C\u6B22\u8FCE\u9996\u6B21\u53D1\u5E03\u7684 Ubuntu MATE \u5B98\u65B9\u7248\u672C\u3002\u73B0\u5728\uFF0C\u7528\u6237\u5C06\u66F4\u5BB9\u6613\u66F4\u65B0\u8F6F\u4EF6\uFF0C\u56E0\u4E3A\u6240\u6709\u7EC4\u4EF6\u73B0\u5728\u90FD\u5728 Ubuntu \u8F6F\u4EF6\u5E93\u4E2D\u3002\u201D</p><p>MATE \u81EA\u5E26\u7684\u5E94\u7528\u7A0B\u5E8F\u5305\u62EC Caja(\u6587\u4EF6\u7BA1\u7406\u5DE5\u5177)\u3001Pluma(\u6587\u672C\u7F16\u8F91\u5DE5\u5177)\u3001Atril(\u6587\u6863\u67E5\u770B\u5DE5\u5177)\u3001Eye of MATE(\u56FE\u50CF\u67E5\u770B\u5DE5\u5177)\u7B49\uFF0C\u5982\u679C\u7528\u6237\u4E0D\u9700\u8981\u5176\u4ED6\u529F\u80FD\u5B8C\u5907\u7684\u684C\u9762\u73AF\u5883\u7684\u6240\u6709\u989D\u5916\u529F\u80FD\uFF0C\u90A3\u4E48 MATE \u5BF9\u4ED6\u4EEC\u6765\u8BF4\u662F\u4E00\u6B3E\u7B80\u5355\u7684\u8F7B\u91CF\u7EA7\u684C\u9762\u73AF\u5883\u3002</p><p>\u5BF9 META \u4F18\u7F3A\u70B9\u7684\u603B\u7ED3:</p><ul><li>\u4F18\u70B9: \u8F7B\u91CF\u7EA7\u7684\u684C\u9762\u73AF\u5883\uFF0C\u80FD\u591F\u517C\u5BB9\u6559\u65E7\u7684\u786C\u4EF6\u8BBE\u5907\u3002</li><li>\u7F3A\u70B9: \u6211\u4E5F\u4E0D\u77E5\u9053\u6709\u4EC0\u4E48\u660E\u663E\u7684\u7F3A\u70B9\uFF0C\u6B22\u8FCE\u8BFB\u8005\u7559\u8A00\u544A\u77E5\u3002</li></ul><h2 id="cinnamon" tabindex="-1"><a class="header-anchor" href="#cinnamon" aria-hidden="true">#</a> Cinnamon</h2><p>\u4E0E MATE \u7C7B\u4F3C\uFF0CCinnamon \u662F\u7531 Linux Mint \u56E2\u961F\u56E0\u4E3A\u4E0D\u6EE1 Gnome 3 \u7684\u6539\u8FDB\u800C\u5F00\u53D1\u7684\u53E6\u4E00\u79CD\u684C\u9762\u73AF\u5883\u3002\u4F46 Cinnamon \u4E0E MATE \u4E0D\u540C\u4E4B\u5904\u5728\u4E8E\uFF0CCinnamon \u5EFA\u7ACB\u5728 Gnome 3 \u7684\u57FA\u7840\u4E0A\u3002Cinnamon \u662F\u65B0\u7684\uFF0C\u800C\u4E14\u5728\u79EF\u6781\u5F00\u53D1\u4E4B\u4E2D\uFF0C\u4F46\u8FD9\u6B3E\u51FA\u8272\u7684\u684C\u9762\u73AF\u5883\u6CA1\u6709\u56E0\u65B0\u9896\u800C\u5728\u529F\u80FD\u65B9\u9762\u6709\u6240\u51CF\u5F31\u3002</p><p>Cinnamon \u62E5\u6709 GNOME \u548C Unity \u7B49\u5176\u5B83\u684C\u9762\u73AF\u5883\u6240\u6CA1\u6709\u7684\u79CD\u79CD\u529F\u80FD\u3002Cinnamon \u662F\u9AD8\u5EA6\u53EF\u5B9A\u5236\u7684\u684C\u9762\u73AF\u5883\uFF0C\u4E0D\u9700\u8981\u4EFB\u4F55\u5916\u90E8\u63D2\u4EF6\u3001\u7A97\u53E3\u7EC4\u4EF6\u548C\u8C03\u6574\u5DE5\u5177\u6765\u5B9A\u5236\u684C\u9762\u3002Cinnamon \u751A\u81F3\u53EF\u4EE5\u901A\u8FC7\u8BBE\u7F6E\u7BA1\u7406\u5668\u672C\u8EAB\u6765\u4E0B\u8F7D\u5E76\u5B89\u88C5\u4E3B\u9898\uFF0C\u751A\u81F3\u4E0D\u9700\u8981\u6253\u5F00\u4E92\u8054\u7F51\u6D4F\u89C8\u5668\u3002</p><p>\u7531\u4E8E\u79CD\u79CD\u51FA\u8272\u7684\u6240\u9700\u529F\u80FD\uFF0CCinnamon \u5BF9\u4EFB\u4F55\u521A\u63A5\u89E6 Linux \u7684\u65B0\u7528\u6237\u6765\u8BF4\u90FD\u975E\u5E38\u65B9\u4FBF\u3002\u8BB8\u591A\u7528\u6237\u653E\u5F03\u4F7F\u7528 Linux\uFF0C\u662F\u56E0\u4E3A\u4ED6\u4EEC\u5E76\u4E0D\u4E86\u89E3 Linux \u7684\u5DE5\u4F5C\u65B9\u5F0F\uFF0C\u4F46\u662F\u6211\u5F3A\u70C8\u5EFA\u8BAE\u65B0\u624B\u5E94\u4ECE Cinnamon \u684C\u9762\u73AF\u5883\u5F00\u59CB\u5165\u624B\u3002</p><p>\u8BB8\u591A\u6D41\u884C\u7684 Linux \u53D1\u884C\u7248\u63D0\u4F9B\u4E86\u5404\u81EA\u7248\u672C\u7684 Cinnamon\uFF0C\u6BD4\u5982 Ubuntu\u3001Fedora\u3001OpenSUSE\u3001Gentoo\u3001Arch Linux \u7B49\u3002Cinnamon \u8FD8\u662F Linux Mint \u7684\u9ED8\u8BA4\u684C\u9762\u73AF\u5883\u3002</p><p>\u5BF9 Cinnamon \u4F18\u7F3A\u70B9\u7684\u603B\u7ED3:</p><ul><li>\u4F18\u70B9: \u6210\u719F\u5B8C\u7F8E\uFF0C\u9AD8\u5EA6\u53EF\u6027\u8D28\uFF0C\u9002\u5408 Linux \u65B0\u624B\u3002</li><li>\u7F3A\u70B9: \u6709\u65F6\u5019\u53EF\u80FD\u4F1A\u6709\u8F6F\u4EF6\u9519\u8BEF\u3002</li></ul>',46),o=[t];function u(l,r){return i(),a("div",null,o)}var h=n(p,[["render",u],["__file","desktop.html.vue"]]);export{h as default};
