import{_ as n}from"./plugin-vue_export-helper.21dcd24c.js";import{o as i,c as u,e}from"./app.8c8575b4.js";var a="/assets/intro.a5843167.jpg";const t={},r=e('<h1 id="linux-\u7B80\u4ECB" tabindex="-1"><a class="header-anchor" href="#linux-\u7B80\u4ECB" aria-hidden="true">#</a> Linux \u7B80\u4ECB</h1><p>Linux \u5185\u6838\u6700\u521D\u53EA\u662F\u7531\u82AC\u5170\u4EBA\u6797\u7EB3\u65AF\xB7\u6258\u74E6\u5179 (Linus Torvalds) \u5728\u8D6B\u5C14\u8F9B\u57FA\u5927\u5B66\u4E0A\u5B66\u65F6\u51FA\u4E8E\u4E2A\u4EBA\u7231\u597D\u800C\u7F16\u5199\u7684\u3002</p><p>Linux \u662F\u4E00\u5957\u514D\u8D39\u4F7F\u7528\u548C\u81EA\u7531\u4F20\u64AD\u7684\u7C7B Unix \u64CD\u4F5C\u7CFB\u7EDF\uFF0C\u662F\u4E00\u4E2A\u57FA\u4E8E POSIX \u548C UNIX \u7684\u591A\u7528\u6237\u3001\u591A\u4EFB\u52A1\u3001\u652F\u6301\u591A\u7EBF\u7A0B\u548C\u591A CPU \u7684\u64CD\u4F5C\u7CFB\u7EDF\u3002</p><p>Linux \u80FD\u8FD0\u884C\u4E3B\u8981\u7684 UNIX \u5DE5\u5177\u8F6F\u4EF6\u3001\u5E94\u7528\u7A0B\u5E8F\u548C\u7F51\u7EDC\u534F\u8BAE\u3002\u5B83\u652F\u6301 32 \u4F4D\u548C 64 \u4F4D\u786C\u4EF6\u3002Linux \u7EE7\u627F\u4E86 Unix \u4EE5\u7F51\u7EDC\u4E3A\u6838\u5FC3\u7684\u8BBE\u8BA1\u601D\u60F3\uFF0C\u662F\u4E00\u4E2A\u6027\u80FD\u7A33\u5B9A\u7684\u591A\u7528\u6237\u7F51\u7EDC\u64CD\u4F5C\u7CFB\u7EDF\u3002</p><p>Linux \u8BA9\u5F00\u5C55\u5404\u79CD\u5B9E\u9645\u6709\u7528\u4E14\u5177\u6709\u521B\u9020\u6027\u7684\u4E8B\u60C5\u6210\u4E3A\u53EF\u80FD\u3002\u4F8B\u5982\uFF0C\u60A8\u53EF\u4EE5\u5728\u4E00\u4E2A U \u76D8\u4E0A\u88C5\u8F7D Linux \u7684\u81EA\u751F\u7CFB\u7EDF\u5F15\u5BFC\u6620\u50CF (live boot image)\uFF0C\u542F\u52A8\u4E00\u53F0\u786C\u76D8\u5DF2\u7ECF\u5D29\u6E83\u7684\u8BA1\u7B97\u673A\uFF0C\u4E4B\u540E\u67FB\u627E\u5E76\u89E3\u51B3\u8BE5\u95EE\u9898\u3002\u6216\u8005\uFF0C\u56E0\u4E3A Linux \u662F\u4E00\u4E2A\u771F\u6B63\u7684\u591A\u7528\u6237\u64CD\u4F5C\u7CFB\u7EDF\uFF0C\u5B83\u5177\u6709\u975E\u5E38\u597D\u7684\u79C1\u5BC6\u6027\u548C\u7A33\u5B9A\u6027\uFF0C\u6574\u4E2A\u56E2\u961F\u53EF\u4EE5\u540C\u65F6\u4ECE\u672C\u5730\u6216\u8FDC\u7A0B\u767B\u5F55\u8FDB\u884C\u5DE5\u4F5C\u3002</p><p>Linux \u7684\u6784\u5EFA\u91C7\u7528\u4E86\u4E00\u4E9B\u4E0E UNIX \u64CD\u4F5C\u7CFB\u7EDF\u76F8\u540C\u7684\u6280\u672F\uFF0C\u5E76\u5E26\u6709\u5927\u591A\u6570\u4E0E\u6DF1\u5EA6\u6210\u719F\u7684 UNIX \u64CD\u4F5C\u7CFB\u7EDF\u76F8\u4E00\u81F4\u7684\u5DE5\u5177\uFF0C\u8FD9\u6781\u5927\u5730\u589E\u52A0\u4E86\u7A33\u5B9A\u6027\u4E0E\u5B89\u5168\u6027\u3002</p><p>Linux \u7684\u53D1\u884C\u7248\u8FD8\u63D0\u4F9B\u4E86\u590D\u6742\u7684\u8F6F\u4EF6\u5305\u7BA1\u7406\u7CFB\u7EDF\uFF0C\u53EF\u4EE5\u53EF\u9760\u5730\u5B89\u88C5\u548C\u7EF4\u62A4\u6BCF\u4E00\u4E2A\u5728\u7EBF\u8D44\u6E90\u5E93\u4E2D\u6210\u5343\u4E0A\u4E07\u7684\u514D\u8D39\u8F6F\u4EF6\u5E94\u7528\u3002</p><p>Linux \u4E0D\u4EC5\u662F\u514D\u8D39\u7684\uFF0C\u66F4\u662F\u5F00\u6E90\u7684(open source)\uFF0C\u8FD9\u610F\u5473\u7740\u4EFB\u4F55\u4EBA\u90FD\u53EF\u4EE5\u83B7\u5F97\u5176\u4EE3\u7801\u5E76\u6839\u636E\u81EA\u5DF1\u7684\u9700\u6C42\u8FDB\u884C\u4FEE\u6539\u3002</p><h2 id="linux-\u7684\u53D1\u884C\u7248" tabindex="-1"><a class="header-anchor" href="#linux-\u7684\u53D1\u884C\u7248" aria-hidden="true">#</a> Linux \u7684\u53D1\u884C\u7248</h2><p>Linux \u7684\u53D1\u884C\u7248\u8BF4\u7B80\u5355\u70B9\u5C31\u662F\u5C06 Linux \u5185\u6838\u4E0E\u5E94\u7528\u8F6F\u4EF6\u505A\u4E00\u4E2A\u6253\u5305\u3002</p><p><img src="'+a+'" alt="intro" loading="lazy"></p><p>\u4E8B\u5B9E\u4E0A\uFF0C\u5DF2\u7ECF\u5B75\u5316\u51FA\u4E13\u5C5E Linux \u5404\u53D1\u884C\u7248\u7684\u5DE8\u5927\u751F\u6001\u7CFB\u7EDF\u3002\u4E00\u4E2A\u53D1\u884C\u7248(distribution \uFF0C\u6709\u65F6\u7F29\u5199\u4E3A distro)\u662F\u4E00\u4E2A\u5B9A\u5236\u7684\u8F6F\u4EF6\u5305\uFF0C\u5B83\u5C06 Linux \u5185\u6838\u4EE5\u53CA\u7528\u6237\u8BA1\u7B97\u673A Linux \u8FD0\u884C\u7248\u7684\u5B89\u88C5\u5DE5\u5177\u4E00\u8D77\u53D1\u5E03\u3002</p><p>\u5728 Linux \u5185\u6838\u7684\u53D1\u5C55\u8FC7\u7A0B\u4E2D\uFF0C\u5404\u79CD Linux \u53D1\u884C\u7248\u672C\u8D77\u4E86\u5DE8\u5927\u7684\u4F5C\u7528\uFF0C\u6B63\u662F\u5B83\u4EEC\u63A8\u52A8\u4E86 Linux \u7684\u5E94\u7528\uFF0C\u4ECE\u800C\u8BA9\u66F4\u591A\u7684\u4EBA\u5F00\u59CB\u5173\u6CE8 Linux\u3002\u56E0\u6B64\uFF0C\u628A Red Hat\u3001Ubuntu\u3001SUSE \u7B49\u76F4\u63A5\u8BF4\u6210 Linux \u5176\u5B9E\u662F\u4E0D\u786E\u5207\u7684\uFF0C\u5B83\u4EEC\u662F Linux \u7684\u53D1\u884C\u7248\u672C\uFF0C\u66F4\u786E\u5207\u5730\u8BF4\uFF0C\u5E94\u8BE5\u53EB\u4F5C\u201C\u4EE5 Linux \u4E3A\u6838\u5FC3\u7684\u64CD\u4F5C\u7CFB\u7EDF\u8F6F\u4EF6\u5305\u201D\u3002</p><p>Linux \u7684\u5404\u4E2A\u53D1\u884C\u7248\u672C\u4F7F\u7528\u7684\u662F\u540C\u4E00\u4E2A Linux \u5185\u6838\uFF0C\u56E0\u6B64\u5728\u5185\u6838\u5C42\u4E0D\u5B58\u5728\u4EC0\u4E48\u517C\u5BB9\u6027\u95EE\u9898\uFF0C\u6BCF\u4E2A\u7248\u672C\u6709\u4E0D\u4E00\u6837\u7684\u611F\u89C9\uFF0C\u53EA\u662F\u5728\u53D1\u884C\u7248\u672C\u7684\u6700\u5916\u5C42(\u7531\u53D1\u884C\u5546\u6574\u5408\u5F00\u53D1\u7684\u5E94\u7528)\u624D\u6709\u6240\u4F53\u73B0\u3002</p><p>\u76EE\u524D\u5E02\u9762\u4E0A\u8F83\u77E5\u540D\u7684\u53D1\u884C\u7248\u6709: Ubuntu\u3001RedHat\u3001CentOS\u3001Debian\u3001Fedora\u3001SuSE\u3001OpenSUSE\u3001Arch Linux\u3001SolusOS \u7B49\u3002</p><p>Linux \u7684\u53D1\u884C\u7248\u672C\u53EF\u4EE5\u5927\u4F53\u5206\u4E3A\u4E24\u7C7B:</p><ul><li>\u5546\u4E1A\u516C\u53F8\u7EF4\u62A4\u7684\u53D1\u884C\u7248\u672C\uFF0C\u4EE5\u8457\u540D\u7684 Red Hat \u4E3A\u4EE3\u8868\uFF1B</li><li>\u793E\u533A\u7EC4\u7EC7\u7EF4\u62A4\u7684\u53D1\u884C\u7248\u672C\uFF0C\u4EE5 Debian \u4E3A\u4EE3\u8868\u3002</li></ul><p>\u5F88\u96BE\u8BF4\u5927\u91CF Linux \u7248\u672C\u4E2D\u54EA\u4E00\u6B3E\u66F4\u597D\uFF0C\u6BCF\u4E2A\u7248\u672C\u90FD\u6709\u81EA\u5DF1\u7684\u7279\u70B9\u3002</p><h3 id="red-hat-linux" tabindex="-1"><a class="header-anchor" href="#red-hat-linux" aria-hidden="true">#</a> Red Hat Linux</h3><p>Red Hat(\u7EA2\u5E3D\u516C\u53F8)\u521B\u5EFA\u4E8E 1993 \u5E74\uFF0C\u662F\u76EE\u524D\u4E16\u754C\u4E0A\u8D44\u6DF1\u7684 Linux \u5382\u5546\uFF0C\u4E5F\u662F\u6700\u83B7\u8BA4\u53EF\u7684 Linux \u54C1\u724C\u3002</p><p>Red Hat \u516C\u53F8\u7684\u4EA7\u54C1\u4E3B\u8981\u5305\u62EC RHEL(Red Hat Enterprise Linux\uFF0C\u6536\u8D39\u7248\u672C)\u548C CentOS(RHEL \u7684\u793E\u533A\u514B\u9686\u7248\u672C\uFF0C\u514D\u8D39\u7248\u672C)\u3001Fedora Core(\u7531 Red Hat \u684C\u9762\u7248\u53D1\u5C55\u800C\u6765\uFF0C\u514D\u8D39\u7248\u672C)\u3002</p><p>Red Hat \u662F\u5728\u6211\u56FD\u56FD\u5185\u4F7F\u7528\u4EBA\u7FA4\u6700\u591A\u7684 Linux \u7248\u672C\uFF0C\u8D44\u6599\u4E30\u5BCC\uFF0C\u5982\u679C\u60A8\u6709\u4EC0\u4E48\u4E0D\u660E\u767D\u7684\u5730\u65B9\uFF0C\u5219\u5BB9\u6613\u627E\u5230\u4EBA\u6765\u8BF7\u6559\uFF0C\u800C\u4E14\u5927\u591A\u6570 Linux \u6559\u7A0B\u662F\u4EE5 Red Hat \u4E3A\u4F8B\u6765\u8BB2\u89E3\u7684(\u5305\u62EC\u672C\u6559\u7A0B)\u3002</p><p>CentOS \u662F\u57FA\u4E8E Red Hat Enterprise Linux \u6E90\u4EE3\u7801\u91CD\u65B0\u7F16\u8BD1\u3001\u53BB\u9664 Red Hat \u5546\u6807\u7684\u4EA7\u7269\uFF0C\u5404\u79CD\u64CD\u4F5C\u4F7F\u7528\u548C\u4ED8\u8D39\u7248\u672C\u6CA1\u6709\u533A\u522B\uFF0C\u4E14\u5B8C\u5168\u514D\u8D39\u3002\u7F3A\u70B9\u662F\u4E0D\u5411\u7528\u6237\u63D0\u4F9B\u6280\u672F\u652F\u6301\uFF0C\u4E5F\u4E0D\u8D1F\u4EFB\u4F55\u5546\u4E1A\u8D23\u4EFB\u3002</p><h3 id="ubuntu-linux" tabindex="-1"><a class="header-anchor" href="#ubuntu-linux" aria-hidden="true">#</a> Ubuntu Linux</h3><p>Ubuntu \u57FA\u4E8E\u77E5\u540D\u7684 Debian Linux \u53D1\u5C55\u800C\u6765\uFF0C\u754C\u9762\u53CB\u597D\uFF0C\u5BB9\u6613\u4E0A\u624B\uFF0C\u5BF9\u786C\u4EF6\u7684\u652F\u6301\u975E\u5E38\u5168\u9762\uFF0C\u662F\u76EE\u524D\u6700\u9002\u5408\u505A\u684C\u9762\u7CFB\u7EDF\u7684 Linux \u53D1\u884C\u7248\u672C\uFF0C\u800C\u4E14 Ubuntu \u7684\u6240\u6709\u53D1\u884C\u7248\u672C\u90FD\u514D\u8D39\u63D0\u4F9B\u3002</p><p>Ubuntu \u7684\u521B\u59CB\u4EBA Mark Shuttleworth \u662F\u975E\u5E38\u5177\u6709\u4F20\u5947\u8272\u5F69\u7684\u4EBA\u7269\u3002\u4ED6\u5728\u5927\u5B66\u6BD5\u4E1A\u540E\u521B\u5EFA\u4E86\u4E00\u5BB6\u5B89\u5168\u54A8\u8BE2\u516C\u53F8\uFF0C1999 \u5E74\u4EE5 5.75 \u4EBF\u7F8E\u5143\u88AB\u6536\u8D2D\uFF0C\u7531\u6B64\u4E00\u8DC3\u6210\u4E3A\u5357\u975E\u6700\u5E74\u8F7B\u6709\u4E3A\u7684\u672C\u571F\u5BCC\u7FC1\u3002\u4F5C\u4E3A\u4E00\u540D\u72C2\u70ED\u7684\u5929\u6587\u7231\u597D\u8005\uFF0CMark Shuttleworth \u4E8E 2002 \u5E74\u81EA\u8D39\u4E58\u5750\u4FC4\u7F57\u65AF\u8054\u76DF\u53F7\u98DE\u8239\uFF0C\u5728\u56FD\u9645\u7A7A\u95F4\u7AD9\u4E2D\u5EA6\u8FC7\u4E86 8 \u5929\u7684\u65F6\u5149\u3002\u4E4B\u540E\uFF0CMark Shuttleworth \u521B\u7ACB\u4E86 Ubuntu \u793E\u533A\uFF0C2005 \u5E74 7 \u6708 1 \u65E5\u5EFA\u7ACB\u4E86 Ubuntu \u57FA\u91D1\u4F1A\uFF0C\u5E76\u4E3A\u8BE5\u57FA\u91D1\u4F1A\u6295\u8D44 1000 \u4E07\u7F8E\u5143\u3002\u4ED6\u8BF4\uFF0C\u592A\u7A7A\u7684\u6240\u89C1\u6B63\u662F\u4ED6\u521B\u7ACB Ubuntu \u7684\u7CBE\u795E\u4E4B\u6240\u5728\u3002\u5982\u4ECA\uFF0C\u4ED6\u6700\u70ED\u8877\u7684\u4E8B\u60C5\u5C31\u662F\u5230\u5904\u4E3A\u81EA\u7531\u5F00\u6E90\u7684 Ubuntu \u8FDB\u884C\u5BA3\u4F20\u6F14\u8BB2\u3002</p><h3 id="suse-linux" tabindex="-1"><a class="header-anchor" href="#suse-linux" aria-hidden="true">#</a> SuSE Linux</h3><p>SuSE Linux \u4EE5 Slackware Linux \u4E3A\u57FA\u7840\uFF0C\u539F\u6765\u662F\u5FB7\u56FD\u7684 SuSE Linux AG \u516C\u53F8\u53D1\u5E03\u7684 Linux \u7248\u672C\uFF0C1994 \u5E74\u53D1\u884C\u4E86\u7B2C\u4E00\u7248\uFF0C\u65E9\u671F\u53EA\u6709\u5546\u4E1A\u7248\u672C\uFF0C2004 \u5E74\u88AB Novell \u516C\u53F8\u6536\u8D2D\u540E\uFF0C\u6210\u7ACB\u4E86 OpenSUSE \u793E\u533A\uFF0C\u63A8\u51FA\u4E86\u81EA\u5DF1\u7684\u793E\u533A\u7248\u672C OpenSUSE\u3002</p><p>SuSE Linux \u5728\u6B27\u6D32\u8F83\u4E3A\u6D41\u884C\uFF0C\u5728\u6211\u56FD\u56FD\u5185\u4E5F\u6709\u8F83\u591A\u5E94\u7528\u3002\u503C\u5F97\u4E00\u63D0\u7684\u662F\uFF0C\u5B83\u5438\u53D6\u4E86 Red Hat Linux \u7684\u5F88\u591A\u7279\u8D28\u3002</p><p>SuSE Linux \u53EF\u4EE5\u975E\u5E38\u65B9\u4FBF\u5730\u5B9E\u73B0\u4E0E Windows \u7684\u4EA4\u4E92\uFF0C\u786C\u4EF6\u68C0\u6D4B\u975E\u5E38\u4F18\u79C0\uFF0C\u62E5\u6709\u754C\u9762\u53CB\u597D\u7684\u5B89\u88C5\u8FC7\u7A0B\u3001\u56FE\u5F62\u7BA1\u7406\u5DE5\u5177\uFF0C\u5BF9\u4E8E\u7EC8\u7AEF\u7528\u6237\u548C\u7BA1\u7406\u5458\u6765\u8BF4\u4F7F\u7528\u975E\u5E38\u65B9\u4FBF\u3002</p><h3 id="gentoo" tabindex="-1"><a class="header-anchor" href="#gentoo" aria-hidden="true">#</a> Gentoo</h3><p>Gentoo \u6700\u521D\u7531 Daniel Robbins(FreeBSD \u7684\u5F00\u53D1\u8005\u4E4B\u4E00)\u521B\u5EFA\uFF0C\u9996\u4E2A\u7A33\u5B9A\u7248\u672C\u53D1\u5E03\u4E8E 2002 \u5E74\u3002Gentoo \u662F\u6240\u6709 Linux \u53D1\u884C\u7248\u672C\u91CC\u5B89\u88C5\u6700\u590D\u6742\u7684\uFF0C\u5230\u76EE\u524D\u4E3A\u6B62\u4ECD\u91C7\u7528\u6E90\u7801\u5305\u7F16\u8BD1\u5B89\u88C5\u64CD\u4F5C\u7CFB\u7EDF\u3002</p><p>\u4E0D\u8FC7\uFF0C\u5B83\u662F\u5B89\u88C5\u5B8C\u6210\u540E\u6700\u4FBF\u4E8E\u7BA1\u7406\u7684\u7248\u672C\uFF0C\u4E5F\u662F\u5728\u76F8\u540C\u786C\u4EF6\u73AF\u5883\u4E0B\u8FD0\u884C\u6700\u5FEB\u7684\u7248\u672C\u3002\u81EA\u4ECE Gentoo 1.0 \u9762\u4E16\u540E\uFF0C\u5B83\u5C31\u50CF\u4E00\u573A\u98CE\u66B4\uFF0C\u7ED9 Linux \u4E16\u754C\u5E26\u6765\u4E86\u5DE8\u5927\u7684\u60CA\u559C\uFF0C\u540C\u65F6\u4E5F\u5438\u5F15\u4E86\u5927\u91CF\u7684\u7528\u6237\u548C\u5F00\u53D1\u8005\u6295\u5165 Gentoo Linux \u7684\u6000\u62B1\u3002</p><p>\u6709\u4EBA\u8FD9\u6837\u8BC4\u4EF7 Gentoo: \u5FEB\u901F\u3001\u8BBE\u8BA1\u5E72\u51C0\u800C\u6709\u5F39\u6027\uFF0C\u5B83\u7684\u51FA\u540D\u662F\u56E0\u4E3A\u5176\u9AD8\u5EA6\u7684\u81EA\u5B9A\u5236\u6027(\u57FA\u4E8E\u6E90\u4EE3\u7801\u7684\u53D1\u884C\u7248)\u3002\u5C3D\u7BA1\u5B89\u88C5\u65F6\u53EF\u4EE5\u9009\u62E9\u9884\u5148\u7F16\u8BD1\u597D\u7684\u8F6F\u4EF6\u5305\uFF0C\u4F46\u662F\u5927\u90E8\u5206\u4F7F\u7528 Gentoo \u7684\u7528\u6237\u90FD\u9009\u62E9\u81EA\u5DF1\u624B\u52A8\u7F16\u8BD1\u3002\u8FD9\u4E5F\u662F\u4E3A\u4EC0\u4E48 Gentoo \u9002\u5408\u6BD4\u8F83\u6709 Linux \u4F7F\u7528\u7ECF\u9A8C\u7684\u8001\u624B\u4F7F\u7528\u3002</p><h3 id="\u53D1\u884C\u7248\u9009\u62E9" tabindex="-1"><a class="header-anchor" href="#\u53D1\u884C\u7248\u9009\u62E9" aria-hidden="true">#</a> \u53D1\u884C\u7248\u9009\u62E9</h3><p>Linux \u7684\u53D1\u884C\u7248\u672C\u4F17\u591A\uFF0C\u5728\u6B64\u4E0D\u9010\u4E00\u4ECB\u7ECD\uFF0C\u4E0B\u9762\u7ED9\u9009\u62E9 Linux \u53D1\u884C\u7248\u672C\u72AF\u6101\u7684\u670B\u53CB\u4E00\u70B9\u5EFA\u8BAE:</p><ul><li><p>\u5982\u679C\u60A8\u9700\u8981\u7684\u662F\u4E00\u4E2A\u670D\u52A1\u5668\u7CFB\u7EDF\uFF0C\u800C\u4E14\u5DF2\u7ECF\u538C\u70E6\u4E86\u5404\u79CD Linux \u7684\u914D\u7F6E\uFF0C\u53EA\u662F\u60F3\u8981\u4E00\u4E2A\u6BD4\u8F83\u7A33\u5B9A\u7684\u670D\u52A1\u5668\u7CFB\u7EDF\uFF0C\u90A3\u4E48\u5EFA\u8BAE\u60A8\u9009\u62E9 CentOS \u6216 RHEL\u3002</p></li><li><p>\u5982\u679C\u60A8\u53EA\u662F\u9700\u8981\u4E00\u4E2A\u684C\u9762\u7CFB\u7EDF\uFF0C\u800C\u4E14\u65E2\u4E0D\u60F3\u4F7F\u7528\u76D7\u7248\uFF0C\u53C8\u4E0D\u60F3\u82B1\u5927\u4EF7\u94B1\u8D2D\u4E70\u5546\u4E1A\u8F6F\u4EF6\uFF0C\u4E0D\u60F3\u81EA\u5DF1\u5B9A\u5236\uFF0C\u4E5F\u4E0D\u60F3\u5728\u7CFB\u7EDF\u4E0A\u6D6A\u8D39\u592A\u591A\u65F6\u95F4\uFF0C\u5219\u53EF\u4EE5\u9009\u62E9 Ubuntu\u3002</p></li><li><p>\u5982\u679C\u60A8\u60F3\u6DF1\u5165\u6478\u7D22\u4E00\u4E0B Linux \u5404\u4E2A\u65B9\u9762\u7684\u77E5\u8BC6\uFF0C\u800C\u4E14\u8FD8\u60F3\u975E\u5E38\u7075\u6D3B\u5730\u5B9A\u5236\u81EA\u5DF1\u7684 Linux \u7CFB\u7EDF\uFF0C\u90A3\u5C31\u9009\u62E9 Gentoo \u5427\uFF0C\u5C3D\u60C5\u4EAB\u53D7 Gentoo \u5E26\u6765\u7684\u81EA\u7531\u5FEB\u611F\u3002</p></li><li><p>\u5982\u679C\u60A8\u5BF9\u7CFB\u7EDF\u7A33\u5B9A\u6027\u8981\u6C42\u5F88\u9AD8\uFF0C\u5219\u53EF\u4EE5\u8003\u8651 FreeBSD\u3002</p></li><li><p>\u5982\u679C\u60A8\u9700\u8981\u4F7F\u7528\u6570\u636E\u5E93\u9AD8\u7EA7\u670D\u52A1\u548C\u7535\u5B50\u90AE\u4EF6\u7F51\u7EDC\u5E94\u7528\uFF0C\u5219\u53EF\u4EE5\u9009\u62E9 SuSE\u3002</p></li></ul>',37),p=[r];function x(o,d){return i(),u("div",null,p)}var h=n(t,[["render",x],["__file","intro.html.vue"]]);export{h as default};
