import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";import{o as r,c as s,b as o,a as t,d as e,e as n,r as p}from"./app.0c030768.js";const a={},g=o("h1",{id:"rocketmq-\u5165\u95E8\u603B\u7ED3",tabindex:"-1"},[o("a",{class:"header-anchor",href:"#rocketmq-\u5165\u95E8\u603B\u7ED3","aria-hidden":"true"},"#"),e(" RocketMQ \u5165\u95E8\u603B\u7ED3")],-1),i=o("p",null,"\u6587\u7AE0\u5F88\u957F\uFF0C\u70B9\u8D5E\u518D\u770B\uFF0C\u517B\u6210\u597D\u4E60\u60EF\u{1F60B}\u{1F60B}\u{1F60B}",-1),l={href:"https://mp.weixin.qq.com/s?__biz=Mzg2OTA0Njk0OA==&mid=2247485969&idx=1&sn=6bd53abde30d42a778d5a35ec104428c&chksm=cea245daf9d5cccce631f93115f0c2c4a7634e55f5bef9009fd03f5a0ffa55b745b5ef4f0530&token=294077121&lang=zh_CN#rd",target:"_blank",rel:"noopener noreferrer"},u=e("\u672C\u6587\u7531 FrancisQ \u8001\u54E5\u6295\u7A3F\uFF01"),h=n('<h2 id="\u6D88\u606F\u961F\u5217\u626B\u76F2" tabindex="-1"><a class="header-anchor" href="#\u6D88\u606F\u961F\u5217\u626B\u76F2" aria-hidden="true">#</a> \u6D88\u606F\u961F\u5217\u626B\u76F2</h2><p>\u6D88\u606F\u961F\u5217\u987E\u540D\u601D\u4E49\u5C31\u662F\u5B58\u653E\u6D88\u606F\u7684\u961F\u5217\uFF0C\u961F\u5217\u6211\u5C31\u4E0D\u89E3\u91CA\u4E86\uFF0C\u522B\u544A\u8BC9\u6211\u4F60\u8FDE\u961F\u5217\u90FD\u4E0D\u77E5\u9053\u662F\u5565\u5427\uFF1F</p><p>\u6240\u4EE5\u95EE\u9898\u5E76\u4E0D\u662F\u6D88\u606F\u961F\u5217\u662F\u4EC0\u4E48\uFF0C\u800C\u662F <strong>\u6D88\u606F\u961F\u5217\u4E3A\u4EC0\u4E48\u4F1A\u51FA\u73B0\uFF1F\u6D88\u606F\u961F\u5217\u80FD\u7528\u6765\u5E72\u4EC0\u4E48\uFF1F\u7528\u5B83\u6765\u5E72\u8FD9\u4E9B\u4E8B\u4F1A\u5E26\u6765\u4EC0\u4E48\u597D\u5904\uFF1F\u6D88\u606F\u961F\u5217\u4F1A\u5E26\u6765\u526F\u4F5C\u7528\u5417\uFF1F</strong></p><h3 id="\u6D88\u606F\u961F\u5217\u4E3A\u4EC0\u4E48\u4F1A\u51FA\u73B0" tabindex="-1"><a class="header-anchor" href="#\u6D88\u606F\u961F\u5217\u4E3A\u4EC0\u4E48\u4F1A\u51FA\u73B0" aria-hidden="true">#</a> \u6D88\u606F\u961F\u5217\u4E3A\u4EC0\u4E48\u4F1A\u51FA\u73B0\uFF1F</h3><p>\u6D88\u606F\u961F\u5217\u7B97\u662F\u4F5C\u4E3A\u540E\u7AEF\u7A0B\u5E8F\u5458\u7684\u4E00\u4E2A\u5FC5\u5907\u6280\u80FD\u5427\uFF0C\u56E0\u4E3A<strong>\u5206\u5E03\u5F0F\u5E94\u7528\u5FC5\u5B9A\u6D89\u53CA\u5230\u5404\u4E2A\u7CFB\u7EDF\u4E4B\u95F4\u7684\u901A\u4FE1\u95EE\u9898</strong>\uFF0C\u8FD9\u4E2A\u65F6\u5019\u6D88\u606F\u961F\u5217\u4E5F\u5E94\u8FD0\u800C\u751F\u4E86\u3002\u53EF\u4EE5\u8BF4\u5206\u5E03\u5F0F\u7684\u4EA7\u751F\u662F\u6D88\u606F\u961F\u5217\u7684\u57FA\u7840\uFF0C\u800C\u5206\u5E03\u5F0F\u6015\u662F\u4E00\u4E2A\u5F88\u53E4\u8001\u7684\u6982\u5FF5\u4E86\u5427\uFF0C\u6240\u4EE5\u6D88\u606F\u961F\u5217\u4E5F\u662F\u4E00\u4E2A\u5F88\u53E4\u8001\u7684\u4E2D\u95F4\u4EF6\u4E86\u3002</p><h3 id="\u6D88\u606F\u961F\u5217\u80FD\u7528\u6765\u5E72\u4EC0\u4E48" tabindex="-1"><a class="header-anchor" href="#\u6D88\u606F\u961F\u5217\u80FD\u7528\u6765\u5E72\u4EC0\u4E48" aria-hidden="true">#</a> \u6D88\u606F\u961F\u5217\u80FD\u7528\u6765\u5E72\u4EC0\u4E48\uFF1F</h3><h4 id="\u5F02\u6B65" tabindex="-1"><a class="header-anchor" href="#\u5F02\u6B65" aria-hidden="true">#</a> \u5F02\u6B65</h4><p>\u4F60\u53EF\u80FD\u4F1A\u53CD\u9A73\u6211\uFF0C\u5E94\u7528\u4E4B\u95F4\u7684\u901A\u4FE1\u53C8\u4E0D\u662F\u53EA\u80FD\u7531\u6D88\u606F\u961F\u5217\u89E3\u51B3\uFF0C\u597D\u597D\u7684\u901A\u4FE1\u4E3A\u4EC0\u4E48\u4E2D\u95F4\u975E\u8981\u63D2\u4E00\u4E2A\u6D88\u606F\u961F\u5217\u5462\uFF1F\u6211\u4E0D\u80FD\u76F4\u63A5\u8FDB\u884C\u901A\u4FE1\u5417\uFF1F</p><p>\u5F88\u597D\u{1F44D}\uFF0C\u4F60\u53C8\u63D0\u51FA\u4E86\u4E00\u4E2A\u6982\u5FF5\uFF0C<strong>\u540C\u6B65\u901A\u4FE1</strong>\u3002\u5C31\u6BD4\u5982\u73B0\u5728\u4E1A\u754C\u4F7F\u7528\u6BD4\u8F83\u591A\u7684 <code>Dubbo</code> \u5C31\u662F\u4E00\u4E2A\u9002\u7528\u4E8E\u5404\u4E2A\u7CFB\u7EDF\u4E4B\u95F4\u540C\u6B65\u901A\u4FE1\u7684 <code>RPC</code> \u6846\u67B6\u3002</p><p>\u6211\u6765\u4E3E\u4E2A\u{1F330}\u5427\uFF0C\u6BD4\u5982\u6211\u4EEC\u6709\u4E00\u4E2A\u8D2D\u7968\u7CFB\u7EDF\uFF0C\u9700\u6C42\u662F\u7528\u6237\u5728\u8D2D\u4E70\u5B8C\u4E4B\u540E\u80FD\u63A5\u6536\u5230\u8D2D\u4E70\u5B8C\u6210\u7684\u77ED\u4FE1\u3002</p><p><img src="https://my-blog-to-use.oss-cn-beijing.aliyuncs.com/2019-11/16ef37fee7e09230.jpg" alt="" loading="lazy"></p><p>\u6211\u4EEC\u7701\u7565\u4E2D\u95F4\u7684\u7F51\u7EDC\u901A\u4FE1\u65F6\u95F4\u6D88\u8017\uFF0C\u5047\u5982\u8D2D\u7968\u7CFB\u7EDF\u5904\u7406\u9700\u8981 150ms \uFF0C\u77ED\u4FE1\u7CFB\u7EDF\u5904\u7406\u9700\u8981 200ms \uFF0C\u90A3\u4E48\u6574\u4E2A\u5904\u7406\u6D41\u7A0B\u7684\u65F6\u95F4\u6D88\u8017\u5C31\u662F 150ms + 200ms = 350ms\u3002</p><p>\u5F53\u7136\uFF0C\u4E4D\u770B\u6CA1\u4EC0\u4E48\u95EE\u9898\u3002\u53EF\u662F\u4ED4\u7EC6\u4E00\u60F3\u4F60\u5C31\u611F\u89C9\u6709\u70B9\u95EE\u9898\uFF0C\u6211\u7528\u6237\u8D2D\u7968\u5728\u8D2D\u7968\u7CFB\u7EDF\u7684\u65F6\u5019\u5176\u5B9E\u5C31\u5DF2\u7ECF\u5B8C\u6210\u4E86\u8D2D\u4E70\uFF0C\u800C\u6211\u73B0\u5728\u901A\u8FC7\u540C\u6B65\u8C03\u7528\u975E\u8981\u8BA9\u6574\u4E2A\u8BF7\u6C42\u62C9\u957F\u65F6\u95F4\uFF0C\u800C\u77ED\u4FE1\u7CFB\u7EDF\u8FD9\u73A9\u610F\u53C8\u4E0D\u662F\u5F88\u6709\u5FC5\u8981\uFF0C\u5B83\u4EC5\u4EC5\u662F\u4E00\u4E2A\u8F85\u52A9\u529F\u80FD\u589E\u5F3A\u7528\u6237\u4F53\u9A8C\u611F\u800C\u5DF2\u3002\u6211\u73B0\u5728\u6574\u4E2A\u8C03\u7528\u6D41\u7A0B\u5C31\u6709\u70B9 <strong>\u5934\u91CD\u811A\u8F7B</strong> \u7684\u611F\u89C9\u4E86\uFF0C\u8D2D\u7968\u662F\u4E00\u4E2A\u4E0D\u592A\u8017\u65F6\u7684\u6D41\u7A0B\uFF0C\u800C\u6211\u73B0\u5728\u56E0\u4E3A\u540C\u6B65\u8C03\u7528\uFF0C\u975E\u8981\u7B49\u5F85\u53D1\u9001\u77ED\u4FE1\u8FD9\u4E2A\u6BD4\u8F83\u8017\u65F6\u7684\u64CD\u4F5C\u624D\u8FD4\u56DE\u7ED3\u679C\u3002\u90A3\u6211\u5982\u679C\u518D\u52A0\u4E00\u4E2A\u53D1\u9001\u90AE\u4EF6\u5462\uFF1F</p><p><img src="https://my-blog-to-use.oss-cn-beijing.aliyuncs.com/2019-11/16ef380429cf373e.jpg" alt="" loading="lazy"></p><p>\u8FD9\u6837\u6574\u4E2A\u7CFB\u7EDF\u7684\u8C03\u7528\u94FE\u53C8\u53D8\u957F\u4E86\uFF0C\u6574\u4E2A\u65F6\u95F4\u5C31\u53D8\u6210\u4E86550ms\u3002</p><p>\u5F53\u6211\u4EEC\u5728\u5B66\u751F\u65F6\u4EE3\u9700\u8981\u5728\u98DF\u5802\u6392\u961F\u7684\u65F6\u5019\uFF0C\u6211\u4EEC\u548C\u98DF\u5802\u5927\u5988\u5C31\u662F\u4E00\u4E2A\u540C\u6B65\u7684\u6A21\u578B\u3002</p><p>\u6211\u4EEC\u9700\u8981\u544A\u8BC9\u98DF\u5802\u5927\u5988\uFF1A\u201C\u59D0\u59D0\uFF0C\u7ED9\u6211\u52A0\u4E2A\u9E21\u817F\uFF0C\u518D\u52A0\u4E2A\u9178\u8FA3\u571F\u8C46\u4E1D\uFF0C\u5E2E\u6211\u6D47\u70B9\u6C41\u4E0A\u53BB\uFF0C\u591A\u6253\u70B9\u996D\u54E6\u{1F60B}\u{1F60B}\u{1F60B}\u201D \u54A6~~~ \u4E3A\u4E86\u591A\u5403\u70B9\uFF0C\u771F\u6076\u5FC3\u3002</p><p>\u7136\u540E\u5927\u5988\u5E2E\u6211\u4EEC\u6253\u996D\u914D\u83DC\uFF0C\u6211\u4EEC\u770B\u7740\u5927\u5988\u90A3\u98A4\u6296\u7684\u624B\u548C\u6389\u843D\u7684\u571F\u8C46\u4E1D\u4E0D\u7981\u54BD\u4E86\u54BD\u53E3\u6C34\u3002</p><p>\u6700\u7EC8\u6211\u4EEC\u4ECE\u5927\u5988\u624B\u4E2D\u63A5\u8FC7\u996D\u83DC\u7136\u540E\u53BB\u5BFB\u627E\u5EA7\u4F4D\u4E86...</p><p>\u56DE\u60F3\u4E00\u4E0B\uFF0C\u6211\u4EEC\u5728\u7ED9\u5927\u5988\u53D1\u9001\u9700\u8981\u7684\u4FE1\u606F\u4E4B\u540E\u6211\u4EEC\u662F <strong>\u540C\u6B65\u7B49\u5F85\u5927\u5988\u7ED9\u6211\u914D\u597D\u996D\u83DC</strong> \u7684\uFF0C\u4E0A\u9762\u6211\u4EEC\u53EA\u662F\u52A0\u4E86\u9E21\u817F\u548C\u571F\u8C46\u4E1D\uFF0C\u4E07\u4E00\u6211\u518D\u52A0\u4E00\u4E2A\u756A\u8304\u725B\u8169\uFF0C\u97ED\u83DC\u9E21\u86CB\uFF0C\u8FD9\u6837\u662F\u4E0D\u662F\u5927\u5988\u6253\u996D\u914D\u83DC\u7684\u6D41\u7A0B\u5C31\u4F1A\u53D8\u957F\uFF0C\u6211\u4EEC\u7B49\u5F85\u7684\u65F6\u95F4\u4E5F\u4F1A\u76F8\u5E94\u7684\u53D8\u957F\u3002</p><p><img src="https://my-blog-to-use.oss-cn-beijing.aliyuncs.com/2019-11/006APoFYly1fvd9cwjlfrj30as0b03ym.jpg" alt="" loading="lazy"></p><p>\u90A3\u540E\u6765\uFF0C\u6211\u4EEC\u5DE5\u4F5C\u8D5A\u94B1\u4E86\u6709\u94B1\u53BB\u996D\u5E97\u5403\u996D\u4E86\uFF0C\u6211\u4EEC\u544A\u8BC9\u670D\u52A1\u5458\u6765\u4E00\u7897\u725B\u8089\u9762\u52A0\u4E2A\u8377\u5305\u86CB <strong>(\u4F20\u8FBE\u4E00\u4E2A\u6D88\u606F)</strong> \uFF0C\u7136\u540E\u6211\u4EEC\u5C31\u53EF\u4EE5\u5728\u996D\u684C\u4E0A\u5B89\u5FC3\u7684\u73A9\u624B\u673A\u4E86 <strong>(\u5E72\u81EA\u5DF1\u5176\u4ED6\u4E8B\u60C5)</strong> \uFF0C\u7B49\u5230\u6211\u4EEC\u7684\u725B\u8089\u9762\u4E0A\u4E86\u6211\u4EEC\u5C31\u53EF\u4EE5\u5403\u4E86\u3002\u8FD9\u5176\u4E2D\u6211\u4EEC\u4E5F\u5C31\u4F20\u8FBE\u4E86\u4E00\u4E2A\u6D88\u606F\uFF0C\u7136\u540E\u6211\u4EEC\u53C8\u8F6C\u8FC7\u5934\u5E72\u5176\u4ED6\u4E8B\u60C5\u4E86\u3002\u8FD9\u5176\u4E2D\u867D\u7136\u505A\u9762\u7684\u65F6\u95F4\u6CA1\u6709\u53D8\u77ED\uFF0C\u4F46\u662F\u6211\u4EEC\u53EA\u9700\u8981\u4F20\u8FBE\u4E00\u4E2A\u6D88\u606F\u5C31\u53EF\u4EE5\u5E72\u5176\u4ED6\u4E8B\u60C5\u4E86\uFF0C\u8FD9\u662F\u4E00\u4E2A <strong>\u5F02\u6B65</strong> \u7684\u6982\u5FF5\u3002</p><p>\u6240\u4EE5\uFF0C\u4E3A\u4E86\u89E3\u51B3\u8FD9\u4E00\u4E2A\u95EE\u9898\uFF0C\u806A\u660E\u7684\u7A0B\u5E8F\u5458\u5728\u4E2D\u95F4\u4E5F\u52A0\u4E86\u4E2A\u7C7B\u4F3C\u4E8E\u670D\u52A1\u5458\u7684\u4E2D\u95F4\u4EF6\u2014\u2014\u6D88\u606F\u961F\u5217\u3002\u8FD9\u4E2A\u65F6\u5019\u6211\u4EEC\u5C31\u53EF\u4EE5\u628A\u6A21\u578B\u7ED9\u6539\u9020\u4E86\u3002</p><p><img src="https://my-blog-to-use.oss-cn-beijing.aliyuncs.com/2019-11/16ef38124f55eaea.jpg" alt="" loading="lazy"></p><p>\u8FD9\u6837\uFF0C\u6211\u4EEC\u5728\u5C06\u6D88\u606F\u5B58\u5165\u6D88\u606F\u961F\u5217\u4E4B\u540E\u6211\u4EEC\u5C31\u53EF\u4EE5\u76F4\u63A5\u8FD4\u56DE\u4E86(\u6211\u4EEC\u544A\u8BC9\u670D\u52A1\u5458\u6211\u4EEC\u8981\u5403\u4EC0\u4E48\u7136\u540E\u73A9\u624B\u673A)\uFF0C\u6240\u4EE5\u6574\u4E2A\u8017\u65F6\u53EA\u662F 150ms + 10ms = 160ms\u3002</p><blockquote><p>\u4F46\u662F\u4F60\u9700\u8981\u6CE8\u610F\u7684\u662F\uFF0C\u6574\u4E2A\u6D41\u7A0B\u7684\u65F6\u957F\u662F\u6CA1\u53D8\u7684\uFF0C\u5C31\u50CF\u4F60\u4EC5\u4EC5\u544A\u8BC9\u670D\u52A1\u5458\u8981\u5403\u4EC0\u4E48\u662F\u4E0D\u4F1A\u5F71\u54CD\u5230\u505A\u9762\u7684\u901F\u5EA6\u7684\u3002</p></blockquote><h4 id="\u89E3\u8026" tabindex="-1"><a class="header-anchor" href="#\u89E3\u8026" aria-hidden="true">#</a> \u89E3\u8026</h4><p>\u56DE\u5230\u6700\u521D\u540C\u6B65\u8C03\u7528\u7684\u8FC7\u7A0B\uFF0C\u6211\u4EEC\u5199\u4E2A\u4F2A\u4EE3\u7801\u7B80\u5355\u6982\u62EC\u4E00\u4E0B\u3002</p><p><img src="https://my-blog-to-use.oss-cn-beijing.aliyuncs.com/2019-11/16ef381a505d3e1f.jpg" alt="" loading="lazy"></p><p>\u90A3\u4E48\u7B2C\u4E8C\u6B65\uFF0C\u6211\u4EEC\u53C8\u6DFB\u52A0\u4E86\u4E00\u4E2A\u53D1\u9001\u90AE\u4EF6\uFF0C\u6211\u4EEC\u5C31\u5F97\u91CD\u65B0\u53BB\u4FEE\u6539\u4EE3\u7801\uFF0C\u5982\u679C\u6211\u4EEC\u53C8\u52A0\u4E00\u4E2A\u9700\u6C42\uFF1A\u7528\u6237\u8D2D\u4E70\u5B8C\u8FD8\u9700\u8981\u7ED9\u4ED6\u52A0\u79EF\u5206\uFF0C\u8FD9\u4E2A\u65F6\u5019\u6211\u4EEC\u662F\u4E0D\u662F\u53C8\u5F97\u6539\u4EE3\u7801\uFF1F</p><p><img src="https://my-blog-to-use.oss-cn-beijing.aliyuncs.com/2019-11/16ef381c4e1b1ac7.jpg" alt="" loading="lazy"></p><p>\u5982\u679C\u4F60\u89C9\u5F97\u8FD8\u884C\uFF0C\u90A3\u4E48\u6211\u8FD9\u4E2A\u65F6\u5019\u4E0D\u8981\u53D1\u90AE\u4EF6\u8FD9\u4E2A\u670D\u52A1\u4E86\u5462\uFF0C\u6211\u662F\u4E0D\u662F\u53C8\u5F97\u6539\u4EE3\u7801\uFF0C\u53C8\u5F97\u91CD\u542F\u5E94\u7528\uFF1F</p><p><img src="https://my-blog-to-use.oss-cn-beijing.aliyuncs.com/2019-11/16ef381f273a66bd.jpg" alt="" loading="lazy"></p><p>\u8FD9\u6837\u6539\u6765\u6539\u53BB\u662F\u4E0D\u662F\u5F88\u9EBB\u70E6\uFF0C\u90A3\u4E48 <strong>\u6B64\u65F6\u6211\u4EEC\u5C31\u7528\u4E00\u4E2A\u6D88\u606F\u961F\u5217\u5728\u4E2D\u95F4\u8FDB\u884C\u89E3\u8026</strong> \u3002\u4F60\u9700\u8981\u6CE8\u610F\u7684\u662F\uFF0C\u6211\u4EEC\u540E\u9762\u7684\u53D1\u9001\u77ED\u4FE1\u3001\u53D1\u9001\u90AE\u4EF6\u3001\u6DFB\u52A0\u79EF\u5206\u7B49\u4E00\u4E9B\u64CD\u4F5C\u90FD\u4F9D\u8D56\u4E8E\u4E0A\u9762\u7684 <code>result</code> \uFF0C\u8FD9\u4E1C\u897F\u62BD\u8C61\u51FA\u6765\u5C31\u662F\u8D2D\u7968\u7684\u5904\u7406\u7ED3\u679C\u5440\uFF0C\u6BD4\u5982\u8BA2\u5355\u53F7\uFF0C\u7528\u6237\u8D26\u53F7\u7B49\u7B49\uFF0C\u4E5F\u5C31\u662F\u8BF4\u6211\u4EEC\u540E\u9762\u7684\u4E00\u7CFB\u5217\u670D\u52A1\u90FD\u662F\u9700\u8981\u540C\u6837\u7684\u6D88\u606F\u6765\u8FDB\u884C\u5904\u7406\u3002\u65E2\u7136\u8FD9\u6837\uFF0C\u6211\u4EEC\u662F\u4E0D\u662F\u53EF\u4EE5\u901A\u8FC7 <strong>\u201C\u5E7F\u64AD\u6D88\u606F\u201D</strong> \u6765\u5B9E\u73B0\u3002</p><p>\u6211\u4E0A\u9762\u6240\u8BB2\u7684\u201C\u5E7F\u64AD\u201D\u5E76\u4E0D\u662F\u771F\u6B63\u7684\u5E7F\u64AD\uFF0C\u800C\u662F\u63A5\u4E0B\u6765\u7684\u7CFB\u7EDF\u4F5C\u4E3A\u6D88\u8D39\u8005\u53BB <strong>\u8BA2\u9605</strong> \u7279\u5B9A\u7684\u4E3B\u9898\u3002\u6BD4\u5982\u6211\u4EEC\u8FD9\u91CC\u7684\u4E3B\u9898\u5C31\u53EF\u4EE5\u53EB\u505A <code>\u8BA2\u7968</code> \uFF0C\u6211\u4EEC\u8D2D\u4E70\u7CFB\u7EDF\u4F5C\u4E3A\u4E00\u4E2A\u751F\u4EA7\u8005\u53BB\u751F\u4EA7\u8FD9\u6761\u6D88\u606F\u653E\u5165\u6D88\u606F\u961F\u5217\uFF0C\u7136\u540E\u6D88\u8D39\u8005\u8BA2\u9605\u4E86\u8FD9\u4E2A\u4E3B\u9898\uFF0C\u4F1A\u4ECE\u6D88\u606F\u961F\u5217\u4E2D\u62C9\u53D6\u6D88\u606F\u5E76\u6D88\u8D39\u3002\u5C31\u6BD4\u5982\u6211\u4EEC\u521A\u521A\u753B\u7684\u90A3\u5F20\u56FE\uFF0C\u4F60\u4F1A\u53D1\u73B0\uFF0C\u5728\u751F\u4EA7\u8005\u8FD9\u8FB9\u6211\u4EEC\u53EA\u9700\u8981\u5173\u6CE8 <strong>\u751F\u4EA7\u6D88\u606F\u5230\u6307\u5B9A\u4E3B\u9898\u4E2D</strong> \uFF0C\u800C <strong>\u6D88\u8D39\u8005\u53EA\u9700\u8981\u5173\u6CE8\u4ECE\u6307\u5B9A\u4E3B\u9898\u4E2D\u62C9\u53D6\u6D88\u606F</strong> \u5C31\u884C\u4E86\u3002</p><p><img src="https://my-blog-to-use.oss-cn-beijing.aliyuncs.com/2019-11/16ef382674b66892.jpg" alt="" loading="lazy"></p><blockquote><p>\u5982\u679C\u6CA1\u6709\u6D88\u606F\u961F\u5217\uFF0C\u6BCF\u5F53\u4E00\u4E2A\u65B0\u7684\u4E1A\u52A1\u63A5\u5165\uFF0C\u6211\u4EEC\u90FD\u8981\u5728\u4E3B\u7CFB\u7EDF\u8C03\u7528\u65B0\u63A5\u53E3\u3001\u6216\u8005\u5F53\u6211\u4EEC\u53D6\u6D88\u67D0\u4E9B\u4E1A\u52A1\uFF0C\u6211\u4EEC\u4E5F\u5F97\u5728\u4E3B\u7CFB\u7EDF\u5220\u9664\u67D0\u4E9B\u63A5\u53E3\u8C03\u7528\u3002\u6709\u4E86\u6D88\u606F\u961F\u5217\uFF0C\u6211\u4EEC\u53EA\u9700\u8981\u5173\u5FC3\u6D88\u606F\u662F\u5426\u9001\u8FBE\u4E86\u961F\u5217\uFF0C\u81F3\u4E8E\u8C01\u5E0C\u671B\u8BA2\u9605\uFF0C\u63A5\u4E0B\u6765\u6536\u5230\u6D88\u606F\u5982\u4F55\u5904\u7406\uFF0C\u662F\u4E0B\u6E38\u7684\u4E8B\u60C5\uFF0C\u65E0\u7591\u6781\u5927\u5730\u51CF\u5C11\u4E86\u5F00\u53D1\u548C\u8054\u8C03\u7684\u5DE5\u4F5C\u91CF\u3002</p></blockquote><h4 id="\u524A\u5CF0" tabindex="-1"><a class="header-anchor" href="#\u524A\u5CF0" aria-hidden="true">#</a> \u524A\u5CF0</h4><p>\u6211\u4EEC\u518D\u6B21\u56DE\u5230\u4E00\u5F00\u59CB\u6211\u4EEC\u4F7F\u7528\u540C\u6B65\u8C03\u7528\u7CFB\u7EDF\u7684\u60C5\u51B5\uFF0C\u5E76\u4E14\u601D\u8003\u4E00\u4E0B\uFF0C\u5982\u679C\u6B64\u65F6\u6709\u5927\u91CF\u7528\u6237\u8BF7\u6C42\u8D2D\u7968\u6574\u4E2A\u7CFB\u7EDF\u4F1A\u53D8\u6210\u4EC0\u4E48\u6837\uFF1F</p><p><img src="https://my-blog-to-use.oss-cn-beijing.aliyuncs.com/2019-11/16ef382a9756bb1c.jpg" alt="" loading="lazy"></p><p>\u5982\u679C\uFF0C\u6B64\u65F6\u6709\u4E00\u4E07\u7684\u8BF7\u6C42\u8FDB\u5165\u8D2D\u7968\u7CFB\u7EDF\uFF0C\u6211\u4EEC\u77E5\u9053\u8FD0\u884C\u6211\u4EEC\u4E3B\u4E1A\u52A1\u7684\u670D\u52A1\u5668\u914D\u7F6E\u4E00\u822C\u4F1A\u6BD4\u8F83\u597D\uFF0C\u6240\u4EE5\u8FD9\u91CC\u6211\u4EEC\u5047\u8BBE\u8D2D\u7968\u7CFB\u7EDF\u80FD\u627F\u53D7\u8FD9\u4E00\u4E07\u7684\u7528\u6237\u8BF7\u6C42\uFF0C\u90A3\u4E48\u4E5F\u5C31\u610F\u5473\u7740\u6211\u4EEC\u540C\u65F6\u4E5F\u4F1A\u51FA\u73B0\u4E00\u4E07\u8C03\u7528\u53D1\u77ED\u4FE1\u670D\u52A1\u7684\u8BF7\u6C42\u3002\u800C\u5BF9\u4E8E\u77ED\u4FE1\u7CFB\u7EDF\u6765\u8BF4\u5E76\u4E0D\u662F\u6211\u4EEC\u7684\u4E3B\u8981\u4E1A\u52A1\uFF0C\u6240\u4EE5\u6211\u4EEC\u914D\u5907\u7684\u786C\u4EF6\u8D44\u6E90\u5E76\u4E0D\u4F1A\u592A\u9AD8\uFF0C\u90A3\u4E48\u4F60\u89C9\u5F97\u73B0\u5728\u8FD9\u4E2A\u77ED\u4FE1\u7CFB\u7EDF\u80FD\u627F\u53D7\u8FD9\u4E00\u4E07\u7684\u5CF0\u503C\u4E48\uFF0C\u4E14\u4E0D\u8BF4\u80FD\u4E0D\u80FD\u627F\u53D7\uFF0C\u7CFB\u7EDF\u4F1A\u4E0D\u4F1A <strong>\u76F4\u63A5\u5D29\u6E83</strong> \u4E86\uFF1F</p><p>\u77ED\u4FE1\u4E1A\u52A1\u53C8\u4E0D\u662F\u6211\u4EEC\u7684\u4E3B\u4E1A\u52A1\uFF0C\u6211\u4EEC\u80FD\u4E0D\u80FD <strong>\u6298\u4E2D\u5904\u7406</strong> \u5462\uFF1F\u5982\u679C\u6211\u4EEC\u628A\u8D2D\u4E70\u5B8C\u6210\u7684\u4FE1\u606F\u53D1\u9001\u5230\u6D88\u606F\u961F\u5217\u4E2D\uFF0C\u800C\u77ED\u4FE1\u7CFB\u7EDF <strong>\u5C3D\u81EA\u5DF1\u6240\u80FD\u5730\u53BB\u6D88\u606F\u961F\u5217\u4E2D\u53D6\u6D88\u606F\u548C\u6D88\u8D39\u6D88\u606F</strong> \uFF0C\u5373\u4F7F\u5904\u7406\u901F\u5EA6\u6162\u4E00\u70B9\u4E5F\u65E0\u6240\u8C13\uFF0C\u53EA\u8981\u6211\u4EEC\u7684\u7CFB\u7EDF\u6CA1\u6709\u5D29\u6E83\u5C31\u884C\u4E86\u3002</p><p>\u7559\u5F97\u6C5F\u5C71\u5728\uFF0C\u8FD8\u6015\u6CA1\u67F4\u70E7\uFF1F\u4F60\u6562\u8BF4\u6BCF\u6B21\u53D1\u9001\u9A8C\u8BC1\u7801\u7684\u65F6\u5019\u662F\u4E00\u53D1\u4F60\u5C31\u6536\u5230\u4E86\u7684\u4E48\uFF1F</p><h4 id="\u6D88\u606F\u961F\u5217\u80FD\u5E26\u6765\u4EC0\u4E48\u597D\u5904" tabindex="-1"><a class="header-anchor" href="#\u6D88\u606F\u961F\u5217\u80FD\u5E26\u6765\u4EC0\u4E48\u597D\u5904" aria-hidden="true">#</a> \u6D88\u606F\u961F\u5217\u80FD\u5E26\u6765\u4EC0\u4E48\u597D\u5904\uFF1F</h4><p>\u5176\u5B9E\u4E0A\u9762\u6211\u5DF2\u7ECF\u8BF4\u4E86\u3002<strong>\u5F02\u6B65\u3001\u89E3\u8026\u3001\u524A\u5CF0\u3002</strong> \u54EA\u6015\u4F60\u4E0A\u9762\u7684\u90FD\u6CA1\u770B\u61C2\u4E5F\u5343\u4E07\u8981\u8BB0\u4F4F\u8FD9\u516D\u4E2A\u5B57\uFF0C\u56E0\u4E3A\u4ED6\u4E0D\u4EC5\u662F\u6D88\u606F\u961F\u5217\u7684\u7CBE\u534E\uFF0C\u66F4\u662F\u7F16\u7A0B\u548C\u67B6\u6784\u7684\u7CBE\u534E\u3002</p><h4 id="\u6D88\u606F\u961F\u5217\u4F1A\u5E26\u6765\u526F\u4F5C\u7528\u5417" tabindex="-1"><a class="header-anchor" href="#\u6D88\u606F\u961F\u5217\u4F1A\u5E26\u6765\u526F\u4F5C\u7528\u5417" aria-hidden="true">#</a> \u6D88\u606F\u961F\u5217\u4F1A\u5E26\u6765\u526F\u4F5C\u7528\u5417\uFF1F</h4><p>\u6CA1\u6709\u54EA\u4E00\u95E8\u6280\u672F\u662F\u201C\u94F6\u5F39\u201D\uFF0C\u6D88\u606F\u961F\u5217\u4E5F\u6709\u5B83\u7684\u526F\u4F5C\u7528\u3002</p><p>\u6BD4\u5982\uFF0C\u672C\u6765\u597D\u597D\u7684\u4E24\u4E2A\u7CFB\u7EDF\u4E4B\u95F4\u7684\u8C03\u7528\uFF0C\u6211\u4E2D\u95F4\u52A0\u4E86\u4E2A\u6D88\u606F\u961F\u5217\uFF0C\u5982\u679C\u6D88\u606F\u961F\u5217\u6302\u4E86\u600E\u4E48\u529E\u5462\uFF1F\u662F\u4E0D\u662F <strong>\u964D\u4F4E\u4E86\u7CFB\u7EDF\u7684\u53EF\u7528\u6027</strong> \uFF1F</p><p>\u90A3\u8FD9\u6837\u662F\u4E0D\u662F\u8981\u4FDD\u8BC1HA(\u9AD8\u53EF\u7528)\uFF1F\u662F\u4E0D\u662F\u8981\u641E\u96C6\u7FA4\uFF1F\u90A3\u4E48\u6211 <strong>\u6574\u4E2A\u7CFB\u7EDF\u7684\u590D\u6742\u5EA6\u662F\u4E0D\u662F\u4E0A\u5347\u4E86</strong> \uFF1F</p><p>\u629B\u5F00\u4E0A\u9762\u7684\u95EE\u9898\u4E0D\u8BB2\uFF0C\u4E07\u4E00\u6211\u53D1\u9001\u65B9\u53D1\u9001\u5931\u8D25\u4E86\uFF0C\u7136\u540E\u6267\u884C\u91CD\u8BD5\uFF0C\u8FD9\u6837\u5C31\u53EF\u80FD\u4EA7\u751F\u91CD\u590D\u7684\u6D88\u606F\u3002</p><p>\u6216\u8005\u6211\u6D88\u8D39\u7AEF\u5904\u7406\u5931\u8D25\u4E86\uFF0C\u8BF7\u6C42\u91CD\u53D1\uFF0C\u8FD9\u6837\u4E5F\u4F1A\u4EA7\u751F\u91CD\u590D\u7684\u6D88\u606F\u3002</p><p>\u5BF9\u4E8E\u4E00\u4E9B\u5FAE\u670D\u52A1\u6765\u8BF4\uFF0C\u6D88\u8D39\u91CD\u590D\u6D88\u606F\u4F1A\u5E26\u6765\u66F4\u5927\u7684\u9EBB\u70E6\uFF0C\u6BD4\u5982\u589E\u52A0\u79EF\u5206\uFF0C\u8FD9\u4E2A\u65F6\u5019\u6211\u52A0\u4E86\u591A\u6B21\u662F\u4E0D\u662F\u5BF9\u5176\u4ED6\u7528\u6237\u4E0D\u516C\u5E73\uFF1F</p><p>\u90A3\u4E48\uFF0C\u53C8 <strong>\u5982\u4F55\u89E3\u51B3\u91CD\u590D\u6D88\u8D39\u6D88\u606F\u7684\u95EE\u9898</strong> \u5462\uFF1F</p><p>\u5982\u679C\u6211\u4EEC\u6B64\u65F6\u7684\u6D88\u606F\u9700\u8981\u4FDD\u8BC1\u4E25\u683C\u7684\u987A\u5E8F\u6027\u600E\u4E48\u529E\u5462\uFF1F\u6BD4\u5982\u751F\u4EA7\u8005\u751F\u4EA7\u4E86\u4E00\u7CFB\u5217\u7684\u6709\u5E8F\u6D88\u606F(\u5BF9\u4E00\u4E2Aid\u4E3A1\u7684\u8BB0\u5F55\u8FDB\u884C\u5220\u9664\u589E\u52A0\u4FEE\u6539)\uFF0C\u4F46\u662F\u6211\u4EEC\u77E5\u9053\u5728\u53D1\u5E03\u8BA2\u9605\u6A21\u578B\u4E2D\uFF0C\u5BF9\u4E8E\u4E3B\u9898\u662F\u65E0\u987A\u5E8F\u7684\uFF0C\u90A3\u4E48\u8FD9\u4E2A\u65F6\u5019\u5C31\u4F1A\u5BFC\u81F4\u5BF9\u4E8E\u6D88\u8D39\u8005\u6D88\u8D39\u6D88\u606F\u7684\u65F6\u5019\u6CA1\u6709\u6309\u7167\u751F\u4EA7\u8005\u7684\u53D1\u9001\u987A\u5E8F\u6D88\u8D39\uFF0C\u6BD4\u5982\u8FD9\u4E2A\u65F6\u5019\u6211\u4EEC\u6D88\u8D39\u7684\u987A\u5E8F\u4E3A\u4FEE\u6539\u5220\u9664\u589E\u52A0\uFF0C\u5982\u679C\u8BE5\u8BB0\u5F55\u6D89\u53CA\u5230\u91D1\u989D\u7684\u8BDD\u662F\u4E0D\u662F\u4F1A\u51FA\u5927\u4E8B\u60C5\uFF1F</p><p>\u90A3\u4E48\uFF0C\u53C8 <strong>\u5982\u4F55\u89E3\u51B3\u6D88\u606F\u7684\u987A\u5E8F\u6D88\u8D39\u95EE\u9898</strong> \u5462\uFF1F</p><p>\u5C31\u62FF\u6211\u4EEC\u4E0A\u9762\u6240\u8BB2\u7684\u5206\u5E03\u5F0F\u7CFB\u7EDF\u6765\u8BF4\uFF0C\u7528\u6237\u8D2D\u7968\u5B8C\u6210\u4E4B\u540E\u662F\u4E0D\u662F\u9700\u8981\u589E\u52A0\u8D26\u6237\u79EF\u5206\uFF1F\u5728\u540C\u4E00\u4E2A\u7CFB\u7EDF\u4E2D\u6211\u4EEC\u4E00\u822C\u4F1A\u4F7F\u7528\u4E8B\u52A1\u6765\u8FDB\u884C\u89E3\u51B3\uFF0C\u5982\u679C\u7528 <code>Spring</code> \u7684\u8BDD\u6211\u4EEC\u5728\u4E0A\u9762\u4F2A\u4EE3\u7801\u4E2D\u52A0\u5165 <code>@Transactional</code> \u6CE8\u89E3\u5C31\u597D\u4E86\u3002\u4F46\u662F\u5728\u4E0D\u540C\u7CFB\u7EDF\u4E2D\u5982\u4F55\u4FDD\u8BC1\u4E8B\u52A1\u5462\uFF1F\u603B\u4E0D\u80FD\u8FD9\u4E2A\u7CFB\u7EDF\u6211\u6263\u94B1\u6210\u529F\u4E86\u4F60\u90A3\u79EF\u5206\u7CFB\u7EDF\u79EF\u5206\u6CA1\u52A0\u5427\uFF1F\u6216\u8005\u8BF4\u6211\u8FD9\u6263\u94B1\u660E\u660E\u5931\u8D25\u4E86\uFF0C\u4F60\u90A3\u79EF\u5206\u7CFB\u7EDF\u7ED9\u6211\u52A0\u4E86\u79EF\u5206\u3002</p><p>\u90A3\u4E48\uFF0C\u53C8\u5982\u4F55 <strong>\u89E3\u51B3\u5206\u5E03\u5F0F\u4E8B\u52A1\u95EE\u9898</strong> \u5462\uFF1F</p><p>\u6211\u4EEC\u521A\u521A\u8BF4\u4E86\uFF0C\u6D88\u606F\u961F\u5217\u53EF\u4EE5\u8FDB\u884C\u524A\u5CF0\u64CD\u4F5C\uFF0C\u90A3\u5982\u679C\u6211\u7684\u6D88\u8D39\u8005\u5982\u679C\u6D88\u8D39\u5F88\u6162\u6216\u8005\u751F\u4EA7\u8005\u751F\u4EA7\u6D88\u606F\u5F88\u5FEB\uFF0C\u8FD9\u6837\u662F\u4E0D\u662F\u4F1A\u5C06\u6D88\u606F\u5806\u79EF\u5728\u6D88\u606F\u961F\u5217\u4E2D\uFF1F</p><p>\u90A3\u4E48\uFF0C\u53C8\u5982\u4F55 <strong>\u89E3\u51B3\u6D88\u606F\u5806\u79EF\u7684\u95EE\u9898</strong> \u5462\uFF1F</p><p>\u53EF\u7528\u6027\u964D\u4F4E\uFF0C\u590D\u6742\u5EA6\u4E0A\u5347\uFF0C\u53C8\u5E26\u6765\u4E00\u7CFB\u5217\u7684\u91CD\u590D\u6D88\u8D39\uFF0C\u987A\u5E8F\u6D88\u8D39\uFF0C\u5206\u5E03\u5F0F\u4E8B\u52A1\uFF0C\u6D88\u606F\u5806\u79EF\u7684\u95EE\u9898\uFF0C\u8FD9\u6D88\u606F\u961F\u5217\u8FD8\u600E\u4E48\u7528\u554A\u{1F635}\uFF1F</p><p><img src="https://my-blog-to-use.oss-cn-beijing.aliyuncs.com/2019-11/16ef382d709abc9d.png" alt="" loading="lazy"></p><p>\u522B\u6025\uFF0C\u529E\u6CD5\u603B\u662F\u6709\u7684\u3002</p><h2 id="rocketmq\u662F\u4EC0\u4E48" tabindex="-1"><a class="header-anchor" href="#rocketmq\u662F\u4EC0\u4E48" aria-hidden="true">#</a> RocketMQ\u662F\u4EC0\u4E48\uFF1F</h2><p><img src="https://my-blog-to-use.oss-cn-beijing.aliyuncs.com/2019-11/16ef383014430799.jpg" alt="" loading="lazy"></p><p>\u54C7\uFF0C\u4F60\u4E2A\u6DF7\u86CB\uFF01\u4E0A\u9762\u7ED9\u6211\u629B\u51FA\u90A3\u4E48\u591A\u95EE\u9898\uFF0C\u4F60\u73B0\u5728\u53C8\u8BB2 <code>RocketMQ</code> \uFF0C\u8FD8\u8BA9\u4E0D\u8BA9\u4EBA\u6D3B\u4E86\uFF1F\uFF01\u{1F92C}</p><p>\u522B\u6025\u522B\u6025\uFF0C\u8BDD\u8BF4\u4F60\u73B0\u5728\u6E05\u695A <code>MQ</code> \u7684\u6784\u9020\u5417\uFF0C\u6211\u8FD8\u6CA1\u8BB2\u5462\uFF0C\u6211\u4EEC\u5148\u641E\u660E\u767D <code>MQ</code> \u7684\u5185\u90E8\u6784\u9020\uFF0C\u518D\u6765\u770B\u770B\u5982\u4F55\u89E3\u51B3\u4E0A\u9762\u7684\u4E00\u7CFB\u5217\u95EE\u9898\u5427\uFF0C\u4E0D\u8FC7\u4F60\u6700\u597D\u5E26\u7740\u95EE\u9898\u53BB\u9605\u8BFB\u548C\u4E86\u89E3\u5594\u3002</p><p><code>RocketMQ</code> \u662F\u4E00\u4E2A <strong>\u961F\u5217\u6A21\u578B</strong> \u7684\u6D88\u606F\u4E2D\u95F4\u4EF6\uFF0C\u5177\u6709<strong>\u9AD8\u6027\u80FD\u3001\u9AD8\u53EF\u9760\u3001\u9AD8\u5B9E\u65F6\u3001\u5206\u5E03\u5F0F</strong> \u7684\u7279\u70B9\u3002\u5B83\u662F\u4E00\u4E2A\u91C7\u7528 <code>Java</code> \u8BED\u8A00\u5F00\u53D1\u7684\u5206\u5E03\u5F0F\u7684\u6D88\u606F\u7CFB\u7EDF\uFF0C\u7531\u963F\u91CC\u5DF4\u5DF4\u56E2\u961F\u5F00\u53D1\uFF0C\u57282016\u5E74\u5E95\u8D21\u732E\u7ED9 <code>Apache</code>\uFF0C\u6210\u4E3A\u4E86 <code>Apache</code> \u7684\u4E00\u4E2A\u9876\u7EA7\u9879\u76EE\u3002 \u5728\u963F\u91CC\u5185\u90E8\uFF0C<code>RocketMQ</code> \u5F88\u597D\u5730\u670D\u52A1\u4E86\u96C6\u56E2\u5927\u5927\u5C0F\u5C0F\u4E0A\u5343\u4E2A\u5E94\u7528\uFF0C\u5728\u6BCF\u5E74\u7684\u53CC\u5341\u4E00\u5F53\u5929\uFF0C\u66F4\u6709\u4E0D\u53EF\u601D\u8BAE\u7684\u4E07\u4EBF\u7EA7\u6D88\u606F\u901A\u8FC7 <code>RocketMQ</code> \u6D41\u8F6C\u3002</p><p>\u5E9F\u8BDD\u4E0D\u591A\u8BF4\uFF0C\u60F3\u8981\u4E86\u89E3 <code>RocketMQ</code> \u5386\u53F2\u7684\u540C\u5B66\u53EF\u4EE5\u81EA\u5DF1\u53BB\u641C\u5BFB\u8D44\u6599\u3002\u542C\u5B8C\u4E0A\u9762\u7684\u4ECB\u7ECD\uFF0C\u4F60\u53EA\u8981\u77E5\u9053 <code>RocketMQ</code> \u5F88\u5FEB\u3001\u5F88\u725B\u3001\u800C\u4E14\u7ECF\u5386\u8FC7\u53CC\u5341\u4E00\u7684\u5B9E\u8DF5\u5C31\u884C\u4E86\uFF01</p><h2 id="\u961F\u5217\u6A21\u578B\u548C\u4E3B\u9898\u6A21\u578B" tabindex="-1"><a class="header-anchor" href="#\u961F\u5217\u6A21\u578B\u548C\u4E3B\u9898\u6A21\u578B" aria-hidden="true">#</a> \u961F\u5217\u6A21\u578B\u548C\u4E3B\u9898\u6A21\u578B</h2><p>\u5728\u8C08 <code>RocketMQ</code> \u7684\u6280\u672F\u67B6\u6784\u4E4B\u524D\uFF0C\u6211\u4EEC\u5148\u6765\u4E86\u89E3\u4E00\u4E0B\u4E24\u4E2A\u540D\u8BCD\u6982\u5FF5\u2014\u2014<strong>\u961F\u5217\u6A21\u578B</strong> \u548C <strong>\u4E3B\u9898\u6A21\u578B</strong> \u3002</p><p>\u9996\u5148\u6211\u95EE\u4E00\u4E2A\u95EE\u9898\uFF0C\u6D88\u606F\u961F\u5217\u4E3A\u4EC0\u4E48\u8981\u53EB\u6D88\u606F\u961F\u5217\uFF1F</p><p>\u4F60\u53EF\u80FD\u89C9\u5F97\u5F88\u5F31\u667A\uFF0C\u8FD9\u73A9\u610F\u4E0D\u5C31\u662F\u5B58\u653E\u6D88\u606F\u7684\u961F\u5217\u561B\uFF1F\u4E0D\u53EB\u6D88\u606F\u961F\u5217\u53EB\u4EC0\u4E48\uFF1F</p><p>\u7684\u786E\uFF0C\u65E9\u671F\u7684\u6D88\u606F\u4E2D\u95F4\u4EF6\u662F\u901A\u8FC7 <strong>\u961F\u5217</strong> \u8FD9\u4E00\u6A21\u578B\u6765\u5B9E\u73B0\u7684\uFF0C\u53EF\u80FD\u662F\u5386\u53F2\u539F\u56E0\uFF0C\u6211\u4EEC\u90FD\u4E60\u60EF\u628A\u6D88\u606F\u4E2D\u95F4\u4EF6\u6210\u4E3A\u6D88\u606F\u961F\u5217\u3002</p><p>\u4F46\u662F\uFF0C\u5982\u4ECA\u4F8B\u5982 <code>RocketMQ</code> \u3001<code>Kafka</code> \u8FD9\u4E9B\u4F18\u79C0\u7684\u6D88\u606F\u4E2D\u95F4\u4EF6\u4E0D\u4EC5\u4EC5\u662F\u901A\u8FC7\u4E00\u4E2A <strong>\u961F\u5217</strong> \u6765\u5B9E\u73B0\u6D88\u606F\u5B58\u50A8\u7684\u3002</p><h3 id="\u961F\u5217\u6A21\u578B" tabindex="-1"><a class="header-anchor" href="#\u961F\u5217\u6A21\u578B" aria-hidden="true">#</a> \u961F\u5217\u6A21\u578B</h3><p>\u5C31\u50CF\u6211\u4EEC\u7406\u89E3\u961F\u5217\u4E00\u6837\uFF0C\u6D88\u606F\u4E2D\u95F4\u4EF6\u7684\u961F\u5217\u6A21\u578B\u5C31\u771F\u7684\u53EA\u662F\u4E00\u4E2A\u961F\u5217\u3002\u3002\u3002\u6211\u753B\u4E00\u5F20\u56FE\u7ED9\u5927\u5BB6\u7406\u89E3\u3002</p><p><img src="https://my-blog-to-use.oss-cn-beijing.aliyuncs.com/2019-11/16ef3834ae653469.jpg" alt="" loading="lazy"></p><p>\u5728\u4E00\u5F00\u59CB\u6211\u8DDF\u4F60\u63D0\u5230\u4E86\u4E00\u4E2A <strong>\u201C\u5E7F\u64AD\u201D</strong> \u7684\u6982\u5FF5\uFF0C\u4E5F\u5C31\u662F\u8BF4\u5982\u679C\u6211\u4EEC\u6B64\u65F6\u6211\u4EEC\u9700\u8981\u5C06\u4E00\u4E2A\u6D88\u606F\u53D1\u9001\u7ED9\u591A\u4E2A\u6D88\u8D39\u8005(\u6BD4\u5982\u6B64\u65F6\u6211\u9700\u8981\u5C06\u4FE1\u606F\u53D1\u9001\u7ED9\u77ED\u4FE1\u7CFB\u7EDF\u548C\u90AE\u4EF6\u7CFB\u7EDF)\uFF0C\u8FD9\u4E2A\u65F6\u5019\u5355\u4E2A\u961F\u5217\u5373\u4E0D\u80FD\u6EE1\u8DB3\u9700\u6C42\u4E86\u3002</p><p>\u5F53\u7136\u4F60\u53EF\u4EE5\u8BA9 <code>Producer</code> \u751F\u4EA7\u6D88\u606F\u653E\u5165\u591A\u4E2A\u961F\u5217\u4E2D\uFF0C\u7136\u540E\u6BCF\u4E2A\u961F\u5217\u53BB\u5BF9\u5E94\u6BCF\u4E00\u4E2A\u6D88\u8D39\u8005\u3002\u95EE\u9898\u662F\u53EF\u4EE5\u89E3\u51B3\uFF0C\u521B\u5EFA\u591A\u4E2A\u961F\u5217\u5E76\u4E14\u590D\u5236\u591A\u4EFD\u6D88\u606F\u662F\u4F1A\u5F88\u5F71\u54CD\u8D44\u6E90\u548C\u6027\u80FD\u7684\u3002\u800C\u4E14\uFF0C\u8FD9\u6837\u5B50\u5C31\u4F1A\u5BFC\u81F4\u751F\u4EA7\u8005\u9700\u8981\u77E5\u9053\u5177\u4F53\u6D88\u8D39\u8005\u4E2A\u6570\u7136\u540E\u53BB\u590D\u5236\u5BF9\u5E94\u6570\u91CF\u7684\u6D88\u606F\u961F\u5217\uFF0C\u8FD9\u5C31\u8FDD\u80CC\u6211\u4EEC\u6D88\u606F\u4E2D\u95F4\u4EF6\u7684 <strong>\u89E3\u8026</strong> \u8FD9\u4E00\u539F\u5219\u3002</p><h3 id="\u4E3B\u9898\u6A21\u578B" tabindex="-1"><a class="header-anchor" href="#\u4E3B\u9898\u6A21\u578B" aria-hidden="true">#</a> \u4E3B\u9898\u6A21\u578B</h3><p>\u90A3\u4E48\u6709\u6CA1\u6709\u597D\u7684\u65B9\u6CD5\u53BB\u89E3\u51B3\u8FD9\u4E00\u4E2A\u95EE\u9898\u5462\uFF1F\u6709\uFF0C\u90A3\u5C31\u662F <strong>\u4E3B\u9898\u6A21\u578B</strong> \u6216\u8005\u53EF\u4EE5\u79F0\u4E3A <strong>\u53D1\u5E03\u8BA2\u9605\u6A21\u578B</strong> \u3002</p><blockquote><p>\u611F\u5174\u8DA3\u7684\u540C\u5B66\u53EF\u4EE5\u53BB\u4E86\u89E3\u4E00\u4E0B\u8BBE\u8BA1\u6A21\u5F0F\u91CC\u9762\u7684\u89C2\u5BDF\u8005\u6A21\u5F0F\u5E76\u4E14\u624B\u52A8\u5B9E\u73B0\u4E00\u4E0B\uFF0C\u6211\u76F8\u4FE1\u4F60\u4F1A\u6709\u6240\u6536\u83B7\u7684\u3002</p></blockquote><p>\u5728\u4E3B\u9898\u6A21\u578B\u4E2D\uFF0C\u6D88\u606F\u7684\u751F\u4EA7\u8005\u79F0\u4E3A <strong>\u53D1\u5E03\u8005(Publisher)</strong> \uFF0C\u6D88\u606F\u7684\u6D88\u8D39\u8005\u79F0\u4E3A <strong>\u8BA2\u9605\u8005(Subscriber)</strong> \uFF0C\u5B58\u653E\u6D88\u606F\u7684\u5BB9\u5668\u79F0\u4E3A <strong>\u4E3B\u9898(Topic)</strong> \u3002</p><p>\u5176\u4E2D\uFF0C\u53D1\u5E03\u8005\u5C06\u6D88\u606F\u53D1\u9001\u5230\u6307\u5B9A\u4E3B\u9898\u4E2D\uFF0C\u8BA2\u9605\u8005\u9700\u8981 <strong>\u63D0\u524D\u8BA2\u9605\u4E3B\u9898</strong> \u624D\u80FD\u63A5\u53D7\u7279\u5B9A\u4E3B\u9898\u7684\u6D88\u606F\u3002</p><p><img src="https://my-blog-to-use.oss-cn-beijing.aliyuncs.com/2019-11/16ef3837887d9a54sds.jpg" alt="" loading="lazy"></p><h3 id="rocketmq\u4E2D\u7684\u6D88\u606F\u6A21\u578B" tabindex="-1"><a class="header-anchor" href="#rocketmq\u4E2D\u7684\u6D88\u606F\u6A21\u578B" aria-hidden="true">#</a> RocketMQ\u4E2D\u7684\u6D88\u606F\u6A21\u578B</h3><p><code>RocketMQ</code> \u4E2D\u7684\u6D88\u606F\u6A21\u578B\u5C31\u662F\u6309\u7167 <strong>\u4E3B\u9898\u6A21\u578B</strong> \u6240\u5B9E\u73B0\u7684\u3002\u4F60\u53EF\u80FD\u4F1A\u597D\u5947\u8FD9\u4E2A <strong>\u4E3B\u9898</strong> \u5230\u5E95\u662F\u600E\u4E48\u5B9E\u73B0\u7684\u5462\uFF1F\u4F60\u4E0A\u9762\u4E5F\u6CA1\u6709\u8BB2\u5230\u5440\uFF01</p><p>\u5176\u5B9E\u5BF9\u4E8E\u4E3B\u9898\u6A21\u578B\u7684\u5B9E\u73B0\u6765\u8BF4\u6BCF\u4E2A\u6D88\u606F\u4E2D\u95F4\u4EF6\u7684\u5E95\u5C42\u8BBE\u8BA1\u90FD\u662F\u4E0D\u4E00\u6837\u7684\uFF0C\u5C31\u6BD4\u5982 <code>Kafka</code> \u4E2D\u7684 <strong>\u5206\u533A</strong> \uFF0C<code>RocketMQ</code> \u4E2D\u7684 <strong>\u961F\u5217</strong> \uFF0C<code>RabbitMQ</code> \u4E2D\u7684 <code>Exchange</code> \u3002\u6211\u4EEC\u53EF\u4EE5\u7406\u89E3\u4E3A <strong>\u4E3B\u9898\u6A21\u578B/\u53D1\u5E03\u8BA2\u9605\u6A21\u578B</strong> \u5C31\u662F\u4E00\u4E2A\u6807\u51C6\uFF0C\u90A3\u4E9B\u4E2D\u95F4\u4EF6\u53EA\u4E0D\u8FC7\u7167\u7740\u8FD9\u4E2A\u6807\u51C6\u53BB\u5B9E\u73B0\u800C\u5DF2\u3002</p><p>\u6240\u4EE5\uFF0C<code>RocketMQ</code> \u4E2D\u7684 <strong>\u4E3B\u9898\u6A21\u578B</strong> \u5230\u5E95\u662F\u5982\u4F55\u5B9E\u73B0\u7684\u5462\uFF1F\u9996\u5148\u6211\u753B\u4E00\u5F20\u56FE\uFF0C\u5927\u5BB6\u5C1D\u8BD5\u7740\u53BB\u7406\u89E3\u4E00\u4E0B\u3002</p><p><img src="https://my-blog-to-use.oss-cn-beijing.aliyuncs.com/2019-11/16ef383d3e8c9788.jpg" alt="" loading="lazy"></p><p>\u6211\u4EEC\u53EF\u4EE5\u770B\u5230\u5728\u6574\u4E2A\u56FE\u4E2D\u6709 <code>Producer Group</code> \u3001<code>Topic</code> \u3001<code>Consumer Group</code> \u4E09\u4E2A\u89D2\u8272\uFF0C\u6211\u6765\u5206\u522B\u4ECB\u7ECD\u4E00\u4E0B\u4ED6\u4EEC\u3002</p><ul><li><code>Producer Group</code> \u751F\u4EA7\u8005\u7EC4\uFF1A \u4EE3\u8868\u67D0\u4E00\u7C7B\u7684\u751F\u4EA7\u8005\uFF0C\u6BD4\u5982\u6211\u4EEC\u6709\u591A\u4E2A\u79D2\u6740\u7CFB\u7EDF\u4F5C\u4E3A\u751F\u4EA7\u8005\uFF0C\u8FD9\u591A\u4E2A\u5408\u5728\u4E00\u8D77\u5C31\u662F\u4E00\u4E2A <code>Producer Group</code> \u751F\u4EA7\u8005\u7EC4\uFF0C\u5B83\u4EEC\u4E00\u822C\u751F\u4EA7\u76F8\u540C\u7684\u6D88\u606F\u3002</li><li><code>Consumer Group</code> \u6D88\u8D39\u8005\u7EC4\uFF1A \u4EE3\u8868\u67D0\u4E00\u7C7B\u7684\u6D88\u8D39\u8005\uFF0C\u6BD4\u5982\u6211\u4EEC\u6709\u591A\u4E2A\u77ED\u4FE1\u7CFB\u7EDF\u4F5C\u4E3A\u6D88\u8D39\u8005\uFF0C\u8FD9\u591A\u4E2A\u5408\u5728\u4E00\u8D77\u5C31\u662F\u4E00\u4E2A <code>Consumer Group</code> \u6D88\u8D39\u8005\u7EC4\uFF0C\u5B83\u4EEC\u4E00\u822C\u6D88\u8D39\u76F8\u540C\u7684\u6D88\u606F\u3002</li><li><code>Topic</code> \u4E3B\u9898\uFF1A \u4EE3\u8868\u4E00\u7C7B\u6D88\u606F\uFF0C\u6BD4\u5982\u8BA2\u5355\u6D88\u606F\uFF0C\u7269\u6D41\u6D88\u606F\u7B49\u7B49\u3002</li></ul><p>\u4F60\u53EF\u4EE5\u770B\u5230\u56FE\u4E2D\u751F\u4EA7\u8005\u7EC4\u4E2D\u7684\u751F\u4EA7\u8005\u4F1A\u5411\u4E3B\u9898\u53D1\u9001\u6D88\u606F\uFF0C\u800C <strong>\u4E3B\u9898\u4E2D\u5B58\u5728\u591A\u4E2A\u961F\u5217</strong>\uFF0C\u751F\u4EA7\u8005\u6BCF\u6B21\u751F\u4EA7\u6D88\u606F\u4E4B\u540E\u662F\u6307\u5B9A\u4E3B\u9898\u4E2D\u7684\u67D0\u4E2A\u961F\u5217\u53D1\u9001\u6D88\u606F\u7684\u3002</p><p>\u6BCF\u4E2A\u4E3B\u9898\u4E2D\u90FD\u6709\u591A\u4E2A\u961F\u5217(\u5206\u5E03\u5728\u4E0D\u540C\u7684 <code>Broker</code>\u4E2D\uFF0C\u5982\u679C\u662F\u96C6\u7FA4\u7684\u8BDD\uFF0C<code>Broker</code>\u53C8\u5206\u5E03\u5728\u4E0D\u540C\u7684\u670D\u52A1\u5668\u4E2D)\uFF0C\u96C6\u7FA4\u6D88\u8D39\u6A21\u5F0F\u4E0B\uFF0C\u4E00\u4E2A\u6D88\u8D39\u8005\u96C6\u7FA4\u591A\u53F0\u673A\u5668\u5171\u540C\u6D88\u8D39\u4E00\u4E2A <code>topic</code> \u7684\u591A\u4E2A\u961F\u5217\uFF0C<strong>\u4E00\u4E2A\u961F\u5217\u53EA\u4F1A\u88AB\u4E00\u4E2A\u6D88\u8D39\u8005\u6D88\u8D39</strong>\u3002\u5982\u679C\u67D0\u4E2A\u6D88\u8D39\u8005\u6302\u6389\uFF0C\u5206\u7EC4\u5185\u5176\u5B83\u6D88\u8D39\u8005\u4F1A\u63A5\u66FF\u6302\u6389\u7684\u6D88\u8D39\u8005\u7EE7\u7EED\u6D88\u8D39\u3002\u5C31\u50CF\u4E0A\u56FE\u4E2D <code>Consumer1</code> \u548C <code>Consumer2</code> \u5206\u522B\u5BF9\u5E94\u7740\u4E24\u4E2A\u961F\u5217\uFF0C\u800C <code>Consumer3</code> \u662F\u6CA1\u6709\u961F\u5217\u5BF9\u5E94\u7684\uFF0C\u6240\u4EE5\u4E00\u822C\u6765\u8BB2\u8981\u63A7\u5236 <strong>\u6D88\u8D39\u8005\u7EC4\u4E2D\u7684\u6D88\u8D39\u8005\u4E2A\u6570\u548C\u4E3B\u9898\u4E2D\u961F\u5217\u4E2A\u6570\u76F8\u540C</strong> \u3002</p><p>\u5F53\u7136\u4E5F\u53EF\u4EE5\u6D88\u8D39\u8005\u4E2A\u6570\u5C0F\u4E8E\u961F\u5217\u4E2A\u6570\uFF0C\u53EA\u4E0D\u8FC7\u4E0D\u592A\u5EFA\u8BAE\u3002\u5982\u4E0B\u56FE\u3002</p><p><img src="https://my-blog-to-use.oss-cn-beijing.aliyuncs.com/2019-11/16ef3850c808d707.jpg" alt="" loading="lazy"></p><p><strong>\u6BCF\u4E2A\u6D88\u8D39\u7EC4\u5728\u6BCF\u4E2A\u961F\u5217\u4E0A\u7EF4\u62A4\u4E00\u4E2A\u6D88\u8D39\u4F4D\u7F6E</strong> \uFF0C\u4E3A\u4EC0\u4E48\u5462\uFF1F</p><p>\u56E0\u4E3A\u6211\u4EEC\u521A\u521A\u753B\u7684\u4EC5\u4EC5\u662F\u4E00\u4E2A\u6D88\u8D39\u8005\u7EC4\uFF0C\u6211\u4EEC\u77E5\u9053\u5728\u53D1\u5E03\u8BA2\u9605\u6A21\u5F0F\u4E2D\u4E00\u822C\u4F1A\u6D89\u53CA\u5230\u591A\u4E2A\u6D88\u8D39\u8005\u7EC4\uFF0C\u800C\u6BCF\u4E2A\u6D88\u8D39\u8005\u7EC4\u5728\u6BCF\u4E2A\u961F\u5217\u4E2D\u7684\u6D88\u8D39\u4F4D\u7F6E\u90FD\u662F\u4E0D\u540C\u7684\u3002\u5982\u679C\u6B64\u65F6\u6709\u591A\u4E2A\u6D88\u8D39\u8005\u7EC4\uFF0C\u90A3\u4E48\u6D88\u606F\u88AB\u4E00\u4E2A\u6D88\u8D39\u8005\u7EC4\u6D88\u8D39\u5B8C\u4E4B\u540E\u662F\u4E0D\u4F1A\u5220\u9664\u7684(\u56E0\u4E3A\u5176\u5B83\u6D88\u8D39\u8005\u7EC4\u4E5F\u9700\u8981\u5440)\uFF0C\u5B83\u4EC5\u4EC5\u662F\u4E3A\u6BCF\u4E2A\u6D88\u8D39\u8005\u7EC4\u7EF4\u62A4\u4E00\u4E2A <strong>\u6D88\u8D39\u4F4D\u79FB(offset)</strong> \uFF0C\u6BCF\u6B21\u6D88\u8D39\u8005\u7EC4\u6D88\u8D39\u5B8C\u4F1A\u8FD4\u56DE\u4E00\u4E2A\u6210\u529F\u7684\u54CD\u5E94\uFF0C\u7136\u540E\u961F\u5217\u518D\u628A\u7EF4\u62A4\u7684\u6D88\u8D39\u4F4D\u79FB\u52A0\u4E00\uFF0C\u8FD9\u6837\u5C31\u4E0D\u4F1A\u51FA\u73B0\u521A\u521A\u6D88\u8D39\u8FC7\u7684\u6D88\u606F\u518D\u4E00\u6B21\u88AB\u6D88\u8D39\u4E86\u3002</p><p><img src="https://my-blog-to-use.oss-cn-beijing.aliyuncs.com/2019-11/16ef3857fefaa079.jpg" alt="" loading="lazy"></p><p>\u53EF\u80FD\u4F60\u8FD8\u6709\u4E00\u4E2A\u95EE\u9898\uFF0C<strong>\u4E3A\u4EC0\u4E48\u4E00\u4E2A\u4E3B\u9898\u4E2D\u9700\u8981\u7EF4\u62A4\u591A\u4E2A\u961F\u5217</strong> \uFF1F</p><p>\u7B54\u6848\u662F <strong>\u63D0\u9AD8\u5E76\u53D1\u80FD\u529B</strong> \u3002\u7684\u786E\uFF0C\u6BCF\u4E2A\u4E3B\u9898\u4E2D\u53EA\u5B58\u5728\u4E00\u4E2A\u961F\u5217\u4E5F\u662F\u53EF\u884C\u7684\u3002\u4F60\u60F3\u4E00\u4E0B\uFF0C\u5982\u679C\u6BCF\u4E2A\u4E3B\u9898\u4E2D\u53EA\u5B58\u5728\u4E00\u4E2A\u961F\u5217\uFF0C\u8FD9\u4E2A\u961F\u5217\u4E2D\u4E5F\u7EF4\u62A4\u7740\u6BCF\u4E2A\u6D88\u8D39\u8005\u7EC4\u7684\u6D88\u8D39\u4F4D\u7F6E\uFF0C\u8FD9\u6837\u4E5F\u53EF\u4EE5\u505A\u5230 <strong>\u53D1\u5E03\u8BA2\u9605\u6A21\u5F0F</strong> \u3002\u5982\u4E0B\u56FE\u3002</p><p><img src="https://my-blog-to-use.oss-cn-beijing.aliyuncs.com/2019-11/16ef38600cdb6d4b.jpg" alt="" loading="lazy"></p><p>\u4F46\u662F\uFF0C\u8FD9\u6837\u6211\u751F\u4EA7\u8005\u662F\u4E0D\u662F\u53EA\u80FD\u5411\u4E00\u4E2A\u961F\u5217\u53D1\u9001\u6D88\u606F\uFF1F\u53C8\u56E0\u4E3A\u9700\u8981\u7EF4\u62A4\u6D88\u8D39\u4F4D\u7F6E\u6240\u4EE5\u4E00\u4E2A\u961F\u5217\u53EA\u80FD\u5BF9\u5E94\u4E00\u4E2A\u6D88\u8D39\u8005\u7EC4\u4E2D\u7684\u6D88\u8D39\u8005\uFF0C\u8FD9\u6837\u662F\u4E0D\u662F\u5176\u4ED6\u7684 <code>Consumer</code> \u5C31\u6CA1\u6709\u7528\u6B66\u4E4B\u5730\u4E86\uFF1F\u4ECE\u8FD9\u4E24\u4E2A\u89D2\u5EA6\u6765\u8BB2\uFF0C\u5E76\u53D1\u5EA6\u4E00\u4E0B\u5B50\u5C31\u5C0F\u4E86\u5F88\u591A\u3002</p><p>\u6240\u4EE5\u603B\u7ED3\u6765\u8BF4\uFF0C<code>RocketMQ</code> \u901A\u8FC7<strong>\u4F7F\u7528\u5728\u4E00\u4E2A <code>Topic</code> \u4E2D\u914D\u7F6E\u591A\u4E2A\u961F\u5217\u5E76\u4E14\u6BCF\u4E2A\u961F\u5217\u7EF4\u62A4\u6BCF\u4E2A\u6D88\u8D39\u8005\u7EC4\u7684\u6D88\u8D39\u4F4D\u7F6E</strong> \u5B9E\u73B0\u4E86 <strong>\u4E3B\u9898\u6A21\u5F0F/\u53D1\u5E03\u8BA2\u9605\u6A21\u5F0F</strong> \u3002</p><h2 id="rocketmq\u7684\u67B6\u6784\u56FE" tabindex="-1"><a class="header-anchor" href="#rocketmq\u7684\u67B6\u6784\u56FE" aria-hidden="true">#</a> RocketMQ\u7684\u67B6\u6784\u56FE</h2><p>\u8BB2\u5B8C\u4E86\u6D88\u606F\u6A21\u578B\uFF0C\u6211\u4EEC\u7406\u89E3\u8D77 <code>RocketMQ</code> \u7684\u6280\u672F\u67B6\u6784\u8D77\u6765\u5C31\u5BB9\u6613\u591A\u4E86\u3002</p><p><code>RocketMQ</code> \u6280\u672F\u67B6\u6784\u4E2D\u6709\u56DB\u5927\u89D2\u8272 <code>NameServer</code> \u3001<code>Broker</code> \u3001<code>Producer</code> \u3001<code>Consumer</code> \u3002\u6211\u6765\u5411\u5927\u5BB6\u5206\u522B\u89E3\u91CA\u4E00\u4E0B\u8FD9\u56DB\u4E2A\u89D2\u8272\u662F\u5E72\u5565\u7684\u3002</p><ul><li><p><code>Broker</code>\uFF1A \u4E3B\u8981\u8D1F\u8D23\u6D88\u606F\u7684\u5B58\u50A8\u3001\u6295\u9012\u548C\u67E5\u8BE2\u4EE5\u53CA\u670D\u52A1\u9AD8\u53EF\u7528\u4FDD\u8BC1\u3002\u8BF4\u767D\u4E86\u5C31\u662F\u6D88\u606F\u961F\u5217\u670D\u52A1\u5668\u561B\uFF0C\u751F\u4EA7\u8005\u751F\u4EA7\u6D88\u606F\u5230 <code>Broker</code> \uFF0C\u6D88\u8D39\u8005\u4ECE <code>Broker</code> \u62C9\u53D6\u6D88\u606F\u5E76\u6D88\u8D39\u3002</p><p>\u8FD9\u91CC\uFF0C\u6211\u8FD8\u5F97\u666E\u53CA\u4E00\u4E0B\u5173\u4E8E <code>Broker</code> \u3001<code>Topic</code> \u548C \u961F\u5217\u7684\u5173\u7CFB\u3002\u4E0A\u9762\u6211\u8BB2\u89E3\u4E86 <code>Topic</code> \u548C\u961F\u5217\u7684\u5173\u7CFB\u2014\u2014\u4E00\u4E2A <code>Topic</code> \u4E2D\u5B58\u5728\u591A\u4E2A\u961F\u5217\uFF0C\u90A3\u4E48\u8FD9\u4E2A <code>Topic</code> \u548C\u961F\u5217\u5B58\u653E\u5728\u54EA\u5462\uFF1F</p><p><strong>\u4E00\u4E2A <code>Topic</code> \u5206\u5E03\u5728\u591A\u4E2A <code>Broker</code>\u4E0A\uFF0C\u4E00\u4E2A <code>Broker</code> \u53EF\u4EE5\u914D\u7F6E\u591A\u4E2A <code>Topic</code> \uFF0C\u5B83\u4EEC\u662F\u591A\u5BF9\u591A\u7684\u5173\u7CFB</strong>\u3002</p><p>\u5982\u679C\u67D0\u4E2A <code>Topic</code> \u6D88\u606F\u91CF\u5F88\u5927\uFF0C\u5E94\u8BE5\u7ED9\u5B83\u591A\u914D\u7F6E\u51E0\u4E2A\u961F\u5217(\u4E0A\u6587\u4E2D\u63D0\u5230\u4E86\u63D0\u9AD8\u5E76\u53D1\u80FD\u529B)\uFF0C\u5E76\u4E14 <strong>\u5C3D\u91CF\u591A\u5206\u5E03\u5728\u4E0D\u540C <code>Broker</code> \u4E0A\uFF0C\u4EE5\u51CF\u8F7B\u67D0\u4E2A <code>Broker</code> \u7684\u538B\u529B</strong> \u3002</p><p><code>Topic</code> \u6D88\u606F\u91CF\u90FD\u6BD4\u8F83\u5747\u5300\u7684\u60C5\u51B5\u4E0B\uFF0C\u5982\u679C\u67D0\u4E2A <code>broker</code> \u4E0A\u7684\u961F\u5217\u8D8A\u591A\uFF0C\u5219\u8BE5 <code>broker</code> \u538B\u529B\u8D8A\u5927\u3002</p><p><img src="https://my-blog-to-use.oss-cn-beijing.aliyuncs.com/2019-11/16ef38687488a5a4.jpg" alt="" loading="lazy"></p><blockquote><p>\u6240\u4EE5\u8BF4\u6211\u4EEC\u9700\u8981\u914D\u7F6E\u591A\u4E2ABroker\u3002</p></blockquote></li><li><p><code>NameServer</code>\uFF1A \u4E0D\u77E5\u9053\u4F60\u4EEC\u6709\u6CA1\u6709\u63A5\u89E6\u8FC7 <code>ZooKeeper</code> \u548C <code>Spring Cloud</code> \u4E2D\u7684 <code>Eureka</code> \uFF0C\u5B83\u5176\u5B9E\u4E5F\u662F\u4E00\u4E2A <strong>\u6CE8\u518C\u4E2D\u5FC3</strong> \uFF0C\u4E3B\u8981\u63D0\u4F9B\u4E24\u4E2A\u529F\u80FD\uFF1A<strong>Broker\u7BA1\u7406</strong> \u548C <strong>\u8DEF\u7531\u4FE1\u606F\u7BA1\u7406</strong> \u3002\u8BF4\u767D\u4E86\u5C31\u662F <code>Broker</code> \u4F1A\u5C06\u81EA\u5DF1\u7684\u4FE1\u606F\u6CE8\u518C\u5230 <code>NameServer</code> \u4E2D\uFF0C\u6B64\u65F6 <code>NameServer</code> \u5C31\u5B58\u653E\u4E86\u5F88\u591A <code>Broker</code> \u7684\u4FE1\u606F(Broker\u7684\u8DEF\u7531\u8868)\uFF0C\u6D88\u8D39\u8005\u548C\u751F\u4EA7\u8005\u5C31\u4ECE <code>NameServer</code> \u4E2D\u83B7\u53D6\u8DEF\u7531\u8868\u7136\u540E\u7167\u7740\u8DEF\u7531\u8868\u7684\u4FE1\u606F\u548C\u5BF9\u5E94\u7684 <code>Broker</code> \u8FDB\u884C\u901A\u4FE1(\u751F\u4EA7\u8005\u548C\u6D88\u8D39\u8005\u5B9A\u671F\u4F1A\u5411 <code>NameServer</code> \u53BB\u67E5\u8BE2\u76F8\u5173\u7684 <code>Broker</code> \u7684\u4FE1\u606F)\u3002</p></li><li><p><code>Producer</code>\uFF1A \u6D88\u606F\u53D1\u5E03\u7684\u89D2\u8272\uFF0C\u652F\u6301\u5206\u5E03\u5F0F\u96C6\u7FA4\u65B9\u5F0F\u90E8\u7F72\u3002\u8BF4\u767D\u4E86\u5C31\u662F\u751F\u4EA7\u8005\u3002</p></li><li><p><code>Consumer</code>\uFF1A \u6D88\u606F\u6D88\u8D39\u7684\u89D2\u8272\uFF0C\u652F\u6301\u5206\u5E03\u5F0F\u96C6\u7FA4\u65B9\u5F0F\u90E8\u7F72\u3002\u652F\u6301\u4EE5push\u63A8\uFF0Cpull\u62C9\u4E24\u79CD\u6A21\u5F0F\u5BF9\u6D88\u606F\u8FDB\u884C\u6D88\u8D39\u3002\u540C\u65F6\u4E5F\u652F\u6301\u96C6\u7FA4\u65B9\u5F0F\u548C\u5E7F\u64AD\u65B9\u5F0F\u7684\u6D88\u8D39\uFF0C\u5B83\u63D0\u4F9B\u5B9E\u65F6\u6D88\u606F\u8BA2\u9605\u673A\u5236\u3002\u8BF4\u767D\u4E86\u5C31\u662F\u6D88\u8D39\u8005\u3002</p></li></ul><p>\u542C\u5B8C\u4E86\u4E0A\u9762\u7684\u89E3\u91CA\u4F60\u53EF\u80FD\u4F1A\u89C9\u5F97\uFF0C\u8FD9\u73A9\u610F\u597D\u7B80\u5355\u3002\u4E0D\u5C31\u662F\u8FD9\u6837\u7684\u4E48\uFF1F</p><p><img src="https://my-blog-to-use.oss-cn-beijing.aliyuncs.com/2019-11/16ef386c6d1e8bdb.jpg" alt="" loading="lazy"></p><p>\u55EF\uFF1F\u4F60\u53EF\u80FD\u4F1A\u53D1\u73B0\u4E00\u4E2A\u95EE\u9898\uFF0C\u8FD9\u8001\u5BB6\u4F19 <code>NameServer</code> \u5E72\u5565\u7528\u7684\uFF0C\u8FD9\u4E0D\u591A\u4F59\u5417\uFF1F\u76F4\u63A5 <code>Producer</code> \u3001<code>Consumer</code> \u548C <code>Broker</code> \u76F4\u63A5\u8FDB\u884C\u751F\u4EA7\u6D88\u606F\uFF0C\u6D88\u8D39\u6D88\u606F\u4E0D\u5C31\u597D\u4E86\u4E48\uFF1F</p><p>\u4F46\u662F\uFF0C\u6211\u4EEC\u4E0A\u6587\u63D0\u5230\u8FC7 <code>Broker</code> \u662F\u9700\u8981\u4FDD\u8BC1\u9AD8\u53EF\u7528\u7684\uFF0C\u5982\u679C\u6574\u4E2A\u7CFB\u7EDF\u4EC5\u4EC5\u9760\u7740\u4E00\u4E2A <code>Broker</code> \u6765\u7EF4\u6301\u7684\u8BDD\uFF0C\u90A3\u4E48\u8FD9\u4E2A <code>Broker</code> \u7684\u538B\u529B\u4F1A\u4E0D\u4F1A\u5F88\u5927\uFF1F\u6240\u4EE5\u6211\u4EEC\u9700\u8981\u4F7F\u7528\u591A\u4E2A <code>Broker</code> \u6765\u4FDD\u8BC1 <strong>\u8D1F\u8F7D\u5747\u8861</strong> \u3002</p><p>\u5982\u679C\u8BF4\uFF0C\u6211\u4EEC\u7684\u6D88\u8D39\u8005\u548C\u751F\u4EA7\u8005\u76F4\u63A5\u548C\u591A\u4E2A <code>Broker</code> \u76F8\u8FDE\uFF0C\u90A3\u4E48\u5F53 <code>Broker</code> \u4FEE\u6539\u7684\u65F6\u5019\u5FC5\u5B9A\u4F1A\u7275\u8FDE\u7740\u6BCF\u4E2A\u751F\u4EA7\u8005\u548C\u6D88\u8D39\u8005\uFF0C\u8FD9\u6837\u5C31\u4F1A\u4EA7\u751F\u8026\u5408\u95EE\u9898\uFF0C\u800C <code>NameServer</code> \u6CE8\u518C\u4E2D\u5FC3\u5C31\u662F\u7528\u6765\u89E3\u51B3\u8FD9\u4E2A\u95EE\u9898\u7684\u3002</p><blockquote><p>\u5982\u679C\u8FD8\u4E0D\u662F\u5F88\u7406\u89E3\u7684\u8BDD\uFF0C\u53EF\u4EE5\u53BB\u770B\u6211\u4ECB\u7ECD <code>Spring Cloud</code> \u7684\u90A3\u7BC7\u6587\u7AE0\uFF0C\u5176\u4E2D\u4ECB\u7ECD\u4E86 <code>Eureka</code> \u6CE8\u518C\u4E2D\u5FC3\u3002</p></blockquote><p>\u5F53\u7136\uFF0C<code>RocketMQ</code> \u4E2D\u7684\u6280\u672F\u67B6\u6784\u80AF\u5B9A\u4E0D\u6B62\u524D\u9762\u90A3\u4E48\u7B80\u5355\uFF0C\u56E0\u4E3A\u4E0A\u9762\u56FE\u4E2D\u7684\u56DB\u4E2A\u89D2\u8272\u90FD\u662F\u9700\u8981\u505A\u96C6\u7FA4\u7684\u3002\u6211\u7ED9\u51FA\u4E00\u5F20\u5B98\u7F51\u7684\u67B6\u6784\u56FE\uFF0C\u5927\u5BB6\u5C1D\u8BD5\u7406\u89E3\u4E00\u4E0B\u3002</p><p><img src="https://my-blog-to-use.oss-cn-beijing.aliyuncs.com/2019-11/16ef386fa3be1e53.jpg" alt="" loading="lazy"></p><p>\u5176\u5B9E\u548C\u6211\u4EEC\u6700\u5F00\u59CB\u753B\u7684\u90A3\u5F20\u4E5E\u4E10\u7248\u7684\u67B6\u6784\u56FE\u4E5F\u6CA1\u4EC0\u4E48\u533A\u522B\uFF0C\u4E3B\u8981\u662F\u4E00\u4E9B\u7EC6\u8282\u4E0A\u7684\u5DEE\u522B\u3002\u542C\u6211\u7EC6\u7EC6\u9053\u6765\u{1F928}\u3002</p><p>\u7B2C\u4E00\u3001\u6211\u4EEC\u7684 <code>Broker</code> <strong>\u505A\u4E86\u96C6\u7FA4\u5E76\u4E14\u8FD8\u8FDB\u884C\u4E86\u4E3B\u4ECE\u90E8\u7F72</strong> \uFF0C\u7531\u4E8E\u6D88\u606F\u5206\u5E03\u5728\u5404\u4E2A <code>Broker</code> \u4E0A\uFF0C\u4E00\u65E6\u67D0\u4E2A <code>Broker</code> \u5B95\u673A\uFF0C\u5219\u8BE5<code>Broker</code> \u4E0A\u7684\u6D88\u606F\u8BFB\u5199\u90FD\u4F1A\u53D7\u5230\u5F71\u54CD\u3002\u6240\u4EE5 <code>Rocketmq</code> \u63D0\u4F9B\u4E86 <code>master/slave</code> \u7684\u7ED3\u6784\uFF0C<code> salve</code> \u5B9A\u65F6\u4ECE <code>master</code> \u540C\u6B65\u6570\u636E(\u540C\u6B65\u5237\u76D8\u6216\u8005\u5F02\u6B65\u5237\u76D8)\uFF0C\u5982\u679C <code>master</code> \u5B95\u673A\uFF0C<strong>\u5219 <code>slave</code> \u63D0\u4F9B\u6D88\u8D39\u670D\u52A1\uFF0C\u4F46\u662F\u4E0D\u80FD\u5199\u5165\u6D88\u606F</strong> (\u540E\u9762\u6211\u8FD8\u4F1A\u63D0\u5230\u54E6)\u3002</p><p>\u7B2C\u4E8C\u3001\u4E3A\u4E86\u4FDD\u8BC1 <code>HA</code> \uFF0C\u6211\u4EEC\u7684 <code>NameServer</code> \u4E5F\u505A\u4E86\u96C6\u7FA4\u90E8\u7F72\uFF0C\u4F46\u662F\u8BF7\u6CE8\u610F\u5B83\u662F <strong>\u53BB\u4E2D\u5FC3\u5316</strong> \u7684\u3002\u4E5F\u5C31\u610F\u5473\u7740\u5B83\u6CA1\u6709\u4E3B\u8282\u70B9\uFF0C\u4F60\u53EF\u4EE5\u5F88\u660E\u663E\u5730\u770B\u51FA <code>NameServer</code> \u7684\u6240\u6709\u8282\u70B9\u662F\u6CA1\u6709\u8FDB\u884C <code>Info Replicate</code> \u7684\uFF0C\u5728 <code>RocketMQ</code> \u4E2D\u662F\u901A\u8FC7 <strong>\u5355\u4E2ABroker\u548C\u6240\u6709NameServer\u4FDD\u6301\u957F\u8FDE\u63A5</strong> \uFF0C\u5E76\u4E14\u5728\u6BCF\u969430\u79D2 <code>Broker</code> \u4F1A\u5411\u6240\u6709 <code>Nameserver</code> \u53D1\u9001\u5FC3\u8DF3\uFF0C\u5FC3\u8DF3\u5305\u542B\u4E86\u81EA\u8EAB\u7684 <code>Topic</code> \u914D\u7F6E\u4FE1\u606F\uFF0C\u8FD9\u4E2A\u6B65\u9AA4\u5C31\u5BF9\u5E94\u8FD9\u4E0A\u9762\u7684 <code>Routing Info</code> \u3002</p><p>\u7B2C\u4E09\u3001\u5728\u751F\u4EA7\u8005\u9700\u8981\u5411 <code>Broker</code> \u53D1\u9001\u6D88\u606F\u7684\u65F6\u5019\uFF0C<strong>\u9700\u8981\u5148\u4ECE <code>NameServer</code> \u83B7\u53D6\u5173\u4E8E <code>Broker</code> \u7684\u8DEF\u7531\u4FE1\u606F</strong>\uFF0C\u7136\u540E\u901A\u8FC7 <strong>\u8F6E\u8BE2</strong> \u7684\u65B9\u6CD5\u53BB\u5411\u6BCF\u4E2A\u961F\u5217\u4E2D\u751F\u4EA7\u6570\u636E\u4EE5\u8FBE\u5230 <strong>\u8D1F\u8F7D\u5747\u8861</strong> \u7684\u6548\u679C\u3002</p><p>\u7B2C\u56DB\u3001\u6D88\u8D39\u8005\u901A\u8FC7 <code>NameServer</code> \u83B7\u53D6\u6240\u6709 <code>Broker</code> \u7684\u8DEF\u7531\u4FE1\u606F\u540E\uFF0C\u5411 <code>Broker</code> \u53D1\u9001 <code>Pull</code> \u8BF7\u6C42\u6765\u83B7\u53D6\u6D88\u606F\u6570\u636E\u3002<code>Consumer</code> \u53EF\u4EE5\u4EE5\u4E24\u79CD\u6A21\u5F0F\u542F\u52A8\u2014\u2014 <strong>\u5E7F\u64AD\uFF08Broadcast\uFF09\u548C\u96C6\u7FA4\uFF08Cluster\uFF09</strong>\u3002\u5E7F\u64AD\u6A21\u5F0F\u4E0B\uFF0C\u4E00\u6761\u6D88\u606F\u4F1A\u53D1\u9001\u7ED9 <strong>\u540C\u4E00\u4E2A\u6D88\u8D39\u7EC4\u4E2D\u7684\u6240\u6709\u6D88\u8D39\u8005</strong> \uFF0C\u96C6\u7FA4\u6A21\u5F0F\u4E0B\u6D88\u606F\u53EA\u4F1A\u53D1\u9001\u7ED9\u4E00\u4E2A\u6D88\u8D39\u8005\u3002</p><h2 id="\u5982\u4F55\u89E3\u51B3-\u987A\u5E8F\u6D88\u8D39\u3001\u91CD\u590D\u6D88\u8D39" tabindex="-1"><a class="header-anchor" href="#\u5982\u4F55\u89E3\u51B3-\u987A\u5E8F\u6D88\u8D39\u3001\u91CD\u590D\u6D88\u8D39" aria-hidden="true">#</a> \u5982\u4F55\u89E3\u51B3 \u987A\u5E8F\u6D88\u8D39\u3001\u91CD\u590D\u6D88\u8D39</h2><p>\u5176\u5B9E\uFF0C\u8FD9\u4E9B\u4E1C\u897F\u90FD\u662F\u6211\u5728\u4ECB\u7ECD\u6D88\u606F\u961F\u5217\u5E26\u6765\u7684\u4E00\u4E9B\u526F\u4F5C\u7528\u7684\u65F6\u5019\u63D0\u5230\u7684\uFF0C\u4E5F\u5C31\u662F\u8BF4\uFF0C\u8FD9\u4E9B\u95EE\u9898\u4E0D\u4EC5\u4EC5\u6302\u94A9\u4E8E <code>RocketMQ</code> \uFF0C\u800C\u662F\u5E94\u8BE5\u6BCF\u4E2A\u6D88\u606F\u4E2D\u95F4\u4EF6\u90FD\u9700\u8981\u53BB\u89E3\u51B3\u7684\u3002</p><p>\u5728\u4E0A\u9762\u6211\u4ECB\u7ECD <code>RocketMQ</code> \u7684\u6280\u672F\u67B6\u6784\u7684\u65F6\u5019\u6211\u5DF2\u7ECF\u5411\u4F60\u5C55\u793A\u4E86 <strong>\u5B83\u662F\u5982\u4F55\u4FDD\u8BC1\u9AD8\u53EF\u7528\u7684</strong> \uFF0C\u8FD9\u91CC\u4E0D\u6D89\u53CA\u8FD0\u7EF4\u65B9\u9762\u7684\u642D\u5EFA\uFF0C\u5982\u679C\u4F60\u611F\u5174\u8DA3\u53EF\u4EE5\u81EA\u5DF1\u53BB\u5B98\u7F51\u4E0A\u7167\u7740\u4F8B\u5B50\u642D\u5EFA\u5C5E\u4E8E\u4F60\u81EA\u5DF1\u7684 <code>RocketMQ</code> \u96C6\u7FA4\u3002</p><blockquote><p>\u5176\u5B9E <code>Kafka</code> \u7684\u67B6\u6784\u57FA\u672C\u548C <code>RocketMQ</code> \u7C7B\u4F3C\uFF0C\u53EA\u662F\u5B83\u6CE8\u518C\u4E2D\u5FC3\u4F7F\u7528\u4E86 <code>Zookeeper</code> \u3001\u5B83\u7684 <strong>\u5206\u533A</strong> \u5C31\u76F8\u5F53\u4E8E <code>RocketMQ</code> \u4E2D\u7684 <strong>\u961F\u5217</strong> \u3002\u8FD8\u6709\u4E00\u4E9B\u5C0F\u7EC6\u8282\u4E0D\u540C\u4F1A\u5728\u540E\u9762\u63D0\u5230\u3002</p></blockquote><h3 id="\u987A\u5E8F\u6D88\u8D39" tabindex="-1"><a class="header-anchor" href="#\u987A\u5E8F\u6D88\u8D39" aria-hidden="true">#</a> \u987A\u5E8F\u6D88\u8D39</h3><p>\u5728\u4E0A\u9762\u7684\u6280\u672F\u67B6\u6784\u4ECB\u7ECD\u4E2D\uFF0C\u6211\u4EEC\u5DF2\u7ECF\u77E5\u9053\u4E86 <strong><code>RocketMQ</code> \u5728\u4E3B\u9898\u4E0A\u662F\u65E0\u5E8F\u7684\u3001\u5B83\u53EA\u6709\u5728\u961F\u5217\u5C42\u9762\u624D\u662F\u4FDD\u8BC1\u6709\u5E8F</strong> \u7684\u3002</p><p>\u8FD9\u53C8\u626F\u5230\u4E24\u4E2A\u6982\u5FF5\u2014\u2014<strong>\u666E\u901A\u987A\u5E8F</strong> \u548C <strong>\u4E25\u683C\u987A\u5E8F</strong> \u3002</p><p>\u6240\u8C13\u666E\u901A\u987A\u5E8F\u662F\u6307 \u6D88\u8D39\u8005\u901A\u8FC7 <strong>\u540C\u4E00\u4E2A\u6D88\u8D39\u961F\u5217\u6536\u5230\u7684\u6D88\u606F\u662F\u6709\u987A\u5E8F\u7684</strong> \uFF0C\u4E0D\u540C\u6D88\u606F\u961F\u5217\u6536\u5230\u7684\u6D88\u606F\u5219\u53EF\u80FD\u662F\u65E0\u987A\u5E8F\u7684\u3002\u666E\u901A\u987A\u5E8F\u6D88\u606F\u5728 <code>Broker</code> <strong>\u91CD\u542F\u60C5\u51B5\u4E0B\u4E0D\u4F1A\u4FDD\u8BC1\u6D88\u606F\u987A\u5E8F\u6027</strong> (\u77ED\u6682\u65F6\u95F4) \u3002</p><p>\u6240\u8C13\u4E25\u683C\u987A\u5E8F\u662F\u6307 \u6D88\u8D39\u8005\u6536\u5230\u7684 <strong>\u6240\u6709\u6D88\u606F</strong> \u5747\u662F\u6709\u987A\u5E8F\u7684\u3002\u4E25\u683C\u987A\u5E8F\u6D88\u606F <strong>\u5373\u4F7F\u5728\u5F02\u5E38\u60C5\u51B5\u4E0B\u4E5F\u4F1A\u4FDD\u8BC1\u6D88\u606F\u7684\u987A\u5E8F\u6027</strong> \u3002</p><p>\u4F46\u662F\uFF0C\u4E25\u683C\u987A\u5E8F\u770B\u8D77\u6765\u867D\u597D\uFF0C\u5B9E\u73B0\u5B83\u53EF\u4F1A\u4ED8\u51FA\u5DE8\u5927\u7684\u4EE3\u4EF7\u3002\u5982\u679C\u4F60\u4F7F\u7528\u4E25\u683C\u987A\u5E8F\u6A21\u5F0F\uFF0C<code>Broker</code> \u96C6\u7FA4\u4E2D\u53EA\u8981\u6709\u4E00\u53F0\u673A\u5668\u4E0D\u53EF\u7528\uFF0C\u5219\u6574\u4E2A\u96C6\u7FA4\u90FD\u4E0D\u53EF\u7528\u3002\u4F60\u8FD8\u7528\u5565\uFF1F\u73B0\u5728\u4E3B\u8981\u573A\u666F\u4E5F\u5C31\u5728 <code>binlog</code> \u540C\u6B65\u3002</p><p>\u4E00\u822C\u800C\u8A00\uFF0C\u6211\u4EEC\u7684 <code>MQ</code> \u90FD\u662F\u80FD\u5BB9\u5FCD\u77ED\u6682\u7684\u4E71\u5E8F\uFF0C\u6240\u4EE5\u63A8\u8350\u4F7F\u7528\u666E\u901A\u987A\u5E8F\u6A21\u5F0F\u3002</p><p>\u90A3\u4E48\uFF0C\u6211\u4EEC\u73B0\u5728\u4F7F\u7528\u4E86 <strong>\u666E\u901A\u987A\u5E8F\u6A21\u5F0F</strong> \uFF0C\u6211\u4EEC\u4ECE\u4E0A\u9762\u5B66\u4E60\u77E5\u9053\u4E86\u5728 <code>Producer</code> \u751F\u4EA7\u6D88\u606F\u7684\u65F6\u5019\u4F1A\u8FDB\u884C\u8F6E\u8BE2(\u53D6\u51B3\u4F60\u7684\u8D1F\u8F7D\u5747\u8861\u7B56\u7565)\u6765\u5411\u540C\u4E00\u4E3B\u9898\u7684\u4E0D\u540C\u6D88\u606F\u961F\u5217\u53D1\u9001\u6D88\u606F\u3002\u90A3\u4E48\u5982\u679C\u6B64\u65F6\u6211\u6709\u51E0\u4E2A\u6D88\u606F\u5206\u522B\u662F\u540C\u4E00\u4E2A\u8BA2\u5355\u7684\u521B\u5EFA\u3001\u652F\u4ED8\u3001\u53D1\u8D27\uFF0C\u5728\u8F6E\u8BE2\u7684\u7B56\u7565\u4E0B\u8FD9 <strong>\u4E09\u4E2A\u6D88\u606F\u4F1A\u88AB\u53D1\u9001\u5230\u4E0D\u540C\u961F\u5217</strong> \uFF0C\u56E0\u4E3A\u5728\u4E0D\u540C\u7684\u961F\u5217\u6B64\u65F6\u5C31\u65E0\u6CD5\u4F7F\u7528 <code>RocketMQ</code> \u5E26\u6765\u7684\u961F\u5217\u6709\u5E8F\u7279\u6027\u6765\u4FDD\u8BC1\u6D88\u606F\u6709\u5E8F\u6027\u4E86\u3002</p><p><img src="https://my-blog-to-use.oss-cn-beijing.aliyuncs.com/2019-11/16ef3874585e096e.jpg" alt="" loading="lazy"></p><p>\u90A3\u4E48\uFF0C\u600E\u4E48\u89E3\u51B3\u5462\uFF1F</p><p>\u5176\u5B9E\u5F88\u7B80\u5355\uFF0C\u6211\u4EEC\u9700\u8981\u5904\u7406\u7684\u4EC5\u4EC5\u662F\u5C06\u540C\u4E00\u8BED\u4E49\u4E0B\u7684\u6D88\u606F\u653E\u5165\u540C\u4E00\u4E2A\u961F\u5217(\u6BD4\u5982\u8FD9\u91CC\u662F\u540C\u4E00\u4E2A\u8BA2\u5355)\uFF0C\u90A3\u6211\u4EEC\u5C31\u53EF\u4EE5\u4F7F\u7528 <strong>Hash\u53D6\u6A21\u6CD5</strong> \u6765\u4FDD\u8BC1\u540C\u4E00\u4E2A\u8BA2\u5355\u5728\u540C\u4E00\u4E2A\u961F\u5217\u4E2D\u5C31\u884C\u4E86\u3002</p><h3 id="\u91CD\u590D\u6D88\u8D39" tabindex="-1"><a class="header-anchor" href="#\u91CD\u590D\u6D88\u8D39" aria-hidden="true">#</a> \u91CD\u590D\u6D88\u8D39</h3><p>emmm\uFF0C\u5C31\u4E24\u4E2A\u5B57\u2014\u2014 <strong>\u5E42\u7B49</strong> \u3002\u5728\u7F16\u7A0B\u4E2D\u4E00\u4E2A<em>\u5E42\u7B49</em> \u64CD\u4F5C\u7684\u7279\u70B9\u662F\u5176\u4EFB\u610F\u591A\u6B21\u6267\u884C\u6240\u4EA7\u751F\u7684\u5F71\u54CD\u5747\u4E0E\u4E00\u6B21\u6267\u884C\u7684\u5F71\u54CD\u76F8\u540C\u3002\u6BD4\u5982\u8BF4\uFF0C\u8FD9\u4E2A\u65F6\u5019\u6211\u4EEC\u6709\u4E00\u4E2A\u8BA2\u5355\u7684\u5904\u7406\u79EF\u5206\u7684\u7CFB\u7EDF\uFF0C\u6BCF\u5F53\u6765\u4E00\u4E2A\u6D88\u606F\u7684\u65F6\u5019\u5B83\u5C31\u8D1F\u8D23\u4E3A\u521B\u5EFA\u8FD9\u4E2A\u8BA2\u5355\u7684\u7528\u6237\u7684\u79EF\u5206\u52A0\u4E0A\u76F8\u5E94\u7684\u6570\u503C\u3002\u53EF\u662F\u6709\u4E00\u6B21\uFF0C\u6D88\u606F\u961F\u5217\u53D1\u9001\u7ED9\u8BA2\u5355\u7CFB\u7EDF FrancisQ \u7684\u8BA2\u5355\u4FE1\u606F\uFF0C\u5176\u8981\u6C42\u662F\u7ED9 FrancisQ \u7684\u79EF\u5206\u52A0\u4E0A 500\u3002\u4F46\u662F\u79EF\u5206\u7CFB\u7EDF\u5728\u6536\u5230 FrancisQ \u7684\u8BA2\u5355\u4FE1\u606F\u5904\u7406\u5B8C\u6210\u4E4B\u540E\u8FD4\u56DE\u7ED9\u6D88\u606F\u961F\u5217\u5904\u7406\u6210\u529F\u7684\u4FE1\u606F\u7684\u65F6\u5019\u51FA\u73B0\u4E86\u7F51\u7EDC\u6CE2\u52A8(\u5F53\u7136\u8FD8\u6709\u5F88\u591A\u79CD\u60C5\u51B5\uFF0C\u6BD4\u5982Broker\u610F\u5916\u91CD\u542F\u7B49\u7B49)\uFF0C\u8FD9\u6761\u56DE\u5E94\u6CA1\u6709\u53D1\u9001\u6210\u529F\u3002</p><p>\u90A3\u4E48\uFF0C\u6D88\u606F\u961F\u5217\u6CA1\u6536\u5230\u79EF\u5206\u7CFB\u7EDF\u7684\u56DE\u5E94\u4F1A\u4E0D\u4F1A\u5C1D\u8BD5\u91CD\u53D1\u8FD9\u4E2A\u6D88\u606F\uFF1F\u95EE\u9898\u5C31\u6765\u4E86\uFF0C\u6211\u518D\u53D1\u8FD9\u4E2A\u6D88\u606F\uFF0C\u4E07\u4E00\u5B83\u53C8\u7ED9 FrancisQ \u7684\u8D26\u6237\u52A0\u4E0A 500 \u79EF\u5206\u600E\u4E48\u529E\u5462\uFF1F</p><p>\u6240\u4EE5\u6211\u4EEC\u9700\u8981\u7ED9\u6211\u4EEC\u7684\u6D88\u8D39\u8005\u5B9E\u73B0 <strong>\u5E42\u7B49</strong> \uFF0C\u4E5F\u5C31\u662F\u5BF9\u540C\u4E00\u4E2A\u6D88\u606F\u7684\u5904\u7406\u7ED3\u679C\uFF0C\u6267\u884C\u591A\u5C11\u6B21\u90FD\u4E0D\u53D8\u3002</p><p>\u90A3\u4E48\u5982\u4F55\u7ED9\u4E1A\u52A1\u5B9E\u73B0\u5E42\u7B49\u5462\uFF1F\u8FD9\u4E2A\u8FD8\u662F\u9700\u8981\u7ED3\u5408\u5177\u4F53\u7684\u4E1A\u52A1\u7684\u3002\u4F60\u53EF\u4EE5\u4F7F\u7528 <strong>\u5199\u5165 <code>Redis</code></strong> \u6765\u4FDD\u8BC1\uFF0C\u56E0\u4E3A <code>Redis</code> \u7684 <code>key</code> \u548C <code>value</code> \u5C31\u662F\u5929\u7136\u652F\u6301\u5E42\u7B49\u7684\u3002\u5F53\u7136\u8FD8\u6709\u4F7F\u7528 <strong>\u6570\u636E\u5E93\u63D2\u5165\u6CD5</strong> \uFF0C\u57FA\u4E8E\u6570\u636E\u5E93\u7684\u552F\u4E00\u952E\u6765\u4FDD\u8BC1\u91CD\u590D\u6570\u636E\u4E0D\u4F1A\u88AB\u63D2\u5165\u591A\u6761\u3002</p><p>\u4E0D\u8FC7\u6700\u4E3B\u8981\u7684\u8FD8\u662F\u9700\u8981 <strong>\u6839\u636E\u7279\u5B9A\u573A\u666F\u4F7F\u7528\u7279\u5B9A\u7684\u89E3\u51B3\u65B9\u6848</strong> \uFF0C\u4F60\u8981\u77E5\u9053\u4F60\u7684\u6D88\u606F\u6D88\u8D39\u662F\u5426\u662F\u5B8C\u5168\u4E0D\u53EF\u91CD\u590D\u6D88\u8D39\u8FD8\u662F\u53EF\u4EE5\u5FCD\u53D7\u91CD\u590D\u6D88\u8D39\u7684\uFF0C\u7136\u540E\u518D\u9009\u62E9\u5F3A\u6821\u9A8C\u548C\u5F31\u6821\u9A8C\u7684\u65B9\u5F0F\u3002\u6BD5\u7ADF\u5728 CS \u9886\u57DF\u8FD8\u662F\u5F88\u5C11\u6709\u6280\u672F\u94F6\u5F39\u7684\u8BF4\u6CD5\u3002</p><p>\u800C\u5728\u6574\u4E2A\u4E92\u8054\u7F51\u9886\u57DF\uFF0C\u5E42\u7B49\u4E0D\u4EC5\u4EC5\u9002\u7528\u4E8E\u6D88\u606F\u961F\u5217\u7684\u91CD\u590D\u6D88\u8D39\u95EE\u9898\uFF0C\u8FD9\u4E9B\u5B9E\u73B0\u5E42\u7B49\u7684\u65B9\u6CD5\uFF0C\u4E5F\u540C\u6837\u9002\u7528\u4E8E\uFF0C<strong>\u5728\u5176\u4ED6\u573A\u666F\u4E2D\u6765\u89E3\u51B3\u91CD\u590D\u8BF7\u6C42\u6216\u8005\u91CD\u590D\u8C03\u7528\u7684\u95EE\u9898</strong> \u3002\u6BD4\u5982\u5C06HTTP\u670D\u52A1\u8BBE\u8BA1\u6210\u5E42\u7B49\u7684\uFF0C<strong>\u89E3\u51B3\u524D\u7AEF\u6216\u8005APP\u91CD\u590D\u63D0\u4EA4\u8868\u5355\u6570\u636E\u7684\u95EE\u9898</strong> \uFF0C\u4E5F\u53EF\u4EE5\u5C06\u4E00\u4E2A\u5FAE\u670D\u52A1\u8BBE\u8BA1\u6210\u5E42\u7B49\u7684\uFF0C\u89E3\u51B3 <code>RPC</code> \u6846\u67B6\u81EA\u52A8\u91CD\u8BD5\u5BFC\u81F4\u7684 <strong>\u91CD\u590D\u8C03\u7528\u95EE\u9898</strong> \u3002</p><h2 id="\u5206\u5E03\u5F0F\u4E8B\u52A1" tabindex="-1"><a class="header-anchor" href="#\u5206\u5E03\u5F0F\u4E8B\u52A1" aria-hidden="true">#</a> \u5206\u5E03\u5F0F\u4E8B\u52A1</h2><p>\u5982\u4F55\u89E3\u91CA\u5206\u5E03\u5F0F\u4E8B\u52A1\u5462\uFF1F\u4E8B\u52A1\u5927\u5BB6\u90FD\u77E5\u9053\u5427\uFF1F<strong>\u8981\u4E48\u90FD\u6267\u884C\u8981\u4E48\u90FD\u4E0D\u6267\u884C</strong> \u3002\u5728\u540C\u4E00\u4E2A\u7CFB\u7EDF\u4E2D\u6211\u4EEC\u53EF\u4EE5\u8F7B\u677E\u5730\u5B9E\u73B0\u4E8B\u52A1\uFF0C\u4F46\u662F\u5728\u5206\u5E03\u5F0F\u67B6\u6784\u4E2D\uFF0C\u6211\u4EEC\u6709\u5F88\u591A\u670D\u52A1\u662F\u90E8\u7F72\u5728\u4E0D\u540C\u7CFB\u7EDF\u4E4B\u95F4\u7684\uFF0C\u800C\u4E0D\u540C\u670D\u52A1\u4E4B\u95F4\u53C8\u9700\u8981\u8FDB\u884C\u8C03\u7528\u3002\u6BD4\u5982\u6B64\u65F6\u6211\u4E0B\u8BA2\u5355\u7136\u540E\u589E\u52A0\u79EF\u5206\uFF0C\u5982\u679C\u4FDD\u8BC1\u4E0D\u4E86\u5206\u5E03\u5F0F\u4E8B\u52A1\u7684\u8BDD\uFF0C\u5C31\u4F1A\u51FA\u73B0A\u7CFB\u7EDF\u4E0B\u4E86\u8BA2\u5355\uFF0C\u4F46\u662FB\u7CFB\u7EDF\u589E\u52A0\u79EF\u5206\u5931\u8D25\u6216\u8005A\u7CFB\u7EDF\u6CA1\u6709\u4E0B\u8BA2\u5355\uFF0CB\u7CFB\u7EDF\u5374\u589E\u52A0\u4E86\u79EF\u5206\u3002\u524D\u8005\u5BF9\u7528\u6237\u4E0D\u53CB\u597D\uFF0C\u540E\u8005\u5BF9\u8FD0\u8425\u5546\u4E0D\u5229\uFF0C\u8FD9\u662F\u6211\u4EEC\u90FD\u4E0D\u613F\u610F\u89C1\u5230\u7684\u3002</p><p>\u90A3\u4E48\uFF0C\u5982\u4F55\u53BB\u89E3\u51B3\u8FD9\u4E2A\u95EE\u9898\u5462\uFF1F</p><p>\u5982\u4ECA\u6BD4\u8F83\u5E38\u89C1\u7684\u5206\u5E03\u5F0F\u4E8B\u52A1\u5B9E\u73B0\u6709 2PC\u3001TCC \u548C\u4E8B\u52A1\u6D88\u606F(half \u534A\u6D88\u606F\u673A\u5236)\u3002\u6BCF\u4E00\u79CD\u5B9E\u73B0\u90FD\u6709\u5176\u7279\u5B9A\u7684\u4F7F\u7528\u573A\u666F\uFF0C\u4F46\u662F\u4E5F\u6709\u5404\u81EA\u7684\u95EE\u9898\uFF0C<strong>\u90FD\u4E0D\u662F\u5B8C\u7F8E\u7684\u89E3\u51B3\u65B9\u6848</strong>\u3002</p><p>\u5728 <code>RocketMQ</code> \u4E2D\u4F7F\u7528\u7684\u662F <strong>\u4E8B\u52A1\u6D88\u606F\u52A0\u4E0A\u4E8B\u52A1\u53CD\u67E5\u673A\u5236</strong> \u6765\u89E3\u51B3\u5206\u5E03\u5F0F\u4E8B\u52A1\u95EE\u9898\u7684\u3002\u6211\u753B\u4E86\u5F20\u56FE\uFF0C\u5927\u5BB6\u53EF\u4EE5\u5BF9\u7167\u7740\u56FE\u8FDB\u884C\u7406\u89E3\u3002</p><p><img src="https://my-blog-to-use.oss-cn-beijing.aliyuncs.com/2019-11/16ef38798d7a987f.png" alt="" loading="lazy"></p><p>\u5728\u7B2C\u4E00\u6B65\u53D1\u9001\u7684 half \u6D88\u606F \uFF0C\u5B83\u7684\u610F\u601D\u662F <strong>\u5728\u4E8B\u52A1\u63D0\u4EA4\u4E4B\u524D\uFF0C\u5BF9\u4E8E\u6D88\u8D39\u8005\u6765\u8BF4\uFF0C\u8FD9\u4E2A\u6D88\u606F\u662F\u4E0D\u53EF\u89C1\u7684</strong> \u3002</p><blockquote><p>\u90A3\u4E48\uFF0C\u5982\u4F55\u505A\u5230\u5199\u5165\u6D88\u606F\u4F46\u662F\u5BF9\u7528\u6237\u4E0D\u53EF\u89C1\u5462\uFF1FRocketMQ\u4E8B\u52A1\u6D88\u606F\u7684\u505A\u6CD5\u662F\uFF1A\u5982\u679C\u6D88\u606F\u662Fhalf\u6D88\u606F\uFF0C\u5C06\u5907\u4EFD\u539F\u6D88\u606F\u7684\u4E3B\u9898\u4E0E\u6D88\u606F\u6D88\u8D39\u961F\u5217\uFF0C\u7136\u540E <strong>\u6539\u53D8\u4E3B\u9898</strong> \u4E3ARMQ_SYS_TRANS_HALF_TOPIC\u3002\u7531\u4E8E\u6D88\u8D39\u7EC4\u672A\u8BA2\u9605\u8BE5\u4E3B\u9898\uFF0C\u6545\u6D88\u8D39\u7AEF\u65E0\u6CD5\u6D88\u8D39half\u7C7B\u578B\u7684\u6D88\u606F\uFF0C<strong>\u7136\u540ERocketMQ\u4F1A\u5F00\u542F\u4E00\u4E2A\u5B9A\u65F6\u4EFB\u52A1\uFF0C\u4ECETopic\u4E3ARMQ_SYS_TRANS_HALF_TOPIC\u4E2D\u62C9\u53D6\u6D88\u606F\u8FDB\u884C\u6D88\u8D39</strong>\uFF0C\u6839\u636E\u751F\u4EA7\u8005\u7EC4\u83B7\u53D6\u4E00\u4E2A\u670D\u52A1\u63D0\u4F9B\u8005\u53D1\u9001\u56DE\u67E5\u4E8B\u52A1\u72B6\u6001\u8BF7\u6C42\uFF0C\u6839\u636E\u4E8B\u52A1\u72B6\u6001\u6765\u51B3\u5B9A\u662F\u63D0\u4EA4\u6216\u56DE\u6EDA\u6D88\u606F\u3002</p></blockquote><p>\u4F60\u53EF\u4EE5\u8BD5\u60F3\u4E00\u4E0B\uFF0C\u5982\u679C\u6CA1\u6709\u4ECE\u7B2C5\u6B65\u5F00\u59CB\u7684 <strong>\u4E8B\u52A1\u53CD\u67E5\u673A\u5236</strong> \uFF0C\u5982\u679C\u51FA\u73B0\u7F51\u8DEF\u6CE2\u52A8\u7B2C4\u6B65\u6CA1\u6709\u53D1\u9001\u6210\u529F\uFF0C\u8FD9\u6837\u5C31\u4F1A\u4EA7\u751F MQ \u4E0D\u77E5\u9053\u662F\u4E0D\u662F\u9700\u8981\u7ED9\u6D88\u8D39\u8005\u6D88\u8D39\u7684\u95EE\u9898\uFF0C\u4ED6\u5C31\u50CF\u4E00\u4E2A\u65E0\u5934\u82CD\u8747\u4E00\u6837\u3002\u5728 <code>RocketMQ</code> \u4E2D\u5C31\u662F\u4F7F\u7528\u7684\u4E0A\u8FF0\u7684\u4E8B\u52A1\u53CD\u67E5\u6765\u89E3\u51B3\u7684\uFF0C\u800C\u5728 <code>Kafka</code> \u4E2D\u901A\u5E38\u662F\u76F4\u63A5\u629B\u51FA\u4E00\u4E2A\u5F02\u5E38\u8BA9\u7528\u6237\u6765\u81EA\u884C\u89E3\u51B3\u3002</p><p>\u4F60\u8FD8\u9700\u8981\u6CE8\u610F\u7684\u662F\uFF0C\u5728 <code>MQ Server</code> \u6307\u5411\u7CFB\u7EDFB\u7684\u64CD\u4F5C\u5DF2\u7ECF\u548C\u7CFB\u7EDFA\u4E0D\u76F8\u5173\u4E86\uFF0C\u4E5F\u5C31\u662F\u8BF4\u5728\u6D88\u606F\u961F\u5217\u4E2D\u7684\u5206\u5E03\u5F0F\u4E8B\u52A1\u662F\u2014\u2014<strong>\u672C\u5730\u4E8B\u52A1\u548C\u5B58\u50A8\u6D88\u606F\u5230\u6D88\u606F\u961F\u5217\u624D\u662F\u540C\u4E00\u4E2A\u4E8B\u52A1</strong>\u3002\u8FD9\u6837\u4E5F\u5C31\u4EA7\u751F\u4E86\u4E8B\u52A1\u7684<strong>\u6700\u7EC8\u4E00\u81F4\u6027</strong>\uFF0C\u56E0\u4E3A\u6574\u4E2A\u8FC7\u7A0B\u662F\u5F02\u6B65\u7684\uFF0C<strong>\u6BCF\u4E2A\u7CFB\u7EDF\u53EA\u8981\u4FDD\u8BC1\u5B83\u81EA\u5DF1\u90A3\u4E00\u90E8\u5206\u7684\u4E8B\u52A1\u5C31\u884C\u4E86</strong>\u3002</p><h2 id="\u6D88\u606F\u5806\u79EF\u95EE\u9898" tabindex="-1"><a class="header-anchor" href="#\u6D88\u606F\u5806\u79EF\u95EE\u9898" aria-hidden="true">#</a> \u6D88\u606F\u5806\u79EF\u95EE\u9898</h2><p>\u5728\u4E0A\u9762\u6211\u4EEC\u63D0\u5230\u4E86\u6D88\u606F\u961F\u5217\u4E00\u4E2A\u5F88\u91CD\u8981\u7684\u529F\u80FD\u2014\u2014<strong>\u524A\u5CF0</strong> \u3002\u90A3\u4E48\u5982\u679C\u8FD9\u4E2A\u5CF0\u503C\u592A\u5927\u4E86\u5BFC\u81F4\u6D88\u606F\u5806\u79EF\u5728\u961F\u5217\u4E2D\u600E\u4E48\u529E\u5462\uFF1F</p><p>\u5176\u5B9E\u8FD9\u4E2A\u95EE\u9898\u53EF\u4EE5\u5C06\u5B83\u5E7F\u4E49\u5316\uFF0C\u56E0\u4E3A\u4EA7\u751F\u6D88\u606F\u5806\u79EF\u7684\u6839\u6E90\u5176\u5B9E\u5C31\u53EA\u6709\u4E24\u4E2A\u2014\u2014\u751F\u4EA7\u8005\u751F\u4EA7\u592A\u5FEB\u6216\u8005\u6D88\u8D39\u8005\u6D88\u8D39\u592A\u6162\u3002</p><p>\u6211\u4EEC\u53EF\u4EE5\u4ECE\u591A\u4E2A\u89D2\u5EA6\u53BB\u601D\u8003\u89E3\u51B3\u8FD9\u4E2A\u95EE\u9898\uFF0C\u5F53\u6D41\u91CF\u5230\u5CF0\u503C\u7684\u65F6\u5019\u662F\u56E0\u4E3A\u751F\u4EA7\u8005\u751F\u4EA7\u592A\u5FEB\uFF0C\u6211\u4EEC\u53EF\u4EE5\u4F7F\u7528\u4E00\u4E9B <strong>\u9650\u6D41\u964D\u7EA7</strong> \u7684\u65B9\u6CD5\uFF0C\u5F53\u7136\u4F60\u4E5F\u53EF\u4EE5\u589E\u52A0\u591A\u4E2A\u6D88\u8D39\u8005\u5B9E\u4F8B\u53BB\u6C34\u5E73\u6269\u5C55\u589E\u52A0\u6D88\u8D39\u80FD\u529B\u6765\u5339\u914D\u751F\u4EA7\u7684\u6FC0\u589E\u3002\u5982\u679C\u6D88\u8D39\u8005\u6D88\u8D39\u8FC7\u6162\u7684\u8BDD\uFF0C\u6211\u4EEC\u53EF\u4EE5\u5148\u68C0\u67E5 <strong>\u662F\u5426\u662F\u6D88\u8D39\u8005\u51FA\u73B0\u4E86\u5927\u91CF\u7684\u6D88\u8D39\u9519\u8BEF</strong> \uFF0C\u6216\u8005\u6253\u5370\u4E00\u4E0B\u65E5\u5FD7\u67E5\u770B\u662F\u5426\u662F\u54EA\u4E00\u4E2A\u7EBF\u7A0B\u5361\u6B7B\uFF0C\u51FA\u73B0\u4E86\u9501\u8D44\u6E90\u4E0D\u91CA\u653E\u7B49\u7B49\u7684\u95EE\u9898\u3002</p><blockquote><p>\u5F53\u7136\uFF0C\u6700\u5FEB\u901F\u89E3\u51B3\u6D88\u606F\u5806\u79EF\u95EE\u9898\u7684\u65B9\u6CD5\u8FD8\u662F\u589E\u52A0\u6D88\u8D39\u8005\u5B9E\u4F8B\uFF0C\u4E0D\u8FC7 <strong>\u540C\u65F6\u4F60\u8FD8\u9700\u8981\u589E\u52A0\u6BCF\u4E2A\u4E3B\u9898\u7684\u961F\u5217\u6570\u91CF</strong> \u3002</p><p>\u522B\u5FD8\u4E86\u5728 <code>RocketMQ</code> \u4E2D\uFF0C<strong>\u4E00\u4E2A\u961F\u5217\u53EA\u4F1A\u88AB\u4E00\u4E2A\u6D88\u8D39\u8005\u6D88\u8D39</strong> \uFF0C\u5982\u679C\u4F60\u4EC5\u4EC5\u662F\u589E\u52A0\u6D88\u8D39\u8005\u5B9E\u4F8B\u5C31\u4F1A\u51FA\u73B0\u6211\u4E00\u5F00\u59CB\u7ED9\u4F60\u753B\u67B6\u6784\u56FE\u7684\u90A3\u79CD\u60C5\u51B5\u3002</p></blockquote><p><img src="https://my-blog-to-use.oss-cn-beijing.aliyuncs.com/2019-11/16ef387d939ab66d.jpg" alt="" loading="lazy"></p><h2 id="\u56DE\u6EAF\u6D88\u8D39" tabindex="-1"><a class="header-anchor" href="#\u56DE\u6EAF\u6D88\u8D39" aria-hidden="true">#</a> \u56DE\u6EAF\u6D88\u8D39</h2><p>\u56DE\u6EAF\u6D88\u8D39\u662F\u6307 <code>Consumer</code> \u5DF2\u7ECF\u6D88\u8D39\u6210\u529F\u7684\u6D88\u606F\uFF0C\u7531\u4E8E\u4E1A\u52A1\u4E0A\u9700\u6C42\u9700\u8981\u91CD\u65B0\u6D88\u8D39\uFF0C\u5728<code>RocketMQ</code> \u4E2D\uFF0C <code>Broker</code> \u5728\u5411<code>Consumer</code> \u6295\u9012\u6210\u529F\u6D88\u606F\u540E\uFF0C<strong>\u6D88\u606F\u4ECD\u7136\u9700\u8981\u4FDD\u7559</strong> \u3002\u5E76\u4E14\u91CD\u65B0\u6D88\u8D39\u4E00\u822C\u662F\u6309\u7167\u65F6\u95F4\u7EF4\u5EA6\uFF0C\u4F8B\u5982\u7531\u4E8E <code>Consumer</code> \u7CFB\u7EDF\u6545\u969C\uFF0C\u6062\u590D\u540E\u9700\u8981\u91CD\u65B0\u6D88\u8D391\u5C0F\u65F6\u524D\u7684\u6570\u636E\uFF0C\u90A3\u4E48 <code>Broker</code> \u8981\u63D0\u4F9B\u4E00\u79CD\u673A\u5236\uFF0C\u53EF\u4EE5\u6309\u7167\u65F6\u95F4\u7EF4\u5EA6\u6765\u56DE\u9000\u6D88\u8D39\u8FDB\u5EA6\u3002<code>RocketMQ</code> \u652F\u6301\u6309\u7167\u65F6\u95F4\u56DE\u6EAF\u6D88\u8D39\uFF0C\u65F6\u95F4\u7EF4\u5EA6\u7CBE\u786E\u5230\u6BEB\u79D2\u3002</p><p>\u8FD9\u662F\u5B98\u65B9\u6587\u6863\u7684\u89E3\u91CA\uFF0C\u6211\u76F4\u63A5\u7167\u642C\u8FC7\u6765\u5C31\u5F53\u79D1\u666E\u4E86\u{1F601}\u{1F601}\u{1F601}\u3002</p><h2 id="rocketmq-\u7684\u5237\u76D8\u673A\u5236" tabindex="-1"><a class="header-anchor" href="#rocketmq-\u7684\u5237\u76D8\u673A\u5236" aria-hidden="true">#</a> RocketMQ \u7684\u5237\u76D8\u673A\u5236</h2><p>\u4E0A\u9762\u6211\u8BB2\u4E86\u90A3\u4E48\u591A\u7684 <code>RocketMQ</code> \u7684\u67B6\u6784\u548C\u8BBE\u8BA1\u539F\u7406\uFF0C\u4F60\u6709\u6CA1\u6709\u597D\u5947</p><p>\u5728 <code>Topic</code> \u4E2D\u7684 <strong>\u961F\u5217\u662F\u4EE5\u4EC0\u4E48\u6837\u7684\u5F62\u5F0F\u5B58\u5728\u7684\uFF1F</strong></p><p><strong>\u961F\u5217\u4E2D\u7684\u6D88\u606F\u53C8\u662F\u5982\u4F55\u8FDB\u884C\u5B58\u50A8\u6301\u4E45\u5316\u7684\u5462\uFF1F</strong></p><p>\u6211\u5728\u4E0A\u6587\u4E2D\u63D0\u5230\u7684 <strong>\u540C\u6B65\u5237\u76D8</strong> \u548C <strong>\u5F02\u6B65\u5237\u76D8</strong> \u53C8\u662F\u4EC0\u4E48\u5462\uFF1F\u5B83\u4EEC\u4F1A\u7ED9\u6301\u4E45\u5316\u5E26\u6765\u4EC0\u4E48\u6837\u7684\u5F71\u54CD\u5462\uFF1F</p><p>\u4E0B\u9762\u6211\u5C06\u7ED9\u4F60\u4EEC\u4E00\u4E00\u89E3\u91CA\u3002</p><h3 id="\u540C\u6B65\u5237\u76D8\u548C\u5F02\u6B65\u5237\u76D8" tabindex="-1"><a class="header-anchor" href="#\u540C\u6B65\u5237\u76D8\u548C\u5F02\u6B65\u5237\u76D8" aria-hidden="true">#</a> \u540C\u6B65\u5237\u76D8\u548C\u5F02\u6B65\u5237\u76D8</h3><p><img src="https://my-blog-to-use.oss-cn-beijing.aliyuncs.com/2019-11/16ef387fba311cda.jpg" alt="" loading="lazy"></p><p>\u5982\u4E0A\u56FE\u6240\u793A\uFF0C\u5728\u540C\u6B65\u5237\u76D8\u4E2D\u9700\u8981\u7B49\u5F85\u4E00\u4E2A\u5237\u76D8\u6210\u529F\u7684 <code>ACK</code> \uFF0C\u540C\u6B65\u5237\u76D8\u5BF9 <code>MQ</code> \u6D88\u606F\u53EF\u9760\u6027\u6765\u8BF4\u662F\u4E00\u79CD\u4E0D\u9519\u7684\u4FDD\u969C\uFF0C\u4F46\u662F <strong>\u6027\u80FD\u4E0A\u4F1A\u6709\u8F83\u5927\u5F71\u54CD</strong> \uFF0C\u4E00\u822C\u5730\u9002\u7528\u4E8E\u91D1\u878D\u7B49\u7279\u5B9A\u4E1A\u52A1\u573A\u666F\u3002</p><p>\u800C\u5F02\u6B65\u5237\u76D8\u5F80\u5F80\u662F\u5F00\u542F\u4E00\u4E2A\u7EBF\u7A0B\u53BB\u5F02\u6B65\u5730\u6267\u884C\u5237\u76D8\u64CD\u4F5C\u3002\u6D88\u606F\u5237\u76D8\u91C7\u7528\u540E\u53F0\u5F02\u6B65\u7EBF\u7A0B\u63D0\u4EA4\u7684\u65B9\u5F0F\u8FDB\u884C\uFF0C <strong>\u964D\u4F4E\u4E86\u8BFB\u5199\u5EF6\u8FDF</strong> \uFF0C\u63D0\u9AD8\u4E86 <code>MQ</code> \u7684\u6027\u80FD\u548C\u541E\u5410\u91CF\uFF0C\u4E00\u822C\u9002\u7528\u4E8E\u5982\u53D1\u9A8C\u8BC1\u7801\u7B49\u5BF9\u4E8E\u6D88\u606F\u4FDD\u8BC1\u8981\u6C42\u4E0D\u592A\u9AD8\u7684\u4E1A\u52A1\u573A\u666F\u3002</p><p>\u4E00\u822C\u5730\uFF0C<strong>\u5F02\u6B65\u5237\u76D8\u53EA\u6709\u5728 <code>Broker</code> \u610F\u5916\u5B95\u673A\u7684\u65F6\u5019\u4F1A\u4E22\u5931\u90E8\u5206\u6570\u636E</strong>\uFF0C\u4F60\u53EF\u4EE5\u8BBE\u7F6E <code>Broker</code> \u7684\u53C2\u6570 <code>FlushDiskType</code> \u6765\u8C03\u6574\u4F60\u7684\u5237\u76D8\u7B56\u7565(ASYNC_FLUSH \u6216\u8005 SYNC_FLUSH)\u3002</p><h3 id="\u540C\u6B65\u590D\u5236\u548C\u5F02\u6B65\u590D\u5236" tabindex="-1"><a class="header-anchor" href="#\u540C\u6B65\u590D\u5236\u548C\u5F02\u6B65\u590D\u5236" aria-hidden="true">#</a> \u540C\u6B65\u590D\u5236\u548C\u5F02\u6B65\u590D\u5236</h3><p>\u4E0A\u9762\u7684\u540C\u6B65\u5237\u76D8\u548C\u5F02\u6B65\u5237\u76D8\u662F\u5728\u5355\u4E2A\u7ED3\u70B9\u5C42\u9762\u7684\uFF0C\u800C\u540C\u6B65\u590D\u5236\u548C\u5F02\u6B65\u590D\u5236\u4E3B\u8981\u662F\u6307\u7684 <code>Borker</code> \u4E3B\u4ECE\u6A21\u5F0F\u4E0B\uFF0C\u4E3B\u8282\u70B9\u8FD4\u56DE\u6D88\u606F\u7ED9\u5BA2\u6237\u7AEF\u7684\u65F6\u5019\u662F\u5426\u9700\u8981\u540C\u6B65\u4ECE\u8282\u70B9\u3002</p><ul><li>\u540C\u6B65\u590D\u5236\uFF1A \u4E5F\u53EB \u201C\u540C\u6B65\u53CC\u5199\u201D\uFF0C\u4E5F\u5C31\u662F\u8BF4\uFF0C<strong>\u53EA\u6709\u6D88\u606F\u540C\u6B65\u53CC\u5199\u5230\u4E3B\u4ECE\u8282\u70B9\u4E0A\u65F6\u624D\u8FD4\u56DE\u5199\u5165\u6210\u529F</strong> \u3002</li><li>\u5F02\u6B65\u590D\u5236\uFF1A <strong>\u6D88\u606F\u5199\u5165\u4E3B\u8282\u70B9\u4E4B\u540E\u5C31\u76F4\u63A5\u8FD4\u56DE\u5199\u5165\u6210\u529F</strong> \u3002</li></ul><p>\u7136\u800C\uFF0C\u5F88\u591A\u4E8B\u60C5\u662F\u6CA1\u6709\u5B8C\u7F8E\u7684\u65B9\u6848\u7684\uFF0C\u5C31\u6BD4\u5982\u6211\u4EEC\u8FDB\u884C\u6D88\u606F\u5199\u5165\u7684\u8282\u70B9\u8D8A\u591A\u5C31\u66F4\u80FD\u4FDD\u8BC1\u6D88\u606F\u7684\u53EF\u9760\u6027\uFF0C\u4F46\u662F\u968F\u4E4B\u7684\u6027\u80FD\u4E5F\u4F1A\u4E0B\u964D\uFF0C\u6240\u4EE5\u9700\u8981\u7A0B\u5E8F\u5458\u6839\u636E\u7279\u5B9A\u4E1A\u52A1\u573A\u666F\u53BB\u9009\u62E9\u9002\u5E94\u7684\u4E3B\u4ECE\u590D\u5236\u65B9\u6848\u3002</p><p>\u90A3\u4E48\uFF0C<strong>\u5F02\u6B65\u590D\u5236\u4F1A\u4E0D\u4F1A\u4E5F\u50CF\u5F02\u6B65\u5237\u76D8\u90A3\u6837\u5F71\u54CD\u6D88\u606F\u7684\u53EF\u9760\u6027\u5462\uFF1F</strong></p><p>\u7B54\u6848\u662F\u4E0D\u4F1A\u7684\uFF0C\u56E0\u4E3A\u4E24\u8005\u5C31\u662F\u4E0D\u540C\u7684\u6982\u5FF5\uFF0C\u5BF9\u4E8E\u6D88\u606F\u53EF\u9760\u6027\u662F\u901A\u8FC7\u4E0D\u540C\u7684\u5237\u76D8\u7B56\u7565\u4FDD\u8BC1\u7684\uFF0C\u800C\u50CF\u5F02\u6B65\u540C\u6B65\u590D\u5236\u7B56\u7565\u4EC5\u4EC5\u662F\u5F71\u54CD\u5230\u4E86 <strong>\u53EF\u7528\u6027</strong> \u3002\u4E3A\u4EC0\u4E48\u5462\uFF1F\u5176\u4E3B\u8981\u539F\u56E0<strong>\u662F <code>RocketMQ</code> \u662F\u4E0D\u652F\u6301\u81EA\u52A8\u4E3B\u4ECE\u5207\u6362\u7684\uFF0C\u5F53\u4E3B\u8282\u70B9\u6302\u6389\u4E4B\u540E\uFF0C\u751F\u4EA7\u8005\u5C31\u4E0D\u80FD\u518D\u7ED9\u8FD9\u4E2A\u4E3B\u8282\u70B9\u751F\u4EA7\u6D88\u606F\u4E86</strong>\u3002</p><p>\u6BD4\u5982\u8FD9\u4E2A\u65F6\u5019\u91C7\u7528\u5F02\u6B65\u590D\u5236\u7684\u65B9\u5F0F\uFF0C\u5728\u4E3B\u8282\u70B9\u8FD8\u672A\u53D1\u9001\u5B8C\u9700\u8981\u540C\u6B65\u7684\u6D88\u606F\u7684\u65F6\u5019\u4E3B\u8282\u70B9\u6302\u6389\u4E86\uFF0C\u8FD9\u4E2A\u65F6\u5019\u4ECE\u8282\u70B9\u5C31\u5C11\u4E86\u4E00\u90E8\u5206\u6D88\u606F\u3002\u4F46\u662F\u6B64\u65F6\u751F\u4EA7\u8005\u65E0\u6CD5\u518D\u7ED9\u4E3B\u8282\u70B9\u751F\u4EA7\u6D88\u606F\u4E86\uFF0C<strong>\u6D88\u8D39\u8005\u53EF\u4EE5\u81EA\u52A8\u5207\u6362\u5230\u4ECE\u8282\u70B9\u8FDB\u884C\u6D88\u8D39</strong>(\u4EC5\u4EC5\u662F\u6D88\u8D39)\uFF0C\u6240\u4EE5\u5728\u4E3B\u8282\u70B9\u6302\u6389\u7684\u65F6\u95F4\u53EA\u4F1A\u4EA7\u751F\u4E3B\u4ECE\u7ED3\u70B9\u77ED\u6682\u7684\u6D88\u606F\u4E0D\u4E00\u81F4\u7684\u60C5\u51B5\uFF0C\u964D\u4F4E\u4E86\u53EF\u7528\u6027\uFF0C\u800C\u5F53\u4E3B\u8282\u70B9\u91CD\u542F\u4E4B\u540E\uFF0C\u4ECE\u8282\u70B9\u90A3\u90E8\u5206\u672A\u6765\u5F97\u53CA\u590D\u5236\u7684\u6D88\u606F\u8FD8\u4F1A\u7EE7\u7EED\u590D\u5236\u3002</p><p>\u5728\u5355\u4E3B\u4ECE\u67B6\u6784\u4E2D\uFF0C\u5982\u679C\u4E00\u4E2A\u4E3B\u8282\u70B9\u6302\u6389\u4E86\uFF0C\u90A3\u4E48\u4E5F\u5C31\u610F\u5473\u7740\u6574\u4E2A\u7CFB\u7EDF\u4E0D\u80FD\u518D\u751F\u4EA7\u4E86\u3002\u90A3\u4E48\u8FD9\u4E2A\u53EF\u7528\u6027\u7684\u95EE\u9898\u80FD\u5426\u89E3\u51B3\u5462\uFF1F<strong>\u4E00\u4E2A\u4E3B\u4ECE\u4E0D\u884C\u90A3\u5C31\u591A\u4E2A\u4E3B\u4ECE\u7684\u5457</strong>\uFF0C\u522B\u5FD8\u4E86\u5728\u6211\u4EEC\u6700\u521D\u7684\u67B6\u6784\u56FE\u4E2D\uFF0C\u6BCF\u4E2A <code>Topic</code> \u662F\u5206\u5E03\u5728\u4E0D\u540C <code>Broker</code> \u4E2D\u7684\u3002</p><p><img src="https://my-blog-to-use.oss-cn-beijing.aliyuncs.com/2019-11/16ef38687488a5a4.jpg" alt="" loading="lazy"></p><p>\u4F46\u662F\u8FD9\u79CD\u590D\u5236\u65B9\u5F0F\u540C\u6837\u4E5F\u4F1A\u5E26\u6765\u4E00\u4E2A\u95EE\u9898\uFF0C\u90A3\u5C31\u662F\u65E0\u6CD5\u4FDD\u8BC1 <strong>\u4E25\u683C\u987A\u5E8F</strong> \u3002\u5728\u4E0A\u6587\u4E2D\u6211\u4EEC\u63D0\u5230\u4E86\u5982\u4F55\u4FDD\u8BC1\u7684\u6D88\u606F\u987A\u5E8F\u6027\u662F\u901A\u8FC7\u5C06\u4E00\u4E2A\u8BED\u4E49\u7684\u6D88\u606F\u53D1\u9001\u5728\u540C\u4E00\u4E2A\u961F\u5217\u4E2D\uFF0C\u4F7F\u7528 <code>Topic</code> \u4E0B\u7684\u961F\u5217\u6765\u4FDD\u8BC1\u987A\u5E8F\u6027\u7684\u3002\u5982\u679C\u6B64\u65F6\u6211\u4EEC\u4E3B\u8282\u70B9A\u8D1F\u8D23\u7684\u662F\u8BA2\u5355A\u7684\u4E00\u7CFB\u5217\u8BED\u4E49\u6D88\u606F\uFF0C\u7136\u540E\u5B83\u6302\u4E86\uFF0C\u8FD9\u6837\u5176\u4ED6\u8282\u70B9\u662F\u65E0\u6CD5\u4EE3\u66FF\u4E3B\u8282\u70B9A\u7684\uFF0C\u5982\u679C\u6211\u4EEC\u4EFB\u610F\u8282\u70B9\u90FD\u53EF\u4EE5\u5B58\u5165\u4EFB\u4F55\u6D88\u606F\uFF0C\u90A3\u5C31\u6CA1\u6709\u987A\u5E8F\u6027\u53EF\u8A00\u4E86\u3002</p><p>\u800C\u5728 <code>RocketMQ</code> \u4E2D\u91C7\u7528\u4E86 <code>Dledger</code> \u89E3\u51B3\u8FD9\u4E2A\u95EE\u9898\u3002\u4ED6\u8981\u6C42\u5728\u5199\u5165\u6D88\u606F\u7684\u65F6\u5019\uFF0C\u8981\u6C42<strong>\u81F3\u5C11\u6D88\u606F\u590D\u5236\u5230\u534A\u6570\u4EE5\u4E0A\u7684\u8282\u70B9\u4E4B\u540E</strong>\uFF0C\u624D\u7ED9\u5BA2\u2F3E\u7AEF\u8FD4\u56DE\u5199\u2F0A\u6210\u529F\uFF0C\u5E76\u4E14\u5B83\u662F\u2F40\u6301\u901A\u8FC7\u9009\u4E3E\u6765\u52A8\u6001\u5207\u6362\u4E3B\u8282\u70B9\u7684\u3002\u8FD9\u91CC\u6211\u5C31\u4E0D\u5C55\u5F00\u8BF4\u660E\u4E86\uFF0C\u8BFB\u8005\u53EF\u4EE5\u81EA\u5DF1\u53BB\u4E86\u89E3\u3002</p><blockquote><p>\u4E5F\u4E0D\u662F\u8BF4 <code>Dledger</code> \u662F\u4E2A\u5B8C\u7F8E\u7684\u65B9\u6848\uFF0C\u81F3\u5C11\u5728 <code>Dledger</code> \u9009\u4E3E\u8FC7\u7A0B\u4E2D\u662F\u65E0\u6CD5\u63D0\u4F9B\u670D\u52A1\u7684\uFF0C\u800C\u4E14\u4ED6\u5FC5\u987B\u8981\u4F7F\u7528\u4E09\u4E2A\u8282\u70B9\u6216\u4EE5\u4E0A\uFF0C\u5982\u679C\u591A\u6570\u8282\u70B9\u540C\u65F6\u6302\u6389\u4ED6\u4E5F\u662F\u65E0\u6CD5\u4FDD\u8BC1\u53EF\u7528\u6027\u7684\uFF0C\u800C\u4E14\u8981\u6C42\u6D88\u606F\u590D\u5236\u534A\u6570\u4EE5\u4E0A\u8282\u70B9\u7684\u6548\u7387\u548C\u76F4\u63A5\u5F02\u6B65\u590D\u5236\u8FD8\u662F\u6709\u4E00\u5B9A\u7684\u5DEE\u8DDD\u7684\u3002</p></blockquote><h3 id="\u5B58\u50A8\u673A\u5236" tabindex="-1"><a class="header-anchor" href="#\u5B58\u50A8\u673A\u5236" aria-hidden="true">#</a> \u5B58\u50A8\u673A\u5236</h3><p>\u8FD8\u8BB0\u5F97\u4E0A\u9762\u6211\u4EEC\u4E00\u5F00\u59CB\u7684\u4E09\u4E2A\u95EE\u9898\u5417\uFF1F\u5230\u8FD9\u91CC\u7B2C\u4E09\u4E2A\u95EE\u9898\u5DF2\u7ECF\u89E3\u51B3\u4E86\u3002</p><p>\u4F46\u662F\uFF0C\u5728 <code>Topic</code> \u4E2D\u7684 <strong>\u961F\u5217\u662F\u4EE5\u4EC0\u4E48\u6837\u7684\u5F62\u5F0F\u5B58\u5728\u7684\uFF1F\u961F\u5217\u4E2D\u7684\u6D88\u606F\u53C8\u662F\u5982\u4F55\u8FDB\u884C\u5B58\u50A8\u6301\u4E45\u5316\u7684\u5462\uFF1F</strong> \u8FD8\u672A\u89E3\u51B3\uFF0C\u5176\u5B9E\u8FD9\u91CC\u6D89\u53CA\u5230\u4E86 <code>RocketMQ</code> \u662F\u5982\u4F55\u8BBE\u8BA1\u5B83\u7684\u5B58\u50A8\u7ED3\u6784\u4E86\u3002\u6211\u9996\u5148\u60F3\u5927\u5BB6\u4ECB\u7ECD <code>RocketMQ</code> \u6D88\u606F\u5B58\u50A8\u67B6\u6784\u4E2D\u7684\u4E09\u5927\u89D2\u8272\u2014\u2014<code>CommitLog</code> \u3001<code>ConsumeQueue</code> \u548C <code>IndexFile</code> \u3002</p><ul><li><code>CommitLog</code>\uFF1A <strong>\u6D88\u606F\u4E3B\u4F53\u4EE5\u53CA\u5143\u6570\u636E\u7684\u5B58\u50A8\u4E3B\u4F53</strong>\uFF0C\u5B58\u50A8 <code>Producer</code> \u7AEF\u5199\u5165\u7684\u6D88\u606F\u4E3B\u4F53\u5185\u5BB9,\u6D88\u606F\u5185\u5BB9\u4E0D\u662F\u5B9A\u957F\u7684\u3002\u5355\u4E2A\u6587\u4EF6\u5927\u5C0F\u9ED8\u8BA41G \uFF0C\u6587\u4EF6\u540D\u957F\u5EA6\u4E3A20\u4F4D\uFF0C\u5DE6\u8FB9\u8865\u96F6\uFF0C\u5269\u4F59\u4E3A\u8D77\u59CB\u504F\u79FB\u91CF\uFF0C\u6BD4\u598200000000000000000000\u4EE3\u8868\u4E86\u7B2C\u4E00\u4E2A\u6587\u4EF6\uFF0C\u8D77\u59CB\u504F\u79FB\u91CF\u4E3A0\uFF0C\u6587\u4EF6\u5927\u5C0F\u4E3A1G=1073741824\uFF1B\u5F53\u7B2C\u4E00\u4E2A\u6587\u4EF6\u5199\u6EE1\u4E86\uFF0C\u7B2C\u4E8C\u4E2A\u6587\u4EF6\u4E3A00000000001073741824\uFF0C\u8D77\u59CB\u504F\u79FB\u91CF\u4E3A1073741824\uFF0C\u4EE5\u6B64\u7C7B\u63A8\u3002\u6D88\u606F\u4E3B\u8981\u662F<strong>\u987A\u5E8F\u5199\u5165\u65E5\u5FD7\u6587\u4EF6</strong>\uFF0C\u5F53\u6587\u4EF6\u6EE1\u4E86\uFF0C\u5199\u5165\u4E0B\u4E00\u4E2A\u6587\u4EF6\u3002</li><li><code>ConsumeQueue</code>\uFF1A \u6D88\u606F\u6D88\u8D39\u961F\u5217\uFF0C<strong>\u5F15\u5165\u7684\u76EE\u7684\u4E3B\u8981\u662F\u63D0\u9AD8\u6D88\u606F\u6D88\u8D39\u7684\u6027\u80FD</strong>(\u6211\u4EEC\u518D\u524D\u9762\u4E5F\u8BB2\u4E86)\uFF0C\u7531\u4E8E<code>RocketMQ</code> \u662F\u57FA\u4E8E\u4E3B\u9898 <code>Topic</code> \u7684\u8BA2\u9605\u6A21\u5F0F\uFF0C\u6D88\u606F\u6D88\u8D39\u662F\u9488\u5BF9\u4E3B\u9898\u8FDB\u884C\u7684\uFF0C\u5982\u679C\u8981\u904D\u5386 <code>commitlog</code> \u6587\u4EF6\u4E2D\u6839\u636E <code>Topic</code> \u68C0\u7D22\u6D88\u606F\u662F\u975E\u5E38\u4F4E\u6548\u7684\u3002<code>Consumer</code> \u5373\u53EF\u6839\u636E <code>ConsumeQueue</code> \u6765\u67E5\u627E\u5F85\u6D88\u8D39\u7684\u6D88\u606F\u3002\u5176\u4E2D\uFF0C<code>ConsumeQueue</code>\uFF08\u903B\u8F91\u6D88\u8D39\u961F\u5217\uFF09<strong>\u4F5C\u4E3A\u6D88\u8D39\u6D88\u606F\u7684\u7D22\u5F15</strong>\uFF0C\u4FDD\u5B58\u4E86\u6307\u5B9A <code>Topic</code> \u4E0B\u7684\u961F\u5217\u6D88\u606F\u5728 <code>CommitLog</code> \u4E2D\u7684<strong>\u8D77\u59CB\u7269\u7406\u504F\u79FB\u91CF <code>offset</code> <strong>\uFF0C\u6D88\u606F\u5927\u5C0F <code>size</code> \u548C\u6D88\u606F <code>Tag</code> \u7684 <code>HashCode</code> \u503C\u3002</strong><code>consumequeue</code> \u6587\u4EF6\u53EF\u4EE5\u770B\u6210\u662F\u57FA\u4E8E <code>topic</code> \u7684 <code>commitlog</code> \u7D22\u5F15\u6587\u4EF6</strong>\uFF0C\u6545 <code>consumequeue</code> \u6587\u4EF6\u5939\u7684\u7EC4\u7EC7\u65B9\u5F0F\u5982\u4E0B\uFF1Atopic/queue/file\u4E09\u5C42\u7EC4\u7EC7\u7ED3\u6784\uFF0C\u5177\u4F53\u5B58\u50A8\u8DEF\u5F84\u4E3A\uFF1A$HOME/store/consumequeue/{topic}/{queueId}/{fileName}\u3002\u540C\u6837 <code>consumequeue</code> \u6587\u4EF6\u91C7\u53D6\u5B9A\u957F\u8BBE\u8BA1\uFF0C\u6BCF\u4E00\u4E2A\u6761\u76EE\u517120\u4E2A\u5B57\u8282\uFF0C\u5206\u522B\u4E3A8\u5B57\u8282\u7684 <code>commitlog</code> \u7269\u7406\u504F\u79FB\u91CF\u30014\u5B57\u8282\u7684\u6D88\u606F\u957F\u5EA6\u30018\u5B57\u8282tag <code>hashcode</code>\uFF0C\u5355\u4E2A\u6587\u4EF6\u753130W\u4E2A\u6761\u76EE\u7EC4\u6210\uFF0C\u53EF\u4EE5\u50CF\u6570\u7EC4\u4E00\u6837\u968F\u673A\u8BBF\u95EE\u6BCF\u4E00\u4E2A\u6761\u76EE\uFF0C\u6BCF\u4E2A <code>ConsumeQueue</code>\u6587\u4EF6\u5927\u5C0F\u7EA65.72M\uFF1B</li><li><code>IndexFile</code>\uFF1A <code>IndexFile</code>\uFF08\u7D22\u5F15\u6587\u4EF6\uFF09\u63D0\u4F9B\u4E86\u4E00\u79CD\u53EF\u4EE5\u901A\u8FC7key\u6216\u65F6\u95F4\u533A\u95F4\u6765\u67E5\u8BE2\u6D88\u606F\u7684\u65B9\u6CD5\u3002\u8FD9\u91CC\u53EA\u505A\u79D1\u666E\u4E0D\u505A\u8BE6\u7EC6\u4ECB\u7ECD\u3002</li></ul><p>\u603B\u7ED3\u6765\u8BF4\uFF0C\u6574\u4E2A\u6D88\u606F\u5B58\u50A8\u7684\u7ED3\u6784\uFF0C\u6700\u4E3B\u8981\u7684\u5C31\u662F <code>CommitLoq</code> \u548C <code>ConsumeQueue</code> \u3002\u800C <code>ConsumeQueue</code> \u4F60\u53EF\u4EE5\u5927\u6982\u7406\u89E3\u4E3A <code>Topic</code> \u4E2D\u7684\u961F\u5217\u3002</p><p><img src="https://my-blog-to-use.oss-cn-beijing.aliyuncs.com/2019-11/16ef3884c02acc72.png" alt="" loading="lazy"></p><p><code>RocketMQ</code> \u91C7\u7528\u7684\u662F <strong>\u6DF7\u5408\u578B\u7684\u5B58\u50A8\u7ED3\u6784</strong> \uFF0C\u5373\u4E3A <code>Broker</code> \u5355\u4E2A\u5B9E\u4F8B\u4E0B\u6240\u6709\u7684\u961F\u5217\u5171\u7528\u4E00\u4E2A\u65E5\u5FD7\u6570\u636E\u6587\u4EF6\u6765\u5B58\u50A8\u6D88\u606F\u3002\u6709\u610F\u601D\u7684\u662F\u5728\u540C\u6837\u9AD8\u5E76\u53D1\u7684 <code>Kafka</code> \u4E2D\u4F1A\u4E3A\u6BCF\u4E2A <code>Topic</code> \u5206\u914D\u4E00\u4E2A\u5B58\u50A8\u6587\u4EF6\u3002\u8FD9\u5C31\u6709\u70B9\u7C7B\u4F3C\u4E8E\u6211\u4EEC\u6709\u4E00\u5927\u5806\u4E66\u9700\u8981\u88C5\u4E0A\u4E66\u67B6\uFF0C<code>RockeMQ</code> \u662F\u4E0D\u5206\u4E66\u7684\u79CD\u7C7B\u76F4\u63A5\u6210\u6279\u7684\u585E\u4E0A\u53BB\u7684\uFF0C\u800C <code>Kafka</code> \u662F\u5C06\u4E66\u672C\u653E\u5165\u6307\u5B9A\u7684\u5206\u7C7B\u533A\u57DF\u7684\u3002</p><p>\u800C <code>RocketMQ</code> \u4E3A\u4EC0\u4E48\u8981\u8FD9\u4E48\u505A\u5462\uFF1F\u539F\u56E0\u662F <strong>\u63D0\u9AD8\u6570\u636E\u7684\u5199\u5165\u6548\u7387</strong> \uFF0C\u4E0D\u5206 <code>Topic</code> \u610F\u5473\u7740\u6211\u4EEC\u6709\u66F4\u5927\u7684\u51E0\u7387\u83B7\u53D6 <strong>\u6210\u6279</strong> \u7684\u6D88\u606F\u8FDB\u884C\u6570\u636E\u5199\u5165\uFF0C\u4F46\u4E5F\u4F1A\u5E26\u6765\u4E00\u4E2A\u9EBB\u70E6\u5C31\u662F\u8BFB\u53D6\u6D88\u606F\u7684\u65F6\u5019\u9700\u8981\u904D\u5386\u6574\u4E2A\u5927\u6587\u4EF6\uFF0C\u8FD9\u662F\u975E\u5E38\u8017\u65F6\u7684\u3002</p><p>\u6240\u4EE5\uFF0C\u5728 <code>RocketMQ</code> \u4E2D\u53C8\u4F7F\u7528\u4E86 <code>ConsumeQueue</code> \u4F5C\u4E3A\u6BCF\u4E2A\u961F\u5217\u7684\u7D22\u5F15\u6587\u4EF6\u6765 <strong>\u63D0\u5347\u8BFB\u53D6\u6D88\u606F\u7684\u6548\u7387</strong>\u3002\u6211\u4EEC\u53EF\u4EE5\u76F4\u63A5\u6839\u636E\u961F\u5217\u7684\u6D88\u606F\u5E8F\u53F7\uFF0C\u8BA1\u7B97\u51FA\u7D22\u5F15\u7684\u5168\u5C40\u4F4D\u7F6E\uFF08\u7D22\u5F15\u5E8F\u53F7*\u7D22\u5F15\u56FA\u5B9A\u2ED3\u5EA620\uFF09\uFF0C\u7136\u540E\u76F4\u63A5\u8BFB\u53D6\u8FD9\u6761\u7D22\u5F15\uFF0C\u518D\u6839\u636E\u7D22\u5F15\u4E2D\u8BB0\u5F55\u7684\u6D88\u606F\u7684\u5168\u5C40\u4F4D\u7F6E\uFF0C\u627E\u5230\u6D88\u606F\u3002</p><p>\u8BB2\u5230\u8FD9\u91CC\uFF0C\u4F60\u53EF\u80FD\u5BF9 <code>RockeMQ</code> \u7684\u5B58\u50A8\u67B6\u6784\u8FD8\u6709\u4E9B\u6A21\u7CCA\uFF0C\u6CA1\u4E8B\uFF0C\u6211\u4EEC\u7ED3\u5408\u7740\u56FE\u6765\u7406\u89E3\u4E00\u4E0B\u3002</p><p><img src="https://my-blog-to-use.oss-cn-beijing.aliyuncs.com/2019-11/16ef388763c25c62.jpg" alt="" loading="lazy"></p><p>emmm\uFF0C\u662F\u4E0D\u662F\u6709\u4E00\u70B9\u590D\u6742\u{1F923}\uFF0C\u770B\u82F1\u6587\u56FE\u7247\u548C\u82F1\u6587\u6587\u6863\u7684\u65F6\u5019\u5C31\u4E0D\u8981\u6002\uFF0C\u786C\u7740\u5934\u76AE\u5F80\u4E0B\u770B\u5C31\u884C\u3002</p><blockquote><p>\u5982\u679C\u4E0A\u9762\u6CA1\u770B\u61C2\u7684\u8BFB\u8005\u4E00\u5B9A\u8981\u8BA4\u771F\u770B\u4E0B\u9762\u7684\u6D41\u7A0B\u5206\u6790\uFF01</p></blockquote><p>\u9996\u5148\uFF0C\u5728\u6700\u4E0A\u9762\u7684\u90A3\u4E00\u5757\u5C31\u662F\u6211\u521A\u521A\u8BB2\u7684\u4F60\u73B0\u5728\u53EF\u4EE5\u76F4\u63A5 <strong>\u628A <code>ConsumerQueue</code> \u7406\u89E3\u4E3A <code>Queue</code></strong>\u3002</p><p>\u5728\u56FE\u4E2D\u6700\u5DE6\u8FB9\u8BF4\u660E\u4E86\u7EA2\u8272\u65B9\u5757\u4EE3\u8868\u88AB\u5199\u5165\u7684\u6D88\u606F\uFF0C\u865A\u7EBF\u65B9\u5757\u4EE3\u8868\u7B49\u5F85\u88AB\u5199\u5165\u7684\u3002\u5DE6\u8FB9\u7684\u751F\u4EA7\u8005\u53D1\u9001\u6D88\u606F\u4F1A\u6307\u5B9A <code>Topic</code> \u3001<code>QueueId</code> \u548C\u5177\u4F53\u6D88\u606F\u5185\u5BB9\uFF0C\u800C\u5728 <code>Broker</code> \u4E2D\u7BA1\u4F60\u662F\u54EA\u95E8\u5B50\u6D88\u606F\uFF0C\u4ED6\u76F4\u63A5 <strong>\u5168\u90E8\u987A\u5E8F\u5B58\u50A8\u5230\u4E86 CommitLog</strong>\u3002\u800C\u6839\u636E\u751F\u4EA7\u8005\u6307\u5B9A\u7684 <code>Topic</code> \u548C <code>QueueId</code> \u5C06\u8FD9\u6761\u6D88\u606F\u672C\u8EAB\u5728 <code>CommitLog</code> \u7684\u504F\u79FB(offset)\uFF0C\u6D88\u606F\u672C\u8EAB\u5927\u5C0F\uFF0C\u548Ctag\u7684hash\u503C\u5B58\u5165\u5BF9\u5E94\u7684 <code>ConsumeQueue</code> \u7D22\u5F15\u6587\u4EF6\u4E2D\u3002\u800C\u5728\u6BCF\u4E2A\u961F\u5217\u4E2D\u90FD\u4FDD\u5B58\u4E86 <code>ConsumeOffset</code> \u5373\u6BCF\u4E2A\u6D88\u8D39\u8005\u7EC4\u7684\u6D88\u8D39\u4F4D\u7F6E(\u6211\u5728\u67B6\u6784\u90A3\u91CC\u63D0\u5230\u4E86\uFF0C\u5FD8\u4E86\u7684\u540C\u5B66\u53EF\u4EE5\u56DE\u53BB\u770B\u4E00\u4E0B)\uFF0C\u800C\u6D88\u8D39\u8005\u62C9\u53D6\u6D88\u606F\u8FDB\u884C\u6D88\u8D39\u7684\u65F6\u5019\u53EA\u9700\u8981\u6839\u636E <code>ConsumeOffset</code> \u83B7\u53D6\u4E0B\u4E00\u4E2A\u672A\u88AB\u6D88\u8D39\u7684\u6D88\u606F\u5C31\u884C\u4E86\u3002</p><p>\u4E0A\u8FF0\u5C31\u662F\u6211\u5BF9\u4E8E\u6574\u4E2A\u6D88\u606F\u5B58\u50A8\u67B6\u6784\u7684\u5927\u6982\u7406\u89E3(\u8FD9\u91CC\u4E0D\u6D89\u53CA\u5230\u4E00\u4E9B\u7EC6\u8282\u8BA8\u8BBA\uFF0C\u6BD4\u5982\u7A00\u758F\u7D22\u5F15\u7B49\u7B49\u95EE\u9898)\uFF0C\u5E0C\u671B\u5BF9\u4F60\u6709\u5E2E\u52A9\u3002</p><p>\u56E0\u4E3A\u6709\u4E00\u4E2A\u77E5\u8BC6\u70B9\u56E0\u4E3A\u5199\u55E8\u4E86\u5FD8\u8BB2\u4E86\uFF0C\u60F3\u60F3\u5728\u54EA\u91CC\u52A0\u4E5F\u4E0D\u597D\uFF0C\u6240\u4EE5\u6211\u7559\u7ED9\u5927\u5BB6\u53BB\u601D\u8003\u{1F914}\u{1F914}\u4E00\u4E0B\u5427\u3002</p><p><img src="https://my-blog-to-use.oss-cn-beijing.aliyuncs.com/2019-11/e314ee45gy1g05zgr67bbj20gp0b3aba.jpg" alt="" loading="lazy"></p><p>\u4E3A\u4EC0\u4E48 <code>CommitLog</code> \u6587\u4EF6\u8981\u8BBE\u8BA1\u6210\u56FA\u5B9A\u5927\u5C0F\u7684\u957F\u5EA6\u5462\uFF1F\u63D0\u9192\uFF1A<strong>\u5185\u5B58\u6620\u5C04\u673A\u5236</strong>\u3002</p><h2 id="\u603B\u7ED3" tabindex="-1"><a class="header-anchor" href="#\u603B\u7ED3" aria-hidden="true">#</a> \u603B\u7ED3</h2><p>\u603B\u7B97\u628A\u8FD9\u7BC7\u535A\u5BA2\u5199\u5B8C\u4E86\u3002\u6211\u8BB2\u7684\u4F60\u4EEC\u8FD8\u8BB0\u5F97\u5417\u{1F605}\uFF1F</p><p>\u8FD9\u7BC7\u6587\u7AE0\u4E2D\u6211\u4E3B\u8981\u60F3\u5927\u5BB6\u4ECB\u7ECD\u4E86</p><ol><li>\u6D88\u606F\u961F\u5217\u51FA\u73B0\u7684\u539F\u56E0</li><li>\u6D88\u606F\u961F\u5217\u7684\u4F5C\u7528(\u5F02\u6B65\uFF0C\u89E3\u8026\uFF0C\u524A\u5CF0)</li><li>\u6D88\u606F\u961F\u5217\u5E26\u6765\u7684\u4E00\u7CFB\u5217\u95EE\u9898(\u6D88\u606F\u5806\u79EF\u3001\u91CD\u590D\u6D88\u8D39\u3001\u987A\u5E8F\u6D88\u8D39\u3001\u5206\u5E03\u5F0F\u4E8B\u52A1\u7B49\u7B49)</li><li>\u6D88\u606F\u961F\u5217\u7684\u4E24\u79CD\u6D88\u606F\u6A21\u578B\u2014\u2014\u961F\u5217\u548C\u4E3B\u9898\u6A21\u5F0F</li><li>\u5206\u6790\u4E86 <code>RocketMQ</code> \u7684\u6280\u672F\u67B6\u6784(<code>NameServer</code> \u3001<code>Broker</code> \u3001<code>Producer</code> \u3001<code>Comsumer</code>)</li><li>\u7ED3\u5408 <code>RocketMQ</code> \u56DE\u7B54\u4E86\u6D88\u606F\u961F\u5217\u526F\u4F5C\u7528\u7684\u89E3\u51B3\u65B9\u6848</li><li>\u4ECB\u7ECD\u4E86 <code>RocketMQ</code> \u7684\u5B58\u50A8\u673A\u5236\u548C\u5237\u76D8\u7B56\u7565\u3002</li></ol><p>\u7B49\u7B49\u3002\u3002\u3002</p><blockquote><p>\u5982\u679C\u559C\u6B22\u53EF\u4EE5\u70B9\u8D5E\u54DF\u{1F44D}\u{1F44D}\u{1F44D}\u3002</p></blockquote>',210);function m(k,b){const c=p("ExternalLinkIcon");return r(),s("div",null,[g,o("blockquote",null,[i,o("p",null,[o("a",l,[u,t(c)])])]),h])}var Q=d(a,[["render",m],["__file","rocketmq-intro.html.vue"]]);export{Q as default};
