import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";import{o as r,c as t,b as e,a,d as o,e as s,r as n}from"./app.1e05478d.js";const p={},g=e("h1",{id:"zookeeper-\u76F8\u5173\u6982\u5FF5\u603B\u7ED3-\u8FDB\u9636",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#zookeeper-\u76F8\u5173\u6982\u5FF5\u603B\u7ED3-\u8FDB\u9636","aria-hidden":"true"},"#"),o(" ZooKeeper \u76F8\u5173\u6982\u5FF5\u603B\u7ED3(\u8FDB\u9636)")],-1),i={href:"https://juejin.im/user/5c33853851882525ea106810",target:"_blank",rel:"noopener noreferrer"},l=o("FrancisQ"),h=o(" \u6295\u7A3F\u3002"),m=s('<h2 id="_1-\u597D\u4E45\u4E0D\u89C1" tabindex="-1"><a class="header-anchor" href="#_1-\u597D\u4E45\u4E0D\u89C1" aria-hidden="true">#</a> 1. \u597D\u4E45\u4E0D\u89C1</h2><p>\u79BB\u4E0A\u4E00\u7BC7\u6587\u7AE0\u7684\u53D1\u5E03\u4E5F\u5FEB\u4E00\u4E2A\u6708\u4E86\uFF0C\u60F3\u60F3\u5DF2\u7ECF\u5FEB\u4E00\u4E2A\u6708\u6CA1\u5199\u4E1C\u897F\u4E86\uFF0C\u5176\u4E2D\u53EF\u80FD\u6709\u671F\u672B\u8003\u8BD5\u3001\u8BFE\u7A0B\u8BBE\u8BA1\u548C\u9A7E\u7167\u8003\u8BD5\uFF0C\u4F46\u8FD9\u90FD\u4E0D\u662F\u501F\u53E3\uFF01</p><p>\u4E00\u5230\u51AC\u5929\u5C31\u61D2\u7684\u4E0D\u884C\uFF0C\u671B\u5E7F\u5927\u6398\u53CB\u7763\u4FC3\u6211\u{1F644}\u{1F644}\u270D\uFE0F\u270D\uFE0F\u3002</p><blockquote><p>\u6587\u7AE0\u5F88\u957F\uFF0C\u5148\u8D5E\u540E\u770B\uFF0C\u517B\u6210\u4E60\u60EF\u3002\u2764\uFE0F \u{1F9E1} \u{1F49B} \u{1F49A} \u{1F499} \u{1F49C}</p></blockquote><h2 id="_2-\u4EC0\u4E48\u662Fzookeeper" tabindex="-1"><a class="header-anchor" href="#_2-\u4EC0\u4E48\u662Fzookeeper" aria-hidden="true">#</a> 2. \u4EC0\u4E48\u662FZooKeeper</h2><p><code>ZooKeeper</code> \u7531 <code>Yahoo</code> \u5F00\u53D1\uFF0C\u540E\u6765\u6350\u8D60\u7ED9\u4E86 <code>Apache</code> \uFF0C\u73B0\u5DF2\u6210\u4E3A <code>Apache</code> \u9876\u7EA7\u9879\u76EE\u3002<code>ZooKeeper</code> \u662F\u4E00\u4E2A\u5F00\u6E90\u7684\u5206\u5E03\u5F0F\u5E94\u7528\u7A0B\u5E8F\u534F\u8C03\u670D\u52A1\u5668\uFF0C\u5176\u4E3A\u5206\u5E03\u5F0F\u7CFB\u7EDF\u63D0\u4F9B\u4E00\u81F4\u6027\u670D\u52A1\u3002\u5176\u4E00\u81F4\u6027\u662F\u901A\u8FC7\u57FA\u4E8E <code>Paxos</code> \u7B97\u6CD5\u7684 <code>ZAB</code> \u534F\u8BAE\u5B8C\u6210\u7684\u3002\u5176\u4E3B\u8981\u529F\u80FD\u5305\u62EC\uFF1A\u914D\u7F6E\u7EF4\u62A4\u3001\u5206\u5E03\u5F0F\u540C\u6B65\u3001\u96C6\u7FA4\u7BA1\u7406\u3001\u5206\u5E03\u5F0F\u4E8B\u52A1\u7B49\u3002</p><p><img src="https://img-blog.csdnimg.cn/img_convert/7c349891b773671514a68f057b2e14f8.png" alt="zookeeper" loading="lazy"></p><p>\u7B80\u5355\u6765\u8BF4\uFF0C <code>ZooKeeper</code> \u662F\u4E00\u4E2A <strong>\u5206\u5E03\u5F0F\u534F\u8C03\u670D\u52A1\u6846\u67B6</strong> \u3002\u5206\u5E03\u5F0F\uFF1F\u534F\u8C03\u670D\u52A1\uFF1F\u8FD9\u5565\u73A9\u610F\uFF1F\u{1F914}\u{1F914}</p><p>\u5176\u5B9E\u89E3\u91CA\u5230\u5206\u5E03\u5F0F\u8FD9\u4E2A\u6982\u5FF5\u7684\u65F6\u5019\uFF0C\u6211\u53D1\u73B0\u6709\u4E9B\u540C\u5B66\u5E76\u4E0D\u662F\u80FD\u628A **\u5206\u5E03\u5F0F\u548C\u96C6\u7FA4 **\u8FD9\u4E24\u4E2A\u6982\u5FF5\u5F88\u597D\u7684\u7406\u89E3\u900F\u3002\u524D\u6BB5\u65F6\u95F4\u6709\u540C\u5B66\u548C\u6211\u63A2\u8BA8\u8D77\u5206\u5E03\u5F0F\u7684\u4E1C\u897F\uFF0C\u4ED6\u8BF4\u5206\u5E03\u5F0F\u4E0D\u5C31\u662F\u52A0\u673A\u5668\u5417\uFF1F\u4E00\u53F0\u673A\u5668\u4E0D\u591F\u7528\u518D\u52A0\u4E00\u53F0\u6297\u538B\u5457\u3002\u5F53\u7136\u52A0\u673A\u5668\u8FD9\u79CD\u8BF4\u6CD5\u4E5F\u65E0\u53EF\u539A\u975E\uFF0C\u4F60\u4E00\u4E2A\u5206\u5E03\u5F0F\u7CFB\u7EDF\u5FC5\u5B9A\u6D89\u53CA\u5230\u591A\u4E2A\u673A\u5668\uFF0C\u4F46\u662F\u4F60\u522B\u5FD8\u4E86\uFF0C\u8BA1\u7B97\u673A\u5B66\u79D1\u4E2D\u8FD8\u6709\u4E00\u4E2A\u76F8\u4F3C\u7684\u6982\u5FF5\u2014\u2014 <code>Cluster</code> \uFF0C\u96C6\u7FA4\u4E0D\u4E5F\u662F\u52A0\u673A\u5668\u5417\uFF1F\u4F46\u662F \u96C6\u7FA4 \u548C \u5206\u5E03\u5F0F \u5176\u5B9E\u5C31\u662F\u4E24\u4E2A\u5B8C\u5168\u4E0D\u540C\u7684\u6982\u5FF5\u3002</p><p>\u6BD4\u5982\uFF0C\u6211\u73B0\u5728\u6709\u4E00\u4E2A\u79D2\u6740\u670D\u52A1\uFF0C\u5E76\u53D1\u91CF\u592A\u5927\u5355\u673A\u7CFB\u7EDF\u627F\u53D7\u4E0D\u4F4F\uFF0C\u90A3\u6211\u52A0\u51E0\u53F0\u670D\u52A1\u5668\u4E5F <strong>\u4E00\u6837</strong> \u63D0\u4F9B\u79D2\u6740\u670D\u52A1\uFF0C\u8FD9\u4E2A\u65F6\u5019\u5C31\u662F <strong><code>Cluster</code> \u96C6\u7FA4</strong> \u3002</p><p><img src="https://img-blog.csdnimg.cn/img_convert/ffcb080eb66f242ffcd8d2047a7f46aa.png" alt="cluster" loading="lazy"></p><p>\u4F46\u662F\uFF0C\u6211\u73B0\u5728\u6362\u4E00\u79CD\u65B9\u5F0F\uFF0C\u6211\u5C06\u4E00\u4E2A\u79D2\u6740\u670D\u52A1 <strong>\u62C6\u5206\u6210\u591A\u4E2A\u5B50\u670D\u52A1</strong> \uFF0C\u6BD4\u5982\u521B\u5EFA\u8BA2\u5355\u670D\u52A1\uFF0C\u589E\u52A0\u79EF\u5206\u670D\u52A1\uFF0C\u6263\u4F18\u60E0\u5238\u670D\u52A1\u7B49\u7B49\uFF0C<strong>\u7136\u540E\u6211\u5C06\u8FD9\u4E9B\u5B50\u670D\u52A1\u90FD\u90E8\u7F72\u5728\u4E0D\u540C\u7684\u670D\u52A1\u5668\u4E0A</strong> \uFF0C\u8FD9\u4E2A\u65F6\u5019\u5C31\u662F <strong><code>Distributed</code> \u5206\u5E03\u5F0F</strong> \u3002</p><p><img src="https://img-blog.csdnimg.cn/img_convert/07191f38aa947b0075e5c0a6a019a11d.png" alt="distributed" loading="lazy"></p><p>\u800C\u6211\u4E3A\u4EC0\u4E48\u53CD\u9A73\u540C\u5B66\u6240\u8BF4\u7684\u5206\u5E03\u5F0F\u5C31\u662F\u52A0\u673A\u5668\u5462\uFF1F\u56E0\u4E3A\u6211\u8BA4\u4E3A\u52A0\u673A\u5668\u66F4\u52A0\u9002\u7528\u4E8E\u6784\u5EFA\u96C6\u7FA4\uFF0C\u56E0\u4E3A\u5B83\u771F\u662F\u53EA\u6709\u52A0\u673A\u5668\u3002\u800C\u5BF9\u4E8E\u5206\u5E03\u5F0F\u6765\u8BF4\uFF0C\u4F60\u9996\u5148\u9700\u8981\u5C06\u4E1A\u52A1\u8FDB\u884C\u62C6\u5206\uFF0C\u7136\u540E\u518D\u52A0\u673A\u5668\uFF08\u4E0D\u4EC5\u4EC5\u662F\u52A0\u673A\u5668\u90A3\u4E48\u7B80\u5355\uFF09\uFF0C\u540C\u65F6\u4F60\u8FD8\u8981\u53BB\u89E3\u51B3\u5206\u5E03\u5F0F\u5E26\u6765\u7684\u4E00\u7CFB\u5217\u95EE\u9898\u3002</p><p><img src="https://img-blog.csdnimg.cn/img_convert/2b2fbc21abfb3f6547a2121f28c6d00f.png" alt="" loading="lazy"></p><p>\u6BD4\u5982\u5404\u4E2A\u5206\u5E03\u5F0F\u7EC4\u4EF6\u5982\u4F55\u534F\u8C03\u8D77\u6765\uFF0C\u5982\u4F55\u51CF\u5C11\u5404\u4E2A\u7CFB\u7EDF\u4E4B\u95F4\u7684\u8026\u5408\u5EA6\uFF0C\u5206\u5E03\u5F0F\u4E8B\u52A1\u7684\u5904\u7406\uFF0C\u5982\u4F55\u53BB\u914D\u7F6E\u6574\u4E2A\u5206\u5E03\u5F0F\u7CFB\u7EDF\u7B49\u7B49\u3002<code>ZooKeeper</code> \u4E3B\u8981\u5C31\u662F\u89E3\u51B3\u8FD9\u4E9B\u95EE\u9898\u7684\u3002</p><h2 id="_3-\u4E00\u81F4\u6027\u95EE\u9898" tabindex="-1"><a class="header-anchor" href="#_3-\u4E00\u81F4\u6027\u95EE\u9898" aria-hidden="true">#</a> 3. \u4E00\u81F4\u6027\u95EE\u9898</h2><p>\u8BBE\u8BA1\u4E00\u4E2A\u5206\u5E03\u5F0F\u7CFB\u7EDF\u5FC5\u5B9A\u4F1A\u9047\u5230\u4E00\u4E2A\u95EE\u9898\u2014\u2014 <strong>\u56E0\u4E3A\u5206\u533A\u5BB9\u5FCD\u6027\uFF08partition tolerance\uFF09\u7684\u5B58\u5728\uFF0C\u5C31\u5FC5\u5B9A\u8981\u6C42\u6211\u4EEC\u9700\u8981\u5728\u7CFB\u7EDF\u53EF\u7528\u6027\uFF08availability\uFF09\u548C\u6570\u636E\u4E00\u81F4\u6027\uFF08consistency\uFF09\u4E2D\u505A\u51FA\u6743\u8861</strong> \u3002\u8FD9\u5C31\u662F\u8457\u540D\u7684 <code>CAP</code> \u5B9A\u7406\u3002</p><p>\u7406\u89E3\u8D77\u6765\u5176\u5B9E\u5F88\u7B80\u5355\uFF0C\u6BD4\u5982\u8BF4\u628A\u4E00\u4E2A\u73ED\u7EA7\u4F5C\u4E3A\u6574\u4E2A\u7CFB\u7EDF\uFF0C\u800C\u5B66\u751F\u662F\u7CFB\u7EDF\u4E2D\u7684\u4E00\u4E2A\u4E2A\u72EC\u7ACB\u7684\u5B50\u7CFB\u7EDF\u3002\u8FD9\u4E2A\u65F6\u5019\u73ED\u91CC\u7684\u5C0F\u7EA2\u5C0F\u660E\u5077\u5077\u8C08\u604B\u7231\u88AB\u73ED\u91CC\u7684\u5927\u5634\u5DF4\u5C0F\u82B1\u53D1\u73B0\u4E86\uFF0C\u5C0F\u82B1\u6B23\u559C\u82E5\u72C2\u544A\u8BC9\u4E86\u5468\u56F4\u7684\u4EBA\uFF0C\u7136\u540E\u5C0F\u7EA2\u5C0F\u660E\u8C08\u604B\u7231\u7684\u6D88\u606F\u5728\u73ED\u7EA7\u91CC\u4F20\u64AD\u8D77\u6765\u4E86\u3002\u5F53\u5728\u6D88\u606F\u7684\u4F20\u64AD\uFF08\u6563\u5E03\uFF09\u8FC7\u7A0B\u4E2D\uFF0C\u4F60\u6293\u5230\u4E00\u4E2A\u540C\u5B66\u95EE\u4ED6\u4EEC\u7684\u60C5\u51B5\uFF0C\u5982\u679C\u56DE\u7B54\u4F60\u4E0D\u77E5\u9053\uFF0C\u90A3\u4E48\u8BF4\u660E\u6574\u4E2A\u73ED\u7EA7\u7CFB\u7EDF\u51FA\u73B0\u4E86\u6570\u636E\u4E0D\u4E00\u81F4\u7684\u95EE\u9898\uFF08\u56E0\u4E3A\u5C0F\u82B1\u5DF2\u7ECF\u77E5\u9053\u8FD9\u4E2A\u6D88\u606F\u4E86\uFF09\u3002\u800C\u5982\u679C\u4ED6\u76F4\u63A5\u4E0D\u56DE\u7B54\u4F60\uFF0C\u56E0\u4E3A\u6574\u4E2A\u73ED\u7EA7\u6709\u6D88\u606F\u5728\u8FDB\u884C\u4F20\u64AD\uFF08\u4E3A\u4E86\u4FDD\u8BC1\u4E00\u81F4\u6027\uFF0C\u9700\u8981\u6240\u6709\u4EBA\u90FD\u77E5\u9053\u624D\u53EF\u63D0\u4F9B\u670D\u52A1\uFF09\uFF0C\u8FD9\u4E2A\u65F6\u5019\u5C31\u51FA\u73B0\u4E86\u7CFB\u7EDF\u7684\u53EF\u7528\u6027\u95EE\u9898\u3002</p><p><img src="https://img-blog.csdnimg.cn/img_convert/34ffff41f6ca4f221ca9d9ad6f0b5470.png" alt="" loading="lazy"></p><p>\u800C\u4E0A\u8FF0\u524D\u8005\u5C31\u662F <code>Eureka</code> \u7684\u5904\u7406\u65B9\u5F0F\uFF0C\u5B83\u4FDD\u8BC1\u4E86AP\uFF08\u53EF\u7528\u6027\uFF09\uFF0C\u540E\u8005\u5C31\u662F\u6211\u4EEC\u4ECA\u5929\u6240\u8981\u8BB2\u7684 <code>ZooKeeper</code> \u7684\u5904\u7406\u65B9\u5F0F\uFF0C\u5B83\u4FDD\u8BC1\u4E86CP\uFF08\u6570\u636E\u4E00\u81F4\u6027\uFF09\u3002</p><h2 id="_4-\u4E00\u81F4\u6027\u534F\u8BAE\u548C\u7B97\u6CD5" tabindex="-1"><a class="header-anchor" href="#_4-\u4E00\u81F4\u6027\u534F\u8BAE\u548C\u7B97\u6CD5" aria-hidden="true">#</a> 4. \u4E00\u81F4\u6027\u534F\u8BAE\u548C\u7B97\u6CD5</h2><p>\u800C\u4E3A\u4E86\u89E3\u51B3\u6570\u636E\u4E00\u81F4\u6027\u95EE\u9898\uFF0C\u5728\u79D1\u5B66\u5BB6\u548C\u7A0B\u5E8F\u5458\u7684\u4E0D\u65AD\u63A2\u7D22\u4E2D\uFF0C\u5C31\u51FA\u73B0\u4E86\u5F88\u591A\u7684\u4E00\u81F4\u6027\u534F\u8BAE\u548C\u7B97\u6CD5\u3002\u6BD4\u5982 2PC\uFF08\u4E24\u9636\u6BB5\u63D0\u4EA4\uFF09\uFF0C3PC\uFF08\u4E09\u9636\u6BB5\u63D0\u4EA4\uFF09\uFF0CPaxos\u7B97\u6CD5\u7B49\u7B49\u3002</p><p>\u8FD9\u65F6\u5019\u8BF7\u4F60\u601D\u8003\u4E00\u4E2A\u95EE\u9898\uFF0C\u540C\u5B66\u4E4B\u95F4\u5982\u679C\u91C7\u7528\u4F20\u7EB8\u6761\u7684\u65B9\u5F0F\u53BB\u4F20\u64AD\u6D88\u606F\uFF0C\u90A3\u4E48\u5C31\u4F1A\u51FA\u73B0\u4E00\u4E2A\u95EE\u9898\u2014\u2014\u6211\u548B\u77E5\u9053\u6211\u7684\u5C0F\u7EB8\u6761\u6709\u6CA1\u6709\u4F20\u5230\u6211\u60F3\u8981\u4F20\u9012\u7684\u90A3\u4E2A\u4EBA\u624B\u4E2D\u5462\uFF1F\u4E07\u4E00\u88AB\u54EA\u4E2A\u5C0F\u5BB6\u4F19\u7ED9\u52AB\u6301\u7BE1\u6539\u4E86\u5462\uFF0C\u5BF9\u5427\uFF1F</p><p><img src="https://img-blog.csdnimg.cn/img_convert/b0e01fe3213dcc1535c31298ba9bdfbc.png" alt="" loading="lazy"></p><p>\u8FD9\u4E2A\u65F6\u5019\u5C31\u5F15\u7533\u51FA\u4E00\u4E2A\u6982\u5FF5\u2014\u2014 <strong>\u62DC\u5360\u5EAD\u5C06\u519B\u95EE\u9898</strong> \u3002\u5B83\u610F\u6307 <strong>\u5728\u4E0D\u53EF\u9760\u4FE1\u9053\u4E0A\u8BD5\u56FE\u901A\u8FC7\u6D88\u606F\u4F20\u9012\u7684\u65B9\u5F0F\u8FBE\u5230\u4E00\u81F4\u6027\u662F\u4E0D\u53EF\u80FD\u7684</strong>\uFF0C \u6240\u4EE5\u6240\u6709\u7684\u4E00\u81F4\u6027\u7B97\u6CD5\u7684 <strong>\u5FC5\u8981\u524D\u63D0</strong> \u5C31\u662F\u5B89\u5168\u53EF\u9760\u7684\u6D88\u606F\u901A\u9053\u3002</p><p>\u800C\u4E3A\u4EC0\u4E48\u8981\u53BB\u89E3\u51B3\u6570\u636E\u4E00\u81F4\u6027\u7684\u95EE\u9898\uFF1F\u4F60\u60F3\u60F3\uFF0C\u5982\u679C\u4E00\u4E2A\u79D2\u6740\u7CFB\u7EDF\u5C06\u670D\u52A1\u62C6\u5206\u6210\u4E86\u4E0B\u8BA2\u5355\u548C\u52A0\u79EF\u5206\u670D\u52A1\uFF0C\u8FD9\u4E24\u4E2A\u670D\u52A1\u90E8\u7F72\u5728\u4E0D\u540C\u7684\u673A\u5668\u4E0A\u4E86\uFF0C\u4E07\u4E00\u5728\u6D88\u606F\u7684\u4F20\u64AD\u8FC7\u7A0B\u4E2D\u79EF\u5206\u7CFB\u7EDF\u5B95\u673A\u4E86\uFF0C\u603B\u4E0D\u80FD\u4F60\u8FD9\u8FB9\u4E0B\u4E86\u8BA2\u5355\u5374\u6CA1\u52A0\u79EF\u5206\u5427\uFF1F\u4F60\u603B\u5F97\u4FDD\u8BC1\u4E24\u8FB9\u7684\u6570\u636E\u9700\u8981\u4E00\u81F4\u5427\uFF1F</p><h3 id="_4-1-2pc-\u4E24\u9636\u6BB5\u63D0\u4EA4" tabindex="-1"><a class="header-anchor" href="#_4-1-2pc-\u4E24\u9636\u6BB5\u63D0\u4EA4" aria-hidden="true">#</a> 4.1. 2PC\uFF08\u4E24\u9636\u6BB5\u63D0\u4EA4\uFF09</h3><p>\u4E24\u9636\u6BB5\u63D0\u4EA4\u662F\u4E00\u79CD\u4FDD\u8BC1\u5206\u5E03\u5F0F\u7CFB\u7EDF\u6570\u636E\u4E00\u81F4\u6027\u7684\u534F\u8BAE\uFF0C\u73B0\u5728\u5F88\u591A\u6570\u636E\u5E93\u90FD\u662F\u91C7\u7528\u7684\u4E24\u9636\u6BB5\u63D0\u4EA4\u534F\u8BAE\u6765\u5B8C\u6210 <strong>\u5206\u5E03\u5F0F\u4E8B\u52A1</strong> \u7684\u5904\u7406\u3002</p><p>\u5728\u4ECB\u7ECD2PC\u4E4B\u524D\uFF0C\u6211\u4EEC\u5148\u6765\u60F3\u60F3\u5206\u5E03\u5F0F\u4E8B\u52A1\u5230\u5E95\u6709\u4EC0\u4E48\u95EE\u9898\u5462\uFF1F</p><p>\u8FD8\u62FF\u79D2\u6740\u7CFB\u7EDF\u7684\u4E0B\u8BA2\u5355\u548C\u52A0\u79EF\u5206\u4E24\u4E2A\u7CFB\u7EDF\u6765\u4E3E\u4F8B\u5427\uFF08\u6211\u60F3\u4F60\u4EEC\u53EF\u80FD\u90FD\u5410\u4E86\u{1F92E}\u{1F92E}\u{1F92E}\uFF09\uFF0C\u6211\u4EEC\u6B64\u65F6\u4E0B\u5B8C\u8BA2\u5355\u4F1A\u53D1\u4E2A\u6D88\u606F\u7ED9\u79EF\u5206\u7CFB\u7EDF\u544A\u8BC9\u5B83\u4E0B\u9762\u8BE5\u589E\u52A0\u79EF\u5206\u4E86\u3002\u5982\u679C\u6211\u4EEC\u4EC5\u4EC5\u662F\u53D1\u9001\u4E00\u4E2A\u6D88\u606F\u4E5F\u4E0D\u6536\u56DE\u590D\uFF0C\u90A3\u4E48\u6211\u4EEC\u7684\u8BA2\u5355\u7CFB\u7EDF\u600E\u4E48\u80FD\u77E5\u9053\u79EF\u5206\u7CFB\u7EDF\u7684\u6536\u5230\u6D88\u606F\u7684\u60C5\u51B5\u5462\uFF1F\u5982\u679C\u6211\u4EEC\u589E\u52A0\u4E00\u4E2A\u6536\u56DE\u590D\u7684\u8FC7\u7A0B\uFF0C\u90A3\u4E48\u5F53\u79EF\u5206\u7CFB\u7EDF\u6536\u5230\u6D88\u606F\u540E\u8FD4\u56DE\u7ED9\u8BA2\u5355\u7CFB\u7EDF\u4E00\u4E2A <code>Response</code> \uFF0C\u4F46\u5728\u4E2D\u95F4\u51FA\u73B0\u4E86\u7F51\u7EDC\u6CE2\u52A8\uFF0C\u90A3\u4E2A\u56DE\u590D\u6D88\u606F\u6CA1\u6709\u53D1\u9001\u6210\u529F\uFF0C\u8BA2\u5355\u7CFB\u7EDF\u662F\u4E0D\u662F\u4EE5\u4E3A\u79EF\u5206\u7CFB\u7EDF\u6D88\u606F\u63A5\u6536\u5931\u8D25\u4E86\uFF1F\u5B83\u662F\u4E0D\u662F\u4F1A\u56DE\u6EDA\u4E8B\u52A1\uFF1F\u4F46\u6B64\u65F6\u79EF\u5206\u7CFB\u7EDF\u662F\u6210\u529F\u6536\u5230\u6D88\u606F\u7684\uFF0C\u5B83\u5C31\u4F1A\u53BB\u5904\u7406\u6D88\u606F\u7136\u540E\u7ED9\u7528\u6237\u589E\u52A0\u79EF\u5206\uFF0C\u8FD9\u4E2A\u65F6\u5019\u5C31\u4F1A\u51FA\u73B0\u79EF\u5206\u52A0\u4E86\u4F46\u662F\u8BA2\u5355\u6CA1\u4E0B\u6210\u529F\u3002</p><p>\u6240\u4EE5\u6211\u4EEC\u6240\u9700\u8981\u89E3\u51B3\u7684\u662F\u5728\u5206\u5E03\u5F0F\u7CFB\u7EDF\u4E2D\uFF0C\u6574\u4E2A\u8C03\u7528\u94FE\u4E2D\uFF0C\u6211\u4EEC\u6240\u6709\u670D\u52A1\u7684\u6570\u636E\u5904\u7406\u8981\u4E48\u90FD\u6210\u529F\u8981\u4E48\u90FD\u5931\u8D25\uFF0C\u5373\u6240\u6709\u670D\u52A1\u7684 <strong>\u539F\u5B50\u6027\u95EE\u9898</strong> \u3002</p><p>\u5728\u4E24\u9636\u6BB5\u63D0\u4EA4\u4E2D\uFF0C\u4E3B\u8981\u6D89\u53CA\u5230\u4E24\u4E2A\u89D2\u8272\uFF0C\u5206\u522B\u662F\u534F\u8C03\u8005\u548C\u53C2\u4E0E\u8005\u3002</p><p>\u7B2C\u4E00\u9636\u6BB5\uFF1A\u5F53\u8981\u6267\u884C\u4E00\u4E2A\u5206\u5E03\u5F0F\u4E8B\u52A1\u7684\u65F6\u5019\uFF0C\u4E8B\u52A1\u53D1\u8D77\u8005\u9996\u5148\u5411\u534F\u8C03\u8005\u53D1\u8D77\u4E8B\u52A1\u8BF7\u6C42\uFF0C\u7136\u540E\u534F\u8C03\u8005\u4F1A\u7ED9\u6240\u6709\u53C2\u4E0E\u8005\u53D1\u9001 <code>prepare</code> \u8BF7\u6C42\uFF08\u5176\u4E2D\u5305\u62EC\u4E8B\u52A1\u5185\u5BB9\uFF09\u544A\u8BC9\u53C2\u4E0E\u8005\u4F60\u4EEC\u9700\u8981\u6267\u884C\u4E8B\u52A1\u4E86\uFF0C\u5982\u679C\u80FD\u6267\u884C\u6211\u53D1\u7684\u4E8B\u52A1\u5185\u5BB9\u90A3\u4E48\u5C31\u5148\u6267\u884C\u4F46\u4E0D\u63D0\u4EA4\uFF0C\u6267\u884C\u540E\u8BF7\u7ED9\u6211\u56DE\u590D\u3002\u7136\u540E\u53C2\u4E0E\u8005\u6536\u5230 <code>prepare</code> \u6D88\u606F\u540E\uFF0C\u4ED6\u4EEC\u4F1A\u5F00\u59CB\u6267\u884C\u4E8B\u52A1\uFF08\u4F46\u4E0D\u63D0\u4EA4\uFF09\uFF0C\u5E76\u5C06 <code>Undo</code> \u548C <code>Redo</code> \u4FE1\u606F\u8BB0\u5165\u4E8B\u52A1\u65E5\u5FD7\u4E2D\uFF0C\u4E4B\u540E\u53C2\u4E0E\u8005\u5C31\u5411\u534F\u8C03\u8005\u53CD\u9988\u662F\u5426\u51C6\u5907\u597D\u4E86\u3002</p><p>\u7B2C\u4E8C\u9636\u6BB5\uFF1A\u7B2C\u4E8C\u9636\u6BB5\u4E3B\u8981\u662F\u534F\u8C03\u8005\u6839\u636E\u53C2\u4E0E\u8005\u53CD\u9988\u7684\u60C5\u51B5\u6765\u51B3\u5B9A\u63A5\u4E0B\u6765\u662F\u5426\u53EF\u4EE5\u8FDB\u884C\u4E8B\u52A1\u7684\u63D0\u4EA4\u64CD\u4F5C\uFF0C\u5373\u63D0\u4EA4\u4E8B\u52A1\u6216\u8005\u56DE\u6EDA\u4E8B\u52A1\u3002</p><p>\u6BD4\u5982\u8FD9\u4E2A\u65F6\u5019 <strong>\u6240\u6709\u7684\u53C2\u4E0E\u8005</strong> \u90FD\u8FD4\u56DE\u4E86\u51C6\u5907\u597D\u4E86\u7684\u6D88\u606F\uFF0C\u8FD9\u4E2A\u65F6\u5019\u5C31\u8FDB\u884C\u4E8B\u52A1\u7684\u63D0\u4EA4\uFF0C\u534F\u8C03\u8005\u6B64\u65F6\u4F1A\u7ED9\u6240\u6709\u7684\u53C2\u4E0E\u8005\u53D1\u9001 <strong><code>Commit</code> \u8BF7\u6C42</strong> \uFF0C\u5F53\u53C2\u4E0E\u8005\u6536\u5230 <code>Commit</code> \u8BF7\u6C42\u7684\u65F6\u5019\u4F1A\u6267\u884C\u524D\u9762\u6267\u884C\u7684\u4E8B\u52A1\u7684 <strong>\u63D0\u4EA4\u64CD\u4F5C</strong> \uFF0C\u63D0\u4EA4\u5B8C\u6BD5\u4E4B\u540E\u5C06\u7ED9\u534F\u8C03\u8005\u53D1\u9001\u63D0\u4EA4\u6210\u529F\u7684\u54CD\u5E94\u3002</p><p>\u800C\u5982\u679C\u5728\u7B2C\u4E00\u9636\u6BB5\u5E76\u4E0D\u662F\u6240\u6709\u53C2\u4E0E\u8005\u90FD\u8FD4\u56DE\u4E86\u51C6\u5907\u597D\u4E86\u7684\u6D88\u606F\uFF0C\u90A3\u4E48\u6B64\u65F6\u534F\u8C03\u8005\u5C06\u4F1A\u7ED9\u6240\u6709\u53C2\u4E0E\u8005\u53D1\u9001 <strong>\u56DE\u6EDA\u4E8B\u52A1\u7684 <code>rollback</code> \u8BF7\u6C42</strong>\uFF0C\u53C2\u4E0E\u8005\u6536\u5230\u4E4B\u540E\u5C06\u4F1A <strong>\u56DE\u6EDA\u5B83\u5728\u7B2C\u4E00\u9636\u6BB5\u6240\u505A\u7684\u4E8B\u52A1\u5904\u7406</strong> \uFF0C\u7136\u540E\u518D\u5C06\u5904\u7406\u60C5\u51B5\u8FD4\u56DE\u7ED9\u534F\u8C03\u8005\uFF0C\u6700\u7EC8\u534F\u8C03\u8005\u6536\u5230\u54CD\u5E94\u540E\u4FBF\u7ED9\u4E8B\u52A1\u53D1\u8D77\u8005\u8FD4\u56DE\u5904\u7406\u5931\u8D25\u7684\u7ED3\u679C\u3002</p><p><img src="https://img-blog.csdnimg.cn/img_convert/7ce4e40b68d625676bb42c29efce046a.png" alt="2PC\u6D41\u7A0B" loading="lazy"></p><p>\u4E2A\u4EBA\u89C9\u5F97 2PC \u5B9E\u73B0\u5F97\u8FD8\u662F\u6BD4\u8F83\u9E21\u808B\u7684\uFF0C\u56E0\u4E3A\u4E8B\u5B9E\u4E0A\u5B83\u53EA\u89E3\u51B3\u4E86\u5404\u4E2A\u4E8B\u52A1\u7684\u539F\u5B50\u6027\u95EE\u9898\uFF0C\u968F\u4E4B\u4E5F\u5E26\u6765\u4E86\u5F88\u591A\u7684\u95EE\u9898\u3002</p><p><img src="https://img-blog.csdnimg.cn/img_convert/9af1ad68517561a8e9f5d3455a32132d.png" alt="" loading="lazy"></p><ul><li><strong>\u5355\u70B9\u6545\u969C\u95EE\u9898</strong>\uFF0C\u5982\u679C\u534F\u8C03\u8005\u6302\u4E86\u90A3\u4E48\u6574\u4E2A\u7CFB\u7EDF\u90FD\u5904\u4E8E\u4E0D\u53EF\u7528\u7684\u72B6\u6001\u4E86\u3002</li><li><strong>\u963B\u585E\u95EE\u9898</strong>\uFF0C\u5373\u5F53\u534F\u8C03\u8005\u53D1\u9001 <code>prepare</code> \u8BF7\u6C42\uFF0C\u53C2\u4E0E\u8005\u6536\u5230\u4E4B\u540E\u5982\u679C\u80FD\u5904\u7406\u90A3\u4E48\u5B83\u5C06\u4F1A\u8FDB\u884C\u4E8B\u52A1\u7684\u5904\u7406\u4F46\u5E76\u4E0D\u63D0\u4EA4\uFF0C\u8FD9\u4E2A\u65F6\u5019\u4F1A\u4E00\u76F4\u5360\u7528\u7740\u8D44\u6E90\u4E0D\u91CA\u653E\uFF0C\u5982\u679C\u6B64\u65F6\u534F\u8C03\u8005\u6302\u4E86\uFF0C\u90A3\u4E48\u8FD9\u4E9B\u8D44\u6E90\u90FD\u4E0D\u4F1A\u518D\u91CA\u653E\u4E86\uFF0C\u8FD9\u4F1A\u6781\u5927\u5F71\u54CD\u6027\u80FD\u3002</li><li><strong>\u6570\u636E\u4E0D\u4E00\u81F4\u95EE\u9898</strong>\uFF0C\u6BD4\u5982\u5F53\u7B2C\u4E8C\u9636\u6BB5\uFF0C\u534F\u8C03\u8005\u53EA\u53D1\u9001\u4E86\u4E00\u90E8\u5206\u7684 <code>commit</code> \u8BF7\u6C42\u5C31\u6302\u4E86\uFF0C\u90A3\u4E48\u4E5F\u5C31\u610F\u5473\u7740\uFF0C\u6536\u5230\u6D88\u606F\u7684\u53C2\u4E0E\u8005\u4F1A\u8FDB\u884C\u4E8B\u52A1\u7684\u63D0\u4EA4\uFF0C\u800C\u540E\u9762\u6CA1\u6536\u5230\u7684\u5219\u4E0D\u4F1A\u8FDB\u884C\u4E8B\u52A1\u63D0\u4EA4\uFF0C\u90A3\u4E48\u8FD9\u65F6\u5019\u5C31\u4F1A\u4EA7\u751F\u6570\u636E\u4E0D\u4E00\u81F4\u6027\u95EE\u9898\u3002</li></ul><h3 id="_4-2-3pc-\u4E09\u9636\u6BB5\u63D0\u4EA4" tabindex="-1"><a class="header-anchor" href="#_4-2-3pc-\u4E09\u9636\u6BB5\u63D0\u4EA4" aria-hidden="true">#</a> 4.2. 3PC\uFF08\u4E09\u9636\u6BB5\u63D0\u4EA4\uFF09</h3><p>\u56E0\u4E3A2PC\u5B58\u5728\u7684\u4E00\u7CFB\u5217\u95EE\u9898\uFF0C\u6BD4\u5982\u5355\u70B9\uFF0C\u5BB9\u9519\u673A\u5236\u7F3A\u9677\u7B49\u7B49\uFF0C\u4ECE\u800C\u4EA7\u751F\u4E86 <strong>3PC\uFF08\u4E09\u9636\u6BB5\u63D0\u4EA4\uFF09</strong> \u3002\u90A3\u4E48\u8FD9\u4E09\u9636\u6BB5\u53C8\u5206\u522B\u662F\u4EC0\u4E48\u5462\uFF1F</p><blockquote><p>\u5343\u4E07\u4E0D\u8981\u5427PC\u7406\u89E3\u6210\u4E2A\u4EBA\u7535\u8111\u4E86\uFF0C\u5176\u5B9E\u4ED6\u4EEC\u662F phase-commit \u7684\u7F29\u5199\uFF0C\u5373\u9636\u6BB5\u63D0\u4EA4\u3002</p></blockquote><ol><li><strong>CanCommit\u9636\u6BB5</strong>\uFF1A\u534F\u8C03\u8005\u5411\u6240\u6709\u53C2\u4E0E\u8005\u53D1\u9001 <code>CanCommit</code> \u8BF7\u6C42\uFF0C\u53C2\u4E0E\u8005\u6536\u5230\u8BF7\u6C42\u540E\u4F1A\u6839\u636E\u81EA\u8EAB\u60C5\u51B5\u67E5\u770B\u662F\u5426\u80FD\u6267\u884C\u4E8B\u52A1\uFF0C\u5982\u679C\u53EF\u4EE5\u5219\u8FD4\u56DE YES \u54CD\u5E94\u5E76\u8FDB\u5165\u9884\u5907\u72B6\u6001\uFF0C\u5426\u5219\u8FD4\u56DE NO \u3002</li><li><strong>PreCommit\u9636\u6BB5</strong>\uFF1A\u534F\u8C03\u8005\u6839\u636E\u53C2\u4E0E\u8005\u8FD4\u56DE\u7684\u54CD\u5E94\u6765\u51B3\u5B9A\u662F\u5426\u53EF\u4EE5\u8FDB\u884C\u4E0B\u9762\u7684 <code>PreCommit</code> \u64CD\u4F5C\u3002\u5982\u679C\u4E0A\u9762\u53C2\u4E0E\u8005\u8FD4\u56DE\u7684\u90FD\u662F YES\uFF0C\u90A3\u4E48\u534F\u8C03\u8005\u5C06\u5411\u6240\u6709\u53C2\u4E0E\u8005\u53D1\u9001 <code>PreCommit</code> \u9884\u63D0\u4EA4\u8BF7\u6C42\uFF0C<strong>\u53C2\u4E0E\u8005\u6536\u5230\u9884\u63D0\u4EA4\u8BF7\u6C42\u540E\uFF0C\u4F1A\u8FDB\u884C\u4E8B\u52A1\u7684\u6267\u884C\u64CD\u4F5C\uFF0C\u5E76\u5C06 <code>Undo</code> \u548C <code>Redo</code> \u4FE1\u606F\u5199\u5165\u4E8B\u52A1\u65E5\u5FD7\u4E2D</strong> \uFF0C\u6700\u540E\u5982\u679C\u53C2\u4E0E\u8005\u987A\u5229\u6267\u884C\u4E86\u4E8B\u52A1\u5219\u7ED9\u534F\u8C03\u8005\u8FD4\u56DE\u6210\u529F\u7684\u54CD\u5E94\u3002\u5982\u679C\u5728\u7B2C\u4E00\u9636\u6BB5\u534F\u8C03\u8005\u6536\u5230\u4E86 <strong>\u4EFB\u4F55\u4E00\u4E2A NO</strong> \u7684\u4FE1\u606F\uFF0C\u6216\u8005 <strong>\u5728\u4E00\u5B9A\u65F6\u95F4\u5185</strong> \u5E76\u6CA1\u6709\u6536\u5230\u5168\u90E8\u7684\u53C2\u4E0E\u8005\u7684\u54CD\u5E94\uFF0C\u90A3\u4E48\u5C31\u4F1A\u4E2D\u65AD\u4E8B\u52A1\uFF0C\u5B83\u4F1A\u5411\u6240\u6709\u53C2\u4E0E\u8005\u53D1\u9001\u4E2D\u65AD\u8BF7\u6C42\uFF08abort\uFF09\uFF0C\u53C2\u4E0E\u8005\u6536\u5230\u4E2D\u65AD\u8BF7\u6C42\u4E4B\u540E\u4F1A\u7ACB\u5373\u4E2D\u65AD\u4E8B\u52A1\uFF0C\u6216\u8005\u5728\u4E00\u5B9A\u65F6\u95F4\u5185\u6CA1\u6709\u6536\u5230\u534F\u8C03\u8005\u7684\u8BF7\u6C42\uFF0C\u5B83\u4E5F\u4F1A\u4E2D\u65AD\u4E8B\u52A1\u3002</li><li><strong>DoCommit\u9636\u6BB5</strong>\uFF1A\u8FD9\u4E2A\u9636\u6BB5\u5176\u5B9E\u548C <code>2PC</code> \u7684\u7B2C\u4E8C\u9636\u6BB5\u5DEE\u4E0D\u591A\uFF0C\u5982\u679C\u534F\u8C03\u8005\u6536\u5230\u4E86\u6240\u6709\u53C2\u4E0E\u8005\u5728 <code>PreCommit</code> \u9636\u6BB5\u7684 YES \u54CD\u5E94\uFF0C\u90A3\u4E48\u534F\u8C03\u8005\u5C06\u4F1A\u7ED9\u6240\u6709\u53C2\u4E0E\u8005\u53D1\u9001 <code>DoCommit</code> \u8BF7\u6C42\uFF0C<strong>\u53C2\u4E0E\u8005\u6536\u5230 <code>DoCommit</code> \u8BF7\u6C42\u540E\u5219\u4F1A\u8FDB\u884C\u4E8B\u52A1\u7684\u63D0\u4EA4\u5DE5\u4F5C</strong>\uFF0C\u5B8C\u6210\u540E\u5219\u4F1A\u7ED9\u534F\u8C03\u8005\u8FD4\u56DE\u54CD\u5E94\uFF0C\u534F\u8C03\u8005\u6536\u5230\u6240\u6709\u53C2\u4E0E\u8005\u8FD4\u56DE\u7684\u4E8B\u52A1\u63D0\u4EA4\u6210\u529F\u7684\u54CD\u5E94\u4E4B\u540E\u5219\u5B8C\u6210\u4E8B\u52A1\u3002\u82E5\u534F\u8C03\u8005\u5728 <code>PreCommit</code> \u9636\u6BB5 <strong>\u6536\u5230\u4E86\u4EFB\u4F55\u4E00\u4E2A NO \u6216\u8005\u5728\u4E00\u5B9A\u65F6\u95F4\u5185\u6CA1\u6709\u6536\u5230\u6240\u6709\u53C2\u4E0E\u8005\u7684\u54CD\u5E94</strong> \uFF0C\u90A3\u4E48\u5C31\u4F1A\u8FDB\u884C\u4E2D\u65AD\u8BF7\u6C42\u7684\u53D1\u9001\uFF0C\u53C2\u4E0E\u8005\u6536\u5230\u4E2D\u65AD\u8BF7\u6C42\u540E\u5219\u4F1A <strong>\u901A\u8FC7\u4E0A\u9762\u8BB0\u5F55\u7684\u56DE\u6EDA\u65E5\u5FD7</strong> \u6765\u8FDB\u884C\u4E8B\u52A1\u7684\u56DE\u6EDA\u64CD\u4F5C\uFF0C\u5E76\u5411\u534F\u8C03\u8005\u53CD\u9988\u56DE\u6EDA\u72B6\u51B5\uFF0C\u534F\u8C03\u8005\u6536\u5230\u53C2\u4E0E\u8005\u8FD4\u56DE\u7684\u6D88\u606F\u540E\uFF0C\u4E2D\u65AD\u4E8B\u52A1\u3002</li></ol><p><img src="https://img-blog.csdnimg.cn/img_convert/d0b44361c746593f70a6e42c298b413a.png" alt="3PC\u6D41\u7A0B" loading="lazy"></p><blockquote><p>\u8FD9\u91CC\u662F <code>3PC</code> \u5728\u6210\u529F\u7684\u73AF\u5883\u4E0B\u7684\u6D41\u7A0B\u56FE\uFF0C\u4F60\u53EF\u4EE5\u770B\u5230 <code>3PC</code> \u5728\u5F88\u591A\u5730\u65B9\u8FDB\u884C\u4E86\u8D85\u65F6\u4E2D\u65AD\u7684\u5904\u7406\uFF0C\u6BD4\u5982\u534F\u8C03\u8005\u5728\u6307\u5B9A\u65F6\u95F4\u5185\u4E3A\u6536\u5230\u5168\u90E8\u7684\u786E\u8BA4\u6D88\u606F\u5219\u8FDB\u884C\u4E8B\u52A1\u4E2D\u65AD\u7684\u5904\u7406\uFF0C\u8FD9\u6837\u80FD <strong>\u51CF\u5C11\u540C\u6B65\u963B\u585E\u7684\u65F6\u95F4</strong> \u3002\u8FD8\u6709\u9700\u8981\u6CE8\u610F\u7684\u662F\uFF0C<strong><code>3PC</code> \u5728 <code>DoCommit</code> \u9636\u6BB5\u53C2\u4E0E\u8005\u5982\u672A\u6536\u5230\u534F\u8C03\u8005\u53D1\u9001\u7684\u63D0\u4EA4\u4E8B\u52A1\u7684\u8BF7\u6C42\uFF0C\u5B83\u4F1A\u5728\u4E00\u5B9A\u65F6\u95F4\u5185\u8FDB\u884C\u4E8B\u52A1\u7684\u63D0\u4EA4</strong>\u3002\u4E3A\u4EC0\u4E48\u8FD9\u4E48\u505A\u5462\uFF1F\u662F\u56E0\u4E3A\u8FD9\u4E2A\u65F6\u5019\u6211\u4EEC\u80AF\u5B9A<strong>\u4FDD\u8BC1\u4E86\u5728\u7B2C\u4E00\u9636\u6BB5\u6240\u6709\u7684\u534F\u8C03\u8005\u5168\u90E8\u8FD4\u56DE\u4E86\u53EF\u4EE5\u6267\u884C\u4E8B\u52A1\u7684\u54CD\u5E94</strong>\uFF0C\u8FD9\u4E2A\u65F6\u5019\u6211\u4EEC\u6709\u7406\u7531<strong>\u76F8\u4FE1\u5176\u4ED6\u7CFB\u7EDF\u90FD\u80FD\u8FDB\u884C\u4E8B\u52A1\u7684\u6267\u884C\u548C\u63D0\u4EA4</strong>\uFF0C\u6240\u4EE5<strong>\u4E0D\u7BA1</strong>\u534F\u8C03\u8005\u6709\u6CA1\u6709\u53D1\u6D88\u606F\u7ED9\u53C2\u4E0E\u8005\uFF0C\u8FDB\u5165\u7B2C\u4E09\u9636\u6BB5\u53C2\u4E0E\u8005\u90FD\u4F1A\u8FDB\u884C\u4E8B\u52A1\u7684\u63D0\u4EA4\u64CD\u4F5C\u3002</p></blockquote><p>\u603B\u4E4B\uFF0C<code>3PC</code> \u901A\u8FC7\u4E00\u7CFB\u5217\u7684\u8D85\u65F6\u673A\u5236\u5F88\u597D\u7684\u7F13\u89E3\u4E86\u963B\u585E\u95EE\u9898\uFF0C\u4F46\u662F\u6700\u91CD\u8981\u7684\u4E00\u81F4\u6027\u5E76\u6CA1\u6709\u5F97\u5230\u6839\u672C\u7684\u89E3\u51B3\uFF0C\u6BD4\u5982\u5728 <code>PreCommit</code> \u9636\u6BB5\uFF0C\u5F53\u4E00\u4E2A\u53C2\u4E0E\u8005\u6536\u5230\u4E86\u8BF7\u6C42\u4E4B\u540E\u5176\u4ED6\u53C2\u4E0E\u8005\u548C\u534F\u8C03\u8005\u6302\u4E86\u6216\u8005\u51FA\u73B0\u4E86\u7F51\u7EDC\u5206\u533A\uFF0C\u8FD9\u4E2A\u65F6\u5019\u6536\u5230\u6D88\u606F\u7684\u53C2\u4E0E\u8005\u90FD\u4F1A\u8FDB\u884C\u4E8B\u52A1\u63D0\u4EA4\uFF0C\u8FD9\u5C31\u4F1A\u51FA\u73B0\u6570\u636E\u4E0D\u4E00\u81F4\u6027\u95EE\u9898\u3002</p><p>\u6240\u4EE5\uFF0C\u8981\u89E3\u51B3\u4E00\u81F4\u6027\u95EE\u9898\u8FD8\u9700\u8981\u9760 <code>Paxos</code> \u7B97\u6CD5\u2B50\uFE0F \u2B50\uFE0F \u2B50\uFE0F \u3002</p><h3 id="_4-3-paxos-\u7B97\u6CD5" tabindex="-1"><a class="header-anchor" href="#_4-3-paxos-\u7B97\u6CD5" aria-hidden="true">#</a> 4.3. <code>Paxos</code> \u7B97\u6CD5</h3><p><code>Paxos</code> \u7B97\u6CD5\u662F\u57FA\u4E8E<strong>\u6D88\u606F\u4F20\u9012\u4E14\u5177\u6709\u9AD8\u5EA6\u5BB9\u9519\u7279\u6027\u7684\u4E00\u81F4\u6027\u7B97\u6CD5</strong>\uFF0C\u662F\u76EE\u524D\u516C\u8BA4\u7684\u89E3\u51B3\u5206\u5E03\u5F0F\u4E00\u81F4\u6027\u95EE\u9898\u6700\u6709\u6548\u7684\u7B97\u6CD5\u4E4B\u4E00\uFF0C<strong>\u5176\u89E3\u51B3\u7684\u95EE\u9898\u5C31\u662F\u5728\u5206\u5E03\u5F0F\u7CFB\u7EDF\u4E2D\u5982\u4F55\u5C31\u67D0\u4E2A\u503C\uFF08\u51B3\u8BAE\uFF09\u8FBE\u6210\u4E00\u81F4</strong> \u3002</p><p>\u5728 <code>Paxos</code> \u4E2D\u4E3B\u8981\u6709\u4E09\u4E2A\u89D2\u8272\uFF0C\u5206\u522B\u4E3A <code>Proposer\u63D0\u6848\u8005</code>\u3001<code>Acceptor\u8868\u51B3\u8005</code>\u3001<code>Learner\u5B66\u4E60\u8005</code>\u3002<code>Paxos</code> \u7B97\u6CD5\u548C <code>2PC</code> \u4E00\u6837\uFF0C\u4E5F\u6709\u4E24\u4E2A\u9636\u6BB5\uFF0C\u5206\u522B\u4E3A <code>Prepare</code> \u548C <code>accept</code> \u9636\u6BB5\u3002</p><h4 id="_4-3-1-prepare-\u9636\u6BB5" tabindex="-1"><a class="header-anchor" href="#_4-3-1-prepare-\u9636\u6BB5" aria-hidden="true">#</a> 4.3.1. prepare \u9636\u6BB5</h4><ul><li><code>Proposer\u63D0\u6848\u8005</code>\uFF1A\u8D1F\u8D23\u63D0\u51FA <code>proposal</code>\uFF0C\u6BCF\u4E2A\u63D0\u6848\u8005\u5728\u63D0\u51FA\u63D0\u6848\u65F6\u90FD\u4F1A\u9996\u5148\u83B7\u53D6\u5230\u4E00\u4E2A <strong>\u5177\u6709\u5168\u5C40\u552F\u4E00\u6027\u7684\u3001\u9012\u589E\u7684\u63D0\u6848\u7F16\u53F7N</strong>\uFF0C\u5373\u5728\u6574\u4E2A\u96C6\u7FA4\u4E2D\u662F\u552F\u4E00\u7684\u7F16\u53F7 N\uFF0C\u7136\u540E\u5C06\u8BE5\u7F16\u53F7\u8D4B\u4E88\u5176\u8981\u63D0\u51FA\u7684\u63D0\u6848\uFF0C\u5728<strong>\u7B2C\u4E00\u9636\u6BB5\u662F\u53EA\u5C06\u63D0\u6848\u7F16\u53F7\u53D1\u9001\u7ED9\u6240\u6709\u7684\u8868\u51B3\u8005</strong>\u3002</li><li><code>Acceptor\u8868\u51B3\u8005</code>\uFF1A\u6BCF\u4E2A\u8868\u51B3\u8005\u5728 <code>accept</code> \u67D0\u63D0\u6848\u540E\uFF0C\u4F1A\u5C06\u8BE5\u63D0\u6848\u7F16\u53F7N\u8BB0\u5F55\u5728\u672C\u5730\uFF0C\u8FD9\u6837\u6BCF\u4E2A\u8868\u51B3\u8005\u4E2D\u4FDD\u5B58\u7684\u5DF2\u7ECF\u88AB accept \u7684\u63D0\u6848\u4E2D\u4F1A\u5B58\u5728\u4E00\u4E2A<strong>\u7F16\u53F7\u6700\u5927\u7684\u63D0\u6848</strong>\uFF0C\u5176\u7F16\u53F7\u5047\u8BBE\u4E3A <code>maxN</code>\u3002\u6BCF\u4E2A\u8868\u51B3\u8005\u4EC5\u4F1A <code>accept</code> \u7F16\u53F7\u5927\u4E8E\u81EA\u5DF1\u672C\u5730 <code>maxN</code> \u7684\u63D0\u6848\uFF0C\u5728\u6279\u51C6\u63D0\u6848\u65F6\u8868\u51B3\u8005\u4F1A\u5C06\u4EE5\u524D\u63A5\u53D7\u8FC7\u7684\u6700\u5927\u7F16\u53F7\u7684\u63D0\u6848\u4F5C\u4E3A\u54CD\u5E94\u53CD\u9988\u7ED9 <code>Proposer</code> \u3002</li></ul><blockquote><p>\u4E0B\u9762\u662F <code>prepare</code> \u9636\u6BB5\u7684\u6D41\u7A0B\u56FE\uFF0C\u4F60\u53EF\u4EE5\u5BF9\u7167\u7740\u53C2\u8003\u4E00\u4E0B\u3002</p></blockquote><p><img src="https://img-blog.csdnimg.cn/img_convert/22e8d512d954676bdf0cc92d200af8ef.png" alt="paxos\u7B2C\u4E00\u9636\u6BB5" loading="lazy"></p><h4 id="_4-3-2-accept-\u9636\u6BB5" tabindex="-1"><a class="header-anchor" href="#_4-3-2-accept-\u9636\u6BB5" aria-hidden="true">#</a> 4.3.2. accept \u9636\u6BB5</h4><p>\u5F53\u4E00\u4E2A\u63D0\u6848\u88AB <code>Proposer</code> \u63D0\u51FA\u540E\uFF0C\u5982\u679C <code>Proposer</code> \u6536\u5230\u4E86\u8D85\u8FC7\u534A\u6570\u7684 <code>Acceptor</code> \u7684\u6279\u51C6\uFF08<code>Proposer</code> \u672C\u8EAB\u540C\u610F\uFF09\uFF0C\u90A3\u4E48\u6B64\u65F6 <code>Proposer</code> \u4F1A\u7ED9\u6240\u6709\u7684 <code>Acceptor</code> \u53D1\u9001\u771F\u6B63\u7684\u63D0\u6848\uFF08\u4F60\u53EF\u4EE5\u7406\u89E3\u4E3A\u7B2C\u4E00\u9636\u6BB5\u4E3A\u8BD5\u63A2\uFF09\uFF0C\u8FD9\u4E2A\u65F6\u5019 <code>Proposer</code> \u5C31\u4F1A\u53D1\u9001\u63D0\u6848\u7684\u5185\u5BB9\u548C\u63D0\u6848\u7F16\u53F7\u3002</p><p>\u8868\u51B3\u8005\u6536\u5230\u63D0\u6848\u8BF7\u6C42\u540E\u4F1A\u518D\u6B21\u6BD4\u8F83\u672C\u8EAB\u5DF2\u7ECF\u6279\u51C6\u8FC7\u7684\u6700\u5927\u63D0\u6848\u7F16\u53F7\u548C\u8BE5\u63D0\u6848\u7F16\u53F7\uFF0C\u5982\u679C\u8BE5\u63D0\u6848\u7F16\u53F7 <strong>\u5927\u4E8E\u7B49\u4E8E</strong> \u5DF2\u7ECF\u6279\u51C6\u8FC7\u7684\u6700\u5927\u63D0\u6848\u7F16\u53F7\uFF0C\u90A3\u4E48\u5C31 <code>accept</code> \u8BE5\u63D0\u6848\uFF08\u6B64\u65F6\u6267\u884C\u63D0\u6848\u5185\u5BB9\u4F46\u4E0D\u63D0\u4EA4\uFF09\uFF0C\u968F\u540E\u5C06\u60C5\u51B5\u8FD4\u56DE\u7ED9 <code>Proposer</code> \u3002\u5982\u679C\u4E0D\u6EE1\u8DB3\u5219\u4E0D\u56DE\u5E94\u6216\u8005\u8FD4\u56DE NO \u3002</p><p><img src="https://img-blog.csdnimg.cn/img_convert/b82536f956f70a584c6a20c10113f225.png" alt="paxos\u7B2C\u4E8C\u9636\u6BB51" loading="lazy"></p><p>\u5F53 <code>Proposer</code> \u6536\u5230\u8D85\u8FC7\u534A\u6570\u7684 <code>accept</code> \uFF0C\u90A3\u4E48\u5B83\u8FD9\u4E2A\u65F6\u5019\u4F1A\u5411\u6240\u6709\u7684 <code>acceptor</code> \u53D1\u9001\u63D0\u6848\u7684\u63D0\u4EA4\u8BF7\u6C42\u3002\u9700\u8981\u6CE8\u610F\u7684\u662F\uFF0C\u56E0\u4E3A\u4E0A\u8FF0\u4EC5\u4EC5\u662F\u8D85\u8FC7\u534A\u6570\u7684 <code>acceptor</code> \u6279\u51C6\u6267\u884C\u4E86\u8BE5\u63D0\u6848\u5185\u5BB9\uFF0C\u5176\u4ED6\u6CA1\u6709\u6279\u51C6\u7684\u5E76\u6CA1\u6709\u6267\u884C\u8BE5\u63D0\u6848\u5185\u5BB9\uFF0C\u6240\u4EE5\u8FD9\u4E2A\u65F6\u5019\u9700\u8981<strong>\u5411\u672A\u6279\u51C6\u7684 <code>acceptor</code> \u53D1\u9001\u63D0\u6848\u5185\u5BB9\u548C\u63D0\u6848\u7F16\u53F7\u5E76\u8BA9\u5B83\u65E0\u6761\u4EF6\u6267\u884C\u548C\u63D0\u4EA4</strong>\uFF0C\u800C\u5BF9\u4E8E\u524D\u9762\u5DF2\u7ECF\u6279\u51C6\u8FC7\u8BE5\u63D0\u6848\u7684 <code>acceptor</code> \u6765\u8BF4 <strong>\u4EC5\u4EC5\u9700\u8981\u53D1\u9001\u8BE5\u63D0\u6848\u7684\u7F16\u53F7</strong> \uFF0C\u8BA9 <code>acceptor</code> \u6267\u884C\u63D0\u4EA4\u5C31\u884C\u4E86\u3002</p><p><img src="https://img-blog.csdnimg.cn/img_convert/743889b97485fdfe2094e5ef0af6b141.png" alt="paxos\u7B2C\u4E8C\u9636\u6BB52" loading="lazy"></p><p>\u800C\u5982\u679C <code>Proposer</code> \u5982\u679C\u6CA1\u6709\u6536\u5230\u8D85\u8FC7\u534A\u6570\u7684 <code>accept</code> \u90A3\u4E48\u5B83\u5C06\u4F1A\u5C06 <strong>\u9012\u589E</strong> \u8BE5 <code>Proposal</code> \u7684\u7F16\u53F7\uFF0C\u7136\u540E <strong>\u91CD\u65B0\u8FDB\u5165 <code>Prepare</code> \u9636\u6BB5</strong> \u3002</p><blockquote><p>\u5BF9\u4E8E <code>Learner</code> \u6765\u8BF4\u5982\u4F55\u53BB\u5B66\u4E60 <code>Acceptor</code> \u6279\u51C6\u7684\u63D0\u6848\u5185\u5BB9\uFF0C\u8FD9\u6709\u5F88\u591A\u65B9\u5F0F\uFF0C\u8BFB\u8005\u53EF\u4EE5\u81EA\u5DF1\u53BB\u4E86\u89E3\u4E00\u4E0B\uFF0C\u8FD9\u91CC\u4E0D\u505A\u8FC7\u591A\u89E3\u91CA\u3002</p></blockquote><h4 id="_4-3-3-paxos-\u7B97\u6CD5\u7684\u6B7B\u5FAA\u73AF\u95EE\u9898" tabindex="-1"><a class="header-anchor" href="#_4-3-3-paxos-\u7B97\u6CD5\u7684\u6B7B\u5FAA\u73AF\u95EE\u9898" aria-hidden="true">#</a> 4.3.3. <code>paxos</code> \u7B97\u6CD5\u7684\u6B7B\u5FAA\u73AF\u95EE\u9898</h4><p>\u5176\u5B9E\u5C31\u6709\u70B9\u7C7B\u4F3C\u4E8E\u4E24\u4E2A\u4EBA\u5435\u67B6\uFF0C\u5C0F\u660E\u8BF4\u6211\u662F\u5BF9\u7684\uFF0C\u5C0F\u7EA2\u8BF4\u6211\u624D\u662F\u5BF9\u7684\uFF0C\u4E24\u4E2A\u4EBA\u636E\u7406\u529B\u4E89\u7684\u8C01\u4E5F\u4E0D\u8BA9\u8C01\u{1F92C}\u{1F92C}\u3002</p><p>\u6BD4\u5982\u8BF4\uFF0C\u6B64\u65F6\u63D0\u6848\u8005 P1 \u63D0\u51FA\u4E00\u4E2A\u65B9\u6848 M1\uFF0C\u5B8C\u6210\u4E86 <code>Prepare</code> \u9636\u6BB5\u7684\u5DE5\u4F5C\uFF0C\u8FD9\u4E2A\u65F6\u5019 <code>acceptor</code> \u5219\u6279\u51C6\u4E86 M1\uFF0C\u4F46\u662F\u6B64\u65F6\u63D0\u6848\u8005 P2 \u540C\u65F6\u4E5F\u63D0\u51FA\u4E86\u4E00\u4E2A\u65B9\u6848 M2\uFF0C\u5B83\u4E5F\u5B8C\u6210\u4E86 <code>Prepare</code> \u9636\u6BB5\u7684\u5DE5\u4F5C\u3002\u7136\u540E P1 \u7684\u65B9\u6848\u5DF2\u7ECF\u4E0D\u80FD\u5728\u7B2C\u4E8C\u9636\u6BB5\u88AB\u6279\u51C6\u4E86\uFF08\u56E0\u4E3A <code>acceptor</code> \u5DF2\u7ECF\u6279\u51C6\u4E86\u6BD4 M1 \u66F4\u5927\u7684 M2\uFF09\uFF0C\u6240\u4EE5 P1 \u81EA\u589E\u65B9\u6848\u53D8\u4E3A M3 \u91CD\u65B0\u8FDB\u5165 <code>Prepare</code> \u9636\u6BB5\uFF0C\u7136\u540E <code>acceptor</code> \uFF0C\u53C8\u6279\u51C6\u4E86\u65B0\u7684 M3 \u65B9\u6848\uFF0C\u5B83\u53C8\u4E0D\u80FD\u6279\u51C6 M2 \u4E86\uFF0C\u8FD9\u4E2A\u65F6\u5019 M2 \u53C8\u81EA\u589E\u8FDB\u5165 <code>Prepare</code> \u9636\u6BB5\u3002\u3002\u3002</p><p>\u5C31\u8FD9\u6837\u65E0\u4F11\u65E0\u6B62\u7684\u6C38\u8FDC\u63D0\u6848\u4E0B\u53BB\uFF0C\u8FD9\u5C31\u662F <code>paxos</code> \u7B97\u6CD5\u7684\u6B7B\u5FAA\u73AF\u95EE\u9898\u3002</p><p><img src="https://img-blog.csdnimg.cn/img_convert/72ccf65cdc107346ff2a1a881d296a2b.png" alt="" loading="lazy"></p><p>\u90A3\u4E48\u5982\u4F55\u89E3\u51B3\u5462\uFF1F\u5F88\u7B80\u5355\uFF0C\u4EBA\u591A\u4E86\u5BB9\u6613\u5435\u67B6\uFF0C\u6211\u73B0\u5728 <strong>\u5C31\u5141\u8BB8\u4E00\u4E2A\u80FD\u63D0\u6848</strong> \u5C31\u884C\u4E86\u3002</p><h2 id="_5-\u5F15\u51FA-zab" tabindex="-1"><a class="header-anchor" href="#_5-\u5F15\u51FA-zab" aria-hidden="true">#</a> 5. \u5F15\u51FA <code>ZAB</code></h2><h3 id="_5-1-zookeeper-\u67B6\u6784" tabindex="-1"><a class="header-anchor" href="#_5-1-zookeeper-\u67B6\u6784" aria-hidden="true">#</a> 5.1. <code>Zookeeper</code> \u67B6\u6784</h3><p>\u4F5C\u4E3A\u4E00\u4E2A\u4F18\u79C0\u9AD8\u6548\u4E14\u53EF\u9760\u7684\u5206\u5E03\u5F0F\u534F\u8C03\u6846\u67B6\uFF0C<code>ZooKeeper</code> \u5728\u89E3\u51B3\u5206\u5E03\u5F0F\u6570\u636E\u4E00\u81F4\u6027\u95EE\u9898\u65F6\u5E76\u6CA1\u6709\u76F4\u63A5\u4F7F\u7528 <code>Paxos</code> \uFF0C\u800C\u662F\u4E13\u95E8\u5B9A\u5236\u4E86\u4E00\u81F4\u6027\u534F\u8BAE\u53EB\u505A <code>ZAB(ZooKeeper Atomic Broadcast)</code> \u539F\u5B50\u5E7F\u64AD\u534F\u8BAE\uFF0C\u8BE5\u534F\u8BAE\u80FD\u591F\u5F88\u597D\u5730\u652F\u6301 <strong>\u5D29\u6E83\u6062\u590D</strong> \u3002</p><p><img src="https://img-blog.csdnimg.cn/img_convert/0c38d08ea026e25bf3849cc7654a4e79.png" alt="Zookeeper\u67B6\u6784" loading="lazy"></p><h3 id="_5-2-zab-\u4E2D\u7684\u4E09\u4E2A\u89D2\u8272" tabindex="-1"><a class="header-anchor" href="#_5-2-zab-\u4E2D\u7684\u4E09\u4E2A\u89D2\u8272" aria-hidden="true">#</a> 5.2. <code>ZAB</code> \u4E2D\u7684\u4E09\u4E2A\u89D2\u8272</h3><p>\u548C\u4ECB\u7ECD <code>Paxos</code> \u4E00\u6837\uFF0C\u5728\u4ECB\u7ECD <code>ZAB</code> \u534F\u8BAE\u4E4B\u524D\uFF0C\u6211\u4EEC\u9996\u5148\u6765\u4E86\u89E3\u4E00\u4E0B\u5728 <code>ZAB</code> \u4E2D\u4E09\u4E2A\u4E3B\u8981\u7684\u89D2\u8272\uFF0C<code>Leader \u9886\u5BFC\u8005</code>\u3001<code>Follower\u8DDF\u968F\u8005</code>\u3001<code>Observer\u89C2\u5BDF\u8005</code> \u3002</p><ul><li><code>Leader</code> \uFF1A\u96C6\u7FA4\u4E2D <strong>\u552F\u4E00\u7684\u5199\u8BF7\u6C42\u5904\u7406\u8005</strong> \uFF0C\u80FD\u591F\u53D1\u8D77\u6295\u7968\uFF08\u6295\u7968\u4E5F\u662F\u4E3A\u4E86\u8FDB\u884C\u5199\u8BF7\u6C42\uFF09\u3002</li><li><code>Follower</code>\uFF1A\u80FD\u591F\u63A5\u6536\u5BA2\u6237\u7AEF\u7684\u8BF7\u6C42\uFF0C\u5982\u679C\u662F\u8BFB\u8BF7\u6C42\u5219\u53EF\u4EE5\u81EA\u5DF1\u5904\u7406\uFF0C<strong>\u5982\u679C\u662F\u5199\u8BF7\u6C42\u5219\u8981\u8F6C\u53D1\u7ED9 <code>Leader</code></strong> \u3002\u5728\u9009\u4E3E\u8FC7\u7A0B\u4E2D\u4F1A\u53C2\u4E0E\u6295\u7968\uFF0C<strong>\u6709\u9009\u4E3E\u6743\u548C\u88AB\u9009\u4E3E\u6743</strong> \u3002</li><li><code>Observer</code> \uFF1A\u5C31\u662F\u6CA1\u6709\u9009\u4E3E\u6743\u548C\u88AB\u9009\u4E3E\u6743\u7684 <code>Follower</code> \u3002</li></ul><p>\u5728 <code>ZAB</code> \u534F\u8BAE\u4E2D\u5BF9 <code>zkServer</code>(\u5373\u4E0A\u9762\u6211\u4EEC\u8BF4\u7684\u4E09\u4E2A\u89D2\u8272\u7684\u603B\u79F0) \u8FD8\u6709\u4E24\u79CD\u6A21\u5F0F\u7684\u5B9A\u4E49\uFF0C\u5206\u522B\u662F <strong>\u6D88\u606F\u5E7F\u64AD</strong> \u548C <strong>\u5D29\u6E83\u6062\u590D</strong> \u3002</p><h3 id="_5-3-\u6D88\u606F\u5E7F\u64AD\u6A21\u5F0F" tabindex="-1"><a class="header-anchor" href="#_5-3-\u6D88\u606F\u5E7F\u64AD\u6A21\u5F0F" aria-hidden="true">#</a> 5.3. \u6D88\u606F\u5E7F\u64AD\u6A21\u5F0F</h3><p>\u8BF4\u767D\u4E86\u5C31\u662F <code>ZAB</code> \u534F\u8BAE\u662F\u5982\u4F55\u5904\u7406\u5199\u8BF7\u6C42\u7684\uFF0C\u4E0A\u9762\u6211\u4EEC\u4E0D\u662F\u8BF4\u53EA\u6709 <code>Leader</code> \u80FD\u5904\u7406\u5199\u8BF7\u6C42\u561B\uFF1F\u90A3\u4E48\u6211\u4EEC\u7684 <code>Follower</code> \u548C <code>Observer</code> \u662F\u4E0D\u662F\u4E5F\u9700\u8981 <strong>\u540C\u6B65\u66F4\u65B0\u6570\u636E</strong> \u5462\uFF1F\u603B\u4E0D\u80FD\u6570\u636E\u53EA\u5728 <code>Leader</code> \u4E2D\u66F4\u65B0\u4E86\uFF0C\u5176\u4ED6\u89D2\u8272\u90FD\u6CA1\u6709\u5F97\u5230\u66F4\u65B0\u5427\uFF1F</p><p>\u4E0D\u5C31\u662F <strong>\u5728\u6574\u4E2A\u96C6\u7FA4\u4E2D\u4FDD\u6301\u6570\u636E\u7684\u4E00\u81F4\u6027</strong> \u561B\uFF1F\u5982\u679C\u662F\u4F60\uFF0C\u4F60\u4F1A\u600E\u4E48\u505A\u5462\uFF1F</p><p><img src="https://img-blog.csdnimg.cn/img_convert/e6064aea729dcc2d927d5d81c4797e74.png" alt="" loading="lazy"></p><p>\u5E9F\u8BDD\uFF0C\u7B2C\u4E00\u6B65\u80AF\u5B9A\u9700\u8981 <code>Leader</code> \u5C06\u5199\u8BF7\u6C42 <strong>\u5E7F\u64AD</strong> \u51FA\u53BB\u5440\uFF0C\u8BA9 <code>Leader</code> \u95EE\u95EE <code>Followers</code> \u662F\u5426\u540C\u610F\u66F4\u65B0\uFF0C\u5982\u679C\u8D85\u8FC7\u534A\u6570\u4EE5\u4E0A\u7684\u540C\u610F\u90A3\u4E48\u5C31\u8FDB\u884C <code>Follower</code> \u548C <code>Observer</code> \u7684\u66F4\u65B0\uFF08\u548C <code>Paxos</code> \u4E00\u6837\uFF09\u3002\u5F53\u7136\u8FD9\u4E48\u8BF4\u6709\u70B9\u865A\uFF0C\u753B\u5F20\u56FE\u7406\u89E3\u4E00\u4E0B\u3002</p><p><img src="https://img-blog.csdnimg.cn/img_convert/08ccce48190fe4edcbcbb223d6231876.png" alt="\u6D88\u606F\u5E7F\u64AD" loading="lazy"></p><p>\u55EF\u3002\u3002\u3002\u770B\u8D77\u6765\u5F88\u7B80\u5355\uFF0C\u8C8C\u4F3C\u61C2\u4E86\u{1F925}\u{1F925}\u{1F925}\u3002\u8FD9\u4E24\u4E2A <code>Queue</code> \u54EA\u5192\u51FA\u6765\u7684\uFF1F\u7B54\u6848\u662F <strong><code>ZAB</code> \u9700\u8981\u8BA9 <code>Follower</code> \u548C <code>Observer</code> \u4FDD\u8BC1\u987A\u5E8F\u6027</strong> \u3002\u4F55\u4E3A\u987A\u5E8F\u6027\uFF0C\u6BD4\u5982\u6211\u73B0\u5728\u6709\u4E00\u4E2A\u5199\u8BF7\u6C42A\uFF0C\u6B64\u65F6 <code>Leader</code> \u5C06\u8BF7\u6C42A\u5E7F\u64AD\u51FA\u53BB\uFF0C\u56E0\u4E3A\u53EA\u9700\u8981\u534A\u6570\u540C\u610F\u5C31\u884C\uFF0C\u6240\u4EE5\u53EF\u80FD\u8FD9\u4E2A\u65F6\u5019\u6709\u4E00\u4E2A <code>Follower</code> F1\u56E0\u4E3A\u7F51\u7EDC\u539F\u56E0\u6CA1\u6709\u6536\u5230\uFF0C\u800C <code>Leader</code> \u53C8\u5E7F\u64AD\u4E86\u4E00\u4E2A\u8BF7\u6C42B\uFF0C\u56E0\u4E3A\u7F51\u7EDC\u539F\u56E0\uFF0CF1\u7ADF\u7136\u5148\u6536\u5230\u4E86\u8BF7\u6C42B\u7136\u540E\u624D\u6536\u5230\u4E86\u8BF7\u6C42A\uFF0C\u8FD9\u4E2A\u65F6\u5019\u8BF7\u6C42\u5904\u7406\u7684\u987A\u5E8F\u4E0D\u540C\u5C31\u4F1A\u5BFC\u81F4\u6570\u636E\u7684\u4E0D\u540C\uFF0C\u4ECE\u800C <strong>\u4EA7\u751F\u6570\u636E\u4E0D\u4E00\u81F4\u95EE\u9898</strong> \u3002</p><p>\u6240\u4EE5\u5728 <code>Leader</code> \u8FD9\u7AEF\uFF0C\u5B83\u4E3A\u6BCF\u4E2A\u5176\u4ED6\u7684 <code>zkServer</code> \u51C6\u5907\u4E86\u4E00\u4E2A <strong>\u961F\u5217</strong> \uFF0C\u91C7\u7528\u5148\u8FDB\u5148\u51FA\u7684\u65B9\u5F0F\u53D1\u9001\u6D88\u606F\u3002\u7531\u4E8E\u534F\u8BAE\u662F **\u901A\u8FC7 <code>TCP</code> **\u6765\u8FDB\u884C\u7F51\u7EDC\u901A\u4FE1\u7684\uFF0C\u4FDD\u8BC1\u4E86\u6D88\u606F\u7684\u53D1\u9001\u987A\u5E8F\u6027\uFF0C\u63A5\u53D7\u987A\u5E8F\u6027\u4E5F\u5F97\u5230\u4E86\u4FDD\u8BC1\u3002</p><p>\u9664\u6B64\u4E4B\u5916\uFF0C\u5728 <code>ZAB</code> \u4E2D\u8FD8\u5B9A\u4E49\u4E86\u4E00\u4E2A <strong>\u5168\u5C40\u5355\u8C03\u9012\u589E\u7684\u4E8B\u52A1ID <code>ZXID</code></strong> \uFF0C\u5B83\u662F\u4E00\u4E2A64\u4F4Dlong\u578B\uFF0C\u5176\u4E2D\u9AD832\u4F4D\u8868\u793A <code>epoch</code> \u5E74\u4EE3\uFF0C\u4F4E32\u4F4D\u8868\u793A\u4E8B\u52A1id\u3002<code>epoch</code> \u662F\u4F1A\u6839\u636E <code>Leader</code> \u7684\u53D8\u5316\u800C\u53D8\u5316\u7684\uFF0C\u5F53\u4E00\u4E2A <code>Leader</code> \u6302\u4E86\uFF0C\u65B0\u7684 <code>Leader</code> \u4E0A\u4F4D\u7684\u65F6\u5019\uFF0C\u5E74\u4EE3\uFF08<code>epoch</code>\uFF09\u5C31\u53D8\u4E86\u3002\u800C\u4F4E32\u4F4D\u53EF\u4EE5\u7B80\u5355\u7406\u89E3\u4E3A\u9012\u589E\u7684\u4E8B\u52A1id\u3002</p><p>\u5B9A\u4E49\u8FD9\u4E2A\u7684\u539F\u56E0\u4E5F\u662F\u4E3A\u4E86\u987A\u5E8F\u6027\uFF0C\u6BCF\u4E2A <code>proposal</code> \u5728 <code>Leader</code> \u4E2D\u751F\u6210\u540E\u9700\u8981 <strong>\u901A\u8FC7\u5176 <code>ZXID</code> \u6765\u8FDB\u884C\u6392\u5E8F</strong> \uFF0C\u624D\u80FD\u5F97\u5230\u5904\u7406\u3002</p><h3 id="_5-4-\u5D29\u6E83\u6062\u590D\u6A21\u5F0F" tabindex="-1"><a class="header-anchor" href="#_5-4-\u5D29\u6E83\u6062\u590D\u6A21\u5F0F" aria-hidden="true">#</a> 5.4. \u5D29\u6E83\u6062\u590D\u6A21\u5F0F</h3><p>\u8BF4\u5230\u5D29\u6E83\u6062\u590D\u6211\u4EEC\u9996\u5148\u8981\u63D0\u5230 <code>ZAB</code> \u4E2D\u7684 <code>Leader</code> \u9009\u4E3E\u7B97\u6CD5\uFF0C\u5F53\u7CFB\u7EDF\u51FA\u73B0\u5D29\u6E83\u5F71\u54CD\u6700\u5927\u5E94\u8BE5\u662F <code>Leader</code> \u7684\u5D29\u6E83\uFF0C\u56E0\u4E3A\u6211\u4EEC\u53EA\u6709\u4E00\u4E2A <code>Leader</code> \uFF0C\u6240\u4EE5\u5F53 <code>Leader</code> \u51FA\u73B0\u95EE\u9898\u7684\u65F6\u5019\u6211\u4EEC\u52BF\u5FC5\u9700\u8981\u91CD\u65B0\u9009\u4E3E <code>Leader</code> \u3002</p><p><code>Leader</code> \u9009\u4E3E\u53EF\u4EE5\u5206\u4E3A\u4E24\u4E2A\u4E0D\u540C\u7684\u9636\u6BB5\uFF0C\u7B2C\u4E00\u4E2A\u662F\u6211\u4EEC\u63D0\u5230\u7684 <code>Leader</code> \u5B95\u673A\u9700\u8981\u91CD\u65B0\u9009\u4E3E\uFF0C\u7B2C\u4E8C\u5219\u662F\u5F53 <code>Zookeeper</code> \u542F\u52A8\u65F6\u9700\u8981\u8FDB\u884C\u7CFB\u7EDF\u7684 <code>Leader</code> \u521D\u59CB\u5316\u9009\u4E3E\u3002\u4E0B\u9762\u6211\u5148\u6765\u4ECB\u7ECD\u4E00\u4E0B <code>ZAB</code> \u662F\u5982\u4F55\u8FDB\u884C\u521D\u59CB\u5316\u9009\u4E3E\u7684\u3002</p><p>\u5047\u8BBE\u6211\u4EEC\u96C6\u7FA4\u4E2D\u67093\u53F0\u673A\u5668\uFF0C\u90A3\u4E5F\u5C31\u610F\u5473\u7740\u6211\u4EEC\u9700\u8981\u4E24\u53F0\u4EE5\u4E0A\u540C\u610F\uFF08\u8D85\u8FC7\u534A\u6570\uFF09\u3002\u6BD4\u5982\u8FD9\u4E2A\u65F6\u5019\u6211\u4EEC\u542F\u52A8\u4E86 <code>server1</code> \uFF0C\u5B83\u4F1A\u9996\u5148 <strong>\u6295\u7968\u7ED9\u81EA\u5DF1</strong> \uFF0C\u6295\u7968\u5185\u5BB9\u4E3A\u670D\u52A1\u5668\u7684 <code>myid</code> \u548C <code>ZXID</code> \uFF0C\u56E0\u4E3A\u521D\u59CB\u5316\u6240\u4EE5 <code>ZXID</code> \u90FD\u4E3A0\uFF0C\u6B64\u65F6 <code>server1</code> \u53D1\u51FA\u7684\u6295\u7968\u4E3A (1,0)\u3002\u4F46\u6B64\u65F6 <code>server1</code> \u7684\u6295\u7968\u4EC5\u4E3A1\uFF0C\u6240\u4EE5\u4E0D\u80FD\u4F5C\u4E3A <code>Leader</code> \uFF0C\u6B64\u65F6\u8FD8\u5728\u9009\u4E3E\u9636\u6BB5\u6240\u4EE5\u6574\u4E2A\u96C6\u7FA4\u5904\u4E8E <strong><code>Looking</code> \u72B6\u6001</strong>\u3002</p><p>\u63A5\u7740 <code>server2</code> \u542F\u52A8\u4E86\uFF0C\u5B83\u9996\u5148\u4E5F\u4F1A\u5C06\u6295\u7968\u9009\u7ED9\u81EA\u5DF1(2,0)\uFF0C\u5E76\u5C06\u6295\u7968\u4FE1\u606F\u5E7F\u64AD\u51FA\u53BB\uFF08<code>server1</code>\u4E5F\u4F1A\uFF0C\u53EA\u662F\u5B83\u90A3\u65F6\u6CA1\u6709\u5176\u4ED6\u7684\u670D\u52A1\u5668\u4E86\uFF09\uFF0C<code>server1</code> \u5728\u6536\u5230 <code>server2</code> \u7684\u6295\u7968\u4FE1\u606F\u540E\u4F1A\u5C06\u6295\u7968\u4FE1\u606F\u4E0E\u81EA\u5DF1\u7684\u4F5C\u6BD4\u8F83\u3002<strong>\u9996\u5148\u5B83\u4F1A\u6BD4\u8F83 <code>ZXID</code> \uFF0C<code>ZXID</code> \u5927\u7684\u4F18\u5148\u4E3A <code>Leader</code>\uFF0C\u5982\u679C\u76F8\u540C\u5219\u6BD4\u8F83 <code>myid</code>\uFF0C<code>myid</code> \u5927\u7684\u4F18\u5148\u4F5C\u4E3A <code>Leader</code></strong>\u3002\u6240\u4EE5\u6B64\u65F6<code>server1</code> \u53D1\u73B0 <code>server2</code> \u66F4\u9002\u5408\u505A <code>Leader</code>\uFF0C\u5B83\u5C31\u4F1A\u5C06\u81EA\u5DF1\u7684\u6295\u7968\u4FE1\u606F\u66F4\u6539\u4E3A(2,0)\u7136\u540E\u518D\u5E7F\u64AD\u51FA\u53BB\uFF0C\u4E4B\u540E<code>server2</code> \u6536\u5230\u4E4B\u540E\u53D1\u73B0\u548C\u81EA\u5DF1\u7684\u4E00\u6837\u65E0\u9700\u505A\u66F4\u6539\uFF0C\u5E76\u4E14\u81EA\u5DF1\u7684 <strong>\u6295\u7968\u5DF2\u7ECF\u8D85\u8FC7\u534A\u6570</strong> \uFF0C\u5219 <strong>\u786E\u5B9A <code>server2</code> \u4E3A <code>Leader</code></strong>\uFF0C<code>server1</code> \u4E5F\u4F1A\u5C06\u81EA\u5DF1\u670D\u52A1\u5668\u8BBE\u7F6E\u4E3A <code>Following</code> \u53D8\u4E3A <code>Follower</code>\u3002\u6574\u4E2A\u670D\u52A1\u5668\u5C31\u4ECE <code>Looking</code> \u53D8\u4E3A\u4E86\u6B63\u5E38\u72B6\u6001\u3002</p><p>\u5F53 <code>server3</code> \u542F\u52A8\u53D1\u73B0\u96C6\u7FA4\u6CA1\u6709\u5904\u4E8E <code>Looking</code> \u72B6\u6001\u65F6\uFF0C\u5B83\u4F1A\u76F4\u63A5\u4EE5 <code>Follower</code> \u7684\u8EAB\u4EFD\u52A0\u5165\u96C6\u7FA4\u3002</p><p>\u8FD8\u662F\u524D\u9762\u4E09\u4E2A <code>server</code> \u7684\u4F8B\u5B50\uFF0C\u5982\u679C\u5728\u6574\u4E2A\u96C6\u7FA4\u8FD0\u884C\u7684\u8FC7\u7A0B\u4E2D <code>server2</code> \u6302\u4E86\uFF0C\u90A3\u4E48\u6574\u4E2A\u96C6\u7FA4\u4F1A\u5982\u4F55\u91CD\u65B0\u9009\u4E3E <code>Leader</code> \u5462\uFF1F\u5176\u5B9E\u548C\u521D\u59CB\u5316\u9009\u4E3E\u5DEE\u4E0D\u591A\u3002</p><p>\u9996\u5148\u6BEB\u65E0\u7591\u95EE\u7684\u662F\u5269\u4E0B\u7684\u4E24\u4E2A <code>Follower</code> \u4F1A\u5C06\u81EA\u5DF1\u7684\u72B6\u6001 <strong>\u4ECE <code>Following</code> \u53D8\u4E3A <code>Looking</code> \u72B6\u6001</strong> \uFF0C\u7136\u540E\u6BCF\u4E2A <code>server</code> \u4F1A\u5411\u521D\u59CB\u5316\u6295\u7968\u4E00\u6837\u9996\u5148\u7ED9\u81EA\u5DF1\u6295\u7968\uFF08\u8FD9\u4E0D\u8FC7\u8FD9\u91CC\u7684 <code>zxid</code> \u53EF\u80FD\u4E0D\u662F0\u4E86\uFF0C\u8FD9\u91CC\u4E3A\u4E86\u65B9\u4FBF\u968F\u4FBF\u53D6\u4E2A\u6570\u5B57\uFF09\u3002</p><p>\u5047\u8BBE <code>server1</code> \u7ED9\u81EA\u5DF1\u6295\u7968\u4E3A(1,99)\uFF0C\u7136\u540E\u5E7F\u64AD\u7ED9\u5176\u4ED6 <code>server</code>\uFF0C<code>server3</code> \u9996\u5148\u4E5F\u4F1A\u7ED9\u81EA\u5DF1\u6295\u7968(3,95)\uFF0C\u7136\u540E\u4E5F\u5E7F\u64AD\u7ED9\u5176\u4ED6 <code>server</code>\u3002<code>server1</code> \u548C <code>server3</code> \u6B64\u65F6\u4F1A\u6536\u5230\u5F7C\u6B64\u7684\u6295\u7968\u4FE1\u606F\uFF0C\u548C\u4E00\u5F00\u59CB\u9009\u4E3E\u4E00\u6837\uFF0C\u4ED6\u4EEC\u4E5F\u4F1A\u6BD4\u8F83\u81EA\u5DF1\u7684\u6295\u7968\u548C\u6536\u5230\u7684\u6295\u7968\uFF08<code>zxid</code> \u5927\u7684\u4F18\u5148\uFF0C\u5982\u679C\u76F8\u540C\u90A3\u4E48\u5C31 <code>myid</code> \u5927\u7684\u4F18\u5148\uFF09\u3002\u8FD9\u4E2A\u65F6\u5019 <code>server1</code> \u6536\u5230\u4E86 <code>server3</code> \u7684\u6295\u7968\u53D1\u73B0\u6CA1\u81EA\u5DF1\u7684\u5408\u9002\u6545\u4E0D\u53D8\uFF0C<code>server3</code> \u6536\u5230 <code>server1</code> \u7684\u6295\u7968\u7ED3\u679C\u540E\u53D1\u73B0\u6BD4\u81EA\u5DF1\u7684\u5408\u9002\u4E8E\u662F\u66F4\u6539\u6295\u7968\u4E3A(1,99)\u7136\u540E\u5E7F\u64AD\u51FA\u53BB\uFF0C\u6700\u540E <code>server1</code> \u6536\u5230\u4E86\u53D1\u73B0\u81EA\u5DF1\u7684\u6295\u7968\u5DF2\u7ECF\u8D85\u8FC7\u534A\u6570\u5C31\u628A\u81EA\u5DF1\u8BBE\u4E3A <code>Leader</code>\uFF0C<code>server3</code> \u4E5F\u968F\u4E4B\u53D8\u4E3A <code>Follower</code>\u3002</p><blockquote><p>\u8BF7\u6CE8\u610F <code>ZooKeeper</code> \u4E3A\u4EC0\u4E48\u8981\u8BBE\u7F6E\u5947\u6570\u4E2A\u7ED3\u70B9\uFF1F\u6BD4\u5982\u8FD9\u91CC\u6211\u4EEC\u662F\u4E09\u4E2A\uFF0C\u6302\u4E86\u4E00\u4E2A\u6211\u4EEC\u8FD8\u80FD\u6B63\u5E38\u5DE5\u4F5C\uFF0C\u6302\u4E86\u4E24\u4E2A\u6211\u4EEC\u5C31\u4E0D\u80FD\u6B63\u5E38\u5DE5\u4F5C\u4E86\uFF08\u5DF2\u7ECF\u6CA1\u6709\u8D85\u8FC7\u534A\u6570\u7684\u8282\u70B9\u6570\u4E86\uFF0C\u6240\u4EE5\u65E0\u6CD5\u8FDB\u884C\u6295\u7968\u7B49\u64CD\u4F5C\u4E86\uFF09\u3002\u800C\u5047\u8BBE\u6211\u4EEC\u73B0\u5728\u6709\u56DB\u4E2A\uFF0C\u6302\u4E86\u4E00\u4E2A\u4E5F\u80FD\u5DE5\u4F5C\uFF0C<strong>\u4F46\u662F\u6302\u4E86\u4E24\u4E2A\u4E5F\u4E0D\u80FD\u6B63\u5E38\u5DE5\u4F5C\u4E86</strong>\uFF0C\u8FD9\u662F\u548C\u4E09\u4E2A\u4E00\u6837\u7684\uFF0C\u800C\u4E09\u4E2A\u6BD4\u56DB\u4E2A\u8FD8\u5C11\u4E00\u4E2A\uFF0C\u5E26\u6765\u7684\u6548\u76CA\u662F\u4E00\u6837\u7684\uFF0C\u6240\u4EE5 <code>Zookeeper</code> \u63A8\u8350\u5947\u6570\u4E2A <code>server</code> \u3002</p></blockquote><p>\u90A3\u4E48\u8BF4\u5B8C\u4E86 <code>ZAB</code> \u4E2D\u7684 <code>Leader</code> \u9009\u4E3E\u65B9\u5F0F\u4E4B\u540E\u6211\u4EEC\u518D\u6765\u4E86\u89E3\u4E00\u4E0B <strong>\u5D29\u6E83\u6062\u590D</strong> \u662F\u4EC0\u4E48\u73A9\u610F\uFF1F</p><p>\u5176\u5B9E\u4E3B\u8981\u5C31\u662F <strong>\u5F53\u96C6\u7FA4\u4E2D\u6709\u673A\u5668\u6302\u4E86\uFF0C\u6211\u4EEC\u6574\u4E2A\u96C6\u7FA4\u5982\u4F55\u4FDD\u8BC1\u6570\u636E\u4E00\u81F4\u6027\uFF1F</strong></p><p>\u5982\u679C\u53EA\u662F <code>Follower</code> \u6302\u4E86\uFF0C\u800C\u4E14\u6302\u7684\u6CA1\u8D85\u8FC7\u534A\u6570\u7684\u65F6\u5019\uFF0C\u56E0\u4E3A\u6211\u4EEC\u4E00\u5F00\u59CB\u8BB2\u4E86\u5728 <code>Leader</code> \u4E2D\u4F1A\u7EF4\u62A4\u961F\u5217\uFF0C\u6240\u4EE5\u4E0D\u7528\u62C5\u5FC3\u540E\u9762\u7684\u6570\u636E\u6CA1\u63A5\u6536\u5230\u5BFC\u81F4\u6570\u636E\u4E0D\u4E00\u81F4\u6027\u3002</p><p>\u5982\u679C <code>Leader</code> \u6302\u4E86\u90A3\u5C31\u9EBB\u70E6\u4E86\uFF0C\u6211\u4EEC\u80AF\u5B9A\u9700\u8981\u5148\u6682\u505C\u670D\u52A1\u53D8\u4E3A <code>Looking</code> \u72B6\u6001\u7136\u540E\u8FDB\u884C <code>Leader</code> \u7684\u91CD\u65B0\u9009\u4E3E\uFF08\u4E0A\u9762\u6211\u8BB2\u8FC7\u4E86\uFF09\uFF0C\u4F46\u8FD9\u4E2A\u5C31\u8981\u5206\u4E3A\u4E24\u79CD\u60C5\u51B5\u4E86\uFF0C\u5206\u522B\u662F <strong>\u786E\u4FDD\u5DF2\u7ECF\u88ABLeader\u63D0\u4EA4\u7684\u63D0\u6848\u6700\u7EC8\u80FD\u591F\u88AB\u6240\u6709\u7684Follower\u63D0\u4EA4</strong> \u548C <strong>\u8DF3\u8FC7\u90A3\u4E9B\u5DF2\u7ECF\u88AB\u4E22\u5F03\u7684\u63D0\u6848</strong> \u3002</p><p>\u786E\u4FDD\u5DF2\u7ECF\u88ABLeader\u63D0\u4EA4\u7684\u63D0\u6848\u6700\u7EC8\u80FD\u591F\u88AB\u6240\u6709\u7684Follower\u63D0\u4EA4\u662F\u4EC0\u4E48\u610F\u601D\u5462\uFF1F</p><p>\u5047\u8BBE <code>Leader (server2)</code> \u53D1\u9001 <code>commit</code> \u8BF7\u6C42\uFF08\u5FD8\u4E86\u8BF7\u770B\u4E0A\u9762\u7684\u6D88\u606F\u5E7F\u64AD\u6A21\u5F0F\uFF09\uFF0C\u4ED6\u53D1\u9001\u7ED9\u4E86 <code>server3</code>\uFF0C\u7136\u540E\u8981\u53D1\u7ED9 <code>server1</code> \u7684\u65F6\u5019\u7A81\u7136\u6302\u4E86\u3002\u8FD9\u4E2A\u65F6\u5019\u91CD\u65B0\u9009\u4E3E\u7684\u65F6\u5019\u6211\u4EEC\u5982\u679C\u628A <code>server1</code> \u4F5C\u4E3A <code>Leader</code> \u7684\u8BDD\uFF0C\u90A3\u4E48\u80AF\u5B9A\u4F1A\u4EA7\u751F\u6570\u636E\u4E0D\u4E00\u81F4\u6027\uFF0C\u56E0\u4E3A <code>server3</code> \u80AF\u5B9A\u4F1A\u63D0\u4EA4\u521A\u521A <code>server2</code> \u53D1\u9001\u7684 <code>commit</code> \u8BF7\u6C42\u7684\u63D0\u6848\uFF0C\u800C <code>server1</code> \u6839\u672C\u6CA1\u6536\u5230\u6240\u4EE5\u4F1A\u4E22\u5F03\u3002</p><p><img src="https://img-blog.csdnimg.cn/img_convert/ffcb12c6fb2bad76ac7105696655e85c.png" alt="\u5D29\u6E83\u6062\u590D" loading="lazy"></p><p>\u90A3\u600E\u4E48\u89E3\u51B3\u5462\uFF1F</p><p>\u806A\u660E\u7684\u540C\u5B66\u80AF\u5B9A\u4F1A\u8D28\u7591\uFF0C<strong>\u8FD9\u4E2A\u65F6\u5019 <code>server1</code> \u5DF2\u7ECF\u4E0D\u53EF\u80FD\u6210\u4E3A <code>Leader</code> \u4E86\uFF0C\u56E0\u4E3A <code>server1</code> \u548C <code>server3</code> \u8FDB\u884C\u6295\u7968\u9009\u4E3E\u7684\u65F6\u5019\u4F1A\u6BD4\u8F83 <code>ZXID</code> \uFF0C\u800C\u6B64\u65F6 <code>server3</code> \u7684 <code>ZXID</code> \u80AF\u5B9A\u6BD4 <code>server1</code> \u7684\u5927\u4E86</strong>\u3002(\u4E0D\u7406\u89E3\u53EF\u4EE5\u770B\u524D\u9762\u7684\u9009\u4E3E\u7B97\u6CD5)</p><p>\u90A3\u4E48\u8DF3\u8FC7\u90A3\u4E9B\u5DF2\u7ECF\u88AB\u4E22\u5F03\u7684\u63D0\u6848\u53C8\u662F\u4EC0\u4E48\u610F\u601D\u5462\uFF1F</p><p>\u5047\u8BBE <code>Leader (server2)</code> \u6B64\u65F6\u540C\u610F\u4E86\u63D0\u6848N1\uFF0C\u81EA\u8EAB\u63D0\u4EA4\u4E86\u8FD9\u4E2A\u4E8B\u52A1\u5E76\u4E14\u8981\u53D1\u9001\u7ED9\u6240\u6709 <code>Follower</code> \u8981 <code>commit</code> \u7684\u8BF7\u6C42\uFF0C\u5374\u5728\u8FD9\u4E2A\u65F6\u5019\u6302\u4E86\uFF0C\u6B64\u65F6\u80AF\u5B9A\u8981\u91CD\u65B0\u8FDB\u884C <code>Leader</code> \u7684\u9009\u4E3E\uFF0C\u6BD4\u5982\u8BF4\u6B64\u65F6\u9009 <code>server1</code> \u4E3A <code>Leader</code> \uFF08\u8FD9\u65E0\u6240\u8C13\uFF09\u3002\u4F46\u662F\u8FC7\u4E86\u4E00\u4F1A\uFF0C\u8FD9\u4E2A <strong>\u6302\u6389\u7684 <code>Leader</code> \u53C8\u91CD\u65B0\u6062\u590D\u4E86</strong> \uFF0C\u6B64\u65F6\u5B83\u80AF\u5B9A\u4F1A\u4F5C\u4E3A <code>Follower</code> \u7684\u8EAB\u4EFD\u8FDB\u5165\u96C6\u7FA4\u4E2D\uFF0C\u9700\u8981\u6CE8\u610F\u7684\u662F\u521A\u521A <code>server2</code> \u5DF2\u7ECF\u540C\u610F\u63D0\u4EA4\u4E86\u63D0\u6848N1\uFF0C\u4F46\u5176\u4ED6 <code>server</code> \u5E76\u6CA1\u6709\u6536\u5230\u5B83\u7684 <code>commit</code> \u4FE1\u606F\uFF0C\u6240\u4EE5\u5176\u4ED6 <code>server</code> \u4E0D\u53EF\u80FD\u518D\u63D0\u4EA4\u8FD9\u4E2A\u63D0\u6848N1\u4E86\uFF0C\u8FD9\u6837\u5C31\u4F1A\u51FA\u73B0\u6570\u636E\u4E0D\u4E00\u81F4\u6027\u95EE\u9898\u4E86\uFF0C\u6240\u4EE5 <strong>\u8BE5\u63D0\u6848N1\u6700\u7EC8\u9700\u8981\u88AB\u629B\u5F03\u6389</strong> \u3002</p><p><img src="https://img-blog.csdnimg.cn/img_convert/abb6efc7d4df9c82b162cbecb129a6e3.png" alt="\u5D29\u6E83\u6062\u590D" loading="lazy"></p><h2 id="_6-zookeeper\u7684\u51E0\u4E2A\u7406\u8BBA\u77E5\u8BC6" tabindex="-1"><a class="header-anchor" href="#_6-zookeeper\u7684\u51E0\u4E2A\u7406\u8BBA\u77E5\u8BC6" aria-hidden="true">#</a> 6. Zookeeper\u7684\u51E0\u4E2A\u7406\u8BBA\u77E5\u8BC6</h2><p>\u4E86\u89E3\u4E86 <code>ZAB</code> \u534F\u8BAE\u8FD8\u4E0D\u591F\uFF0C\u5B83\u4EC5\u4EC5\u662F <code>Zookeeper</code> \u5185\u90E8\u5B9E\u73B0\u7684\u4E00\u79CD\u65B9\u5F0F\uFF0C\u800C\u6211\u4EEC\u5982\u4F55\u901A\u8FC7 <code>Zookeeper</code> \u53BB\u505A\u4E00\u4E9B\u5178\u578B\u7684\u5E94\u7528\u573A\u666F\u5462\uFF1F\u6BD4\u5982\u8BF4\u96C6\u7FA4\u7BA1\u7406\uFF0C\u5206\u5E03\u5F0F\u9501\uFF0C<code>Master</code> \u9009\u4E3E\u7B49\u7B49\u3002</p><p>\u8FD9\u5C31\u6D89\u53CA\u5230\u5982\u4F55\u4F7F\u7528 <code>Zookeeper</code> \u4E86\uFF0C\u4F46\u5728\u4F7F\u7528\u4E4B\u524D\u6211\u4EEC\u8FD8\u9700\u8981\u638C\u63E1\u51E0\u4E2A\u6982\u5FF5\u3002\u6BD4\u5982 <code>Zookeeper</code> \u7684 <strong>\u6570\u636E\u6A21\u578B</strong> \u3001<strong>\u4F1A\u8BDD\u673A\u5236</strong>\u3001<strong>ACL</strong>\u3001<strong>Watcher\u673A\u5236</strong> \u7B49\u7B49\u3002</p><h3 id="_6-1-\u6570\u636E\u6A21\u578B" tabindex="-1"><a class="header-anchor" href="#_6-1-\u6570\u636E\u6A21\u578B" aria-hidden="true">#</a> 6.1. \u6570\u636E\u6A21\u578B</h3><p><code>zookeeper</code> \u6570\u636E\u5B58\u50A8\u7ED3\u6784\u4E0E\u6807\u51C6\u7684 <code>Unix</code> \u6587\u4EF6\u7CFB\u7EDF\u975E\u5E38\u76F8\u4F3C\uFF0C\u90FD\u662F\u5728\u6839\u8282\u70B9\u4E0B\u6302\u5F88\u591A\u5B50\u8282\u70B9(\u6811\u578B)\u3002\u4F46\u662F <code>zookeeper</code> \u4E2D\u6CA1\u6709\u6587\u4EF6\u7CFB\u7EDF\u4E2D\u76EE\u5F55\u4E0E\u6587\u4EF6\u7684\u6982\u5FF5\uFF0C\u800C\u662F <strong>\u4F7F\u7528\u4E86 <code>znode</code> \u4F5C\u4E3A\u6570\u636E\u8282\u70B9</strong> \u3002<code>znode</code> \u662F <code>zookeeper</code> \u4E2D\u7684\u6700\u5C0F\u6570\u636E\u5355\u5143\uFF0C\u6BCF\u4E2A <code>znode</code> \u4E0A\u90FD\u53EF\u4EE5\u4FDD\u5B58\u6570\u636E\uFF0C\u540C\u65F6\u8FD8\u53EF\u4EE5\u6302\u8F7D\u5B50\u8282\u70B9\uFF0C\u5F62\u6210\u4E00\u4E2A\u6811\u5F62\u5316\u547D\u540D\u7A7A\u95F4\u3002</p><p><img src="https://img-blog.csdnimg.cn/img_convert/8f35dba8c44c4a10d81e3395df971ce7.png" alt="zk\u6570\u636E\u6A21\u578B" loading="lazy"></p><p>\u6BCF\u4E2A <code>znode</code> \u90FD\u6709\u81EA\u5DF1\u6240\u5C5E\u7684 <strong>\u8282\u70B9\u7C7B\u578B</strong> \u548C <strong>\u8282\u70B9\u72B6\u6001</strong>\u3002</p><p>\u5176\u4E2D\u8282\u70B9\u7C7B\u578B\u53EF\u4EE5\u5206\u4E3A <strong>\u6301\u4E45\u8282\u70B9</strong>\u3001<strong>\u6301\u4E45\u987A\u5E8F\u8282\u70B9</strong>\u3001<strong>\u4E34\u65F6\u8282\u70B9</strong> \u548C <strong>\u4E34\u65F6\u987A\u5E8F\u8282\u70B9</strong>\u3002</p><ul><li>\u6301\u4E45\u8282\u70B9\uFF1A\u4E00\u65E6\u521B\u5EFA\u5C31\u4E00\u76F4\u5B58\u5728\uFF0C\u76F4\u5230\u5C06\u5176\u5220\u9664\u3002</li><li>\u6301\u4E45\u987A\u5E8F\u8282\u70B9\uFF1A\u4E00\u4E2A\u7236\u8282\u70B9\u53EF\u4EE5\u4E3A\u5176\u5B50\u8282\u70B9 <strong>\u7EF4\u62A4\u4E00\u4E2A\u521B\u5EFA\u7684\u5148\u540E\u987A\u5E8F</strong> \uFF0C\u8FD9\u4E2A\u987A\u5E8F\u4F53\u73B0\u5728 <strong>\u8282\u70B9\u540D\u79F0</strong> \u4E0A\uFF0C\u662F\u8282\u70B9\u540D\u79F0\u540E\u81EA\u52A8\u6DFB\u52A0\u4E00\u4E2A\u7531 10 \u4F4D\u6570\u5B57\u7EC4\u6210\u7684\u6570\u5B57\u4E32\uFF0C\u4ECE 0 \u5F00\u59CB\u8BA1\u6570\u3002</li><li>\u4E34\u65F6\u8282\u70B9\uFF1A\u4E34\u65F6\u8282\u70B9\u7684\u751F\u547D\u5468\u671F\u662F\u4E0E <strong>\u5BA2\u6237\u7AEF\u4F1A\u8BDD</strong> \u7ED1\u5B9A\u7684\uFF0C<strong>\u4F1A\u8BDD\u6D88\u5931\u5219\u8282\u70B9\u6D88\u5931</strong> \u3002\u4E34\u65F6\u8282\u70B9 <strong>\u53EA\u80FD\u505A\u53F6\u5B50\u8282\u70B9</strong> \uFF0C\u4E0D\u80FD\u521B\u5EFA\u5B50\u8282\u70B9\u3002</li><li>\u4E34\u65F6\u987A\u5E8F\u8282\u70B9\uFF1A\u7236\u8282\u70B9\u53EF\u4EE5\u521B\u5EFA\u4E00\u4E2A\u7EF4\u6301\u4E86\u987A\u5E8F\u7684\u4E34\u65F6\u8282\u70B9(\u548C\u524D\u9762\u7684\u6301\u4E45\u987A\u5E8F\u6027\u8282\u70B9\u4E00\u6837)\u3002</li></ul><p>\u8282\u70B9\u72B6\u6001\u4E2D\u5305\u542B\u4E86\u5F88\u591A\u8282\u70B9\u7684\u5C5E\u6027\u6BD4\u5982 <code>czxid</code> \u3001<code>mzxid</code> \u7B49\u7B49\uFF0C\u5728 <code>zookeeper</code> \u4E2D\u662F\u4F7F\u7528 <code>Stat</code> \u8FD9\u4E2A\u7C7B\u6765\u7EF4\u62A4\u7684\u3002\u4E0B\u9762\u6211\u5217\u4E3E\u4E00\u4E9B\u5C5E\u6027\u89E3\u91CA\u3002</p><ul><li><code>czxid</code>\uFF1A<code>Created ZXID</code>\uFF0C\u8BE5\u6570\u636E\u8282\u70B9\u88AB <strong>\u521B\u5EFA</strong> \u65F6\u7684\u4E8B\u52A1ID\u3002</li><li><code>mzxid</code>\uFF1A<code>Modified ZXID</code>\uFF0C\u8282\u70B9 <strong>\u6700\u540E\u4E00\u6B21\u88AB\u66F4\u65B0\u65F6</strong> \u7684\u4E8B\u52A1ID\u3002</li><li><code>ctime</code>\uFF1A<code>Created Time</code>\uFF0C\u8BE5\u8282\u70B9\u88AB\u521B\u5EFA\u7684\u65F6\u95F4\u3002</li><li><code>mtime</code>\uFF1A <code>Modified Time</code>\uFF0C\u8BE5\u8282\u70B9\u6700\u540E\u4E00\u6B21\u88AB\u4FEE\u6539\u7684\u65F6\u95F4\u3002</li><li><code>version</code>\uFF1A\u8282\u70B9\u7684\u7248\u672C\u53F7\u3002</li><li><code>cversion</code>\uFF1A<strong>\u5B50\u8282\u70B9</strong> \u7684\u7248\u672C\u53F7\u3002</li><li><code>aversion</code>\uFF1A\u8282\u70B9\u7684 <code>ACL</code> \u7248\u672C\u53F7\u3002</li><li><code>ephemeralOwner</code>\uFF1A\u521B\u5EFA\u8BE5\u8282\u70B9\u7684\u4F1A\u8BDD\u7684 <code>sessionID</code> \uFF0C\u5982\u679C\u8BE5\u8282\u70B9\u4E3A\u6301\u4E45\u8282\u70B9\uFF0C\u8BE5\u503C\u4E3A0\u3002</li><li><code>dataLength</code>\uFF1A\u8282\u70B9\u6570\u636E\u5185\u5BB9\u7684\u957F\u5EA6\u3002</li><li><code>numChildre</code>\uFF1A\u8BE5\u8282\u70B9\u7684\u5B50\u8282\u70B9\u4E2A\u6570\uFF0C\u5982\u679C\u4E3A\u4E34\u65F6\u8282\u70B9\u4E3A0\u3002</li><li><code>pzxid</code>\uFF1A\u8BE5\u8282\u70B9\u5B50\u8282\u70B9\u5217\u8868\u6700\u540E\u4E00\u6B21\u88AB\u4FEE\u6539\u65F6\u7684\u4E8B\u52A1ID\uFF0C\u6CE8\u610F\u662F\u5B50\u8282\u70B9\u7684 <strong>\u5217\u8868</strong> \uFF0C\u4E0D\u662F\u5185\u5BB9\u3002</li></ul><h3 id="_6-2-\u4F1A\u8BDD" tabindex="-1"><a class="header-anchor" href="#_6-2-\u4F1A\u8BDD" aria-hidden="true">#</a> 6.2. \u4F1A\u8BDD</h3><p>\u6211\u60F3\u8FD9\u4E2A\u5BF9\u4E8E\u540E\u7AEF\u5F00\u53D1\u7684\u670B\u53CB\u80AF\u5B9A\u4E0D\u964C\u751F\uFF0C\u4E0D\u5C31\u662F <code>session</code> \u5417\uFF1F\u53EA\u4E0D\u8FC7 <code>zk</code> \u5BA2\u6237\u7AEF\u548C\u670D\u52A1\u7AEF\u662F\u901A\u8FC7 <strong><code>TCP</code> \u957F\u8FDE\u63A5</strong> \u7EF4\u6301\u7684\u4F1A\u8BDD\u673A\u5236\uFF0C\u5176\u5B9E\u5BF9\u4E8E\u4F1A\u8BDD\u6765\u8BF4\u4F60\u53EF\u4EE5\u7406\u89E3\u4E3A <strong>\u4FDD\u6301\u8FDE\u63A5\u72B6\u6001</strong> \u3002</p><p>\u5728 <code>zookeeper</code> \u4E2D\uFF0C\u4F1A\u8BDD\u8FD8\u6709\u5BF9\u5E94\u7684\u4E8B\u4EF6\uFF0C\u6BD4\u5982 <code>CONNECTION_LOSS \u8FDE\u63A5\u4E22\u5931\u4E8B\u4EF6</code> \u3001<code>SESSION_MOVED \u4F1A\u8BDD\u8F6C\u79FB\u4E8B\u4EF6</code> \u3001<code>SESSION_EXPIRED \u4F1A\u8BDD\u8D85\u65F6\u5931\u6548\u4E8B\u4EF6</code> \u3002</p><h3 id="_6-3-acl" tabindex="-1"><a class="header-anchor" href="#_6-3-acl" aria-hidden="true">#</a> 6.3. ACL</h3><p><code>ACL</code> \u4E3A <code>Access Control Lists</code> \uFF0C\u5B83\u662F\u4E00\u79CD\u6743\u9650\u63A7\u5236\u3002\u5728 <code>zookeeper</code> \u4E2D\u5B9A\u4E49\u4E865\u79CD\u6743\u9650\uFF0C\u5B83\u4EEC\u5206\u522B\u4E3A\uFF1A</p><ul><li><code>CREATE</code> \uFF1A\u521B\u5EFA\u5B50\u8282\u70B9\u7684\u6743\u9650\u3002</li><li><code>READ</code>\uFF1A\u83B7\u53D6\u8282\u70B9\u6570\u636E\u548C\u5B50\u8282\u70B9\u5217\u8868\u7684\u6743\u9650\u3002</li><li><code>WRITE</code>\uFF1A\u66F4\u65B0\u8282\u70B9\u6570\u636E\u7684\u6743\u9650\u3002</li><li><code>DELETE</code>\uFF1A\u5220\u9664\u5B50\u8282\u70B9\u7684\u6743\u9650\u3002</li><li><code>ADMIN</code>\uFF1A\u8BBE\u7F6E\u8282\u70B9 ACL \u7684\u6743\u9650\u3002</li></ul><h3 id="_6-4-watcher\u673A\u5236" tabindex="-1"><a class="header-anchor" href="#_6-4-watcher\u673A\u5236" aria-hidden="true">#</a> 6.4. Watcher\u673A\u5236</h3><p><code>Watcher</code> \u4E3A\u4E8B\u4EF6\u76D1\u542C\u5668\uFF0C\u662F <code>zk</code> \u975E\u5E38\u91CD\u8981\u7684\u4E00\u4E2A\u7279\u6027\uFF0C\u5F88\u591A\u529F\u80FD\u90FD\u4F9D\u8D56\u4E8E\u5B83\uFF0C\u5B83\u6709\u70B9\u7C7B\u4F3C\u4E8E\u8BA2\u9605\u7684\u65B9\u5F0F\uFF0C\u5373\u5BA2\u6237\u7AEF\u5411\u670D\u52A1\u7AEF <strong>\u6CE8\u518C</strong> \u6307\u5B9A\u7684 <code>watcher</code> \uFF0C\u5F53\u670D\u52A1\u7AEF\u7B26\u5408\u4E86 <code>watcher</code> \u7684\u67D0\u4E9B\u4E8B\u4EF6\u6216\u8981\u6C42\u5219\u4F1A <strong>\u5411\u5BA2\u6237\u7AEF\u53D1\u9001\u4E8B\u4EF6\u901A\u77E5</strong> \uFF0C\u5BA2\u6237\u7AEF\u6536\u5230\u901A\u77E5\u540E\u627E\u5230\u81EA\u5DF1\u5B9A\u4E49\u7684 <code>Watcher</code> \u7136\u540E <strong>\u6267\u884C\u76F8\u5E94\u7684\u56DE\u8C03\u65B9\u6CD5</strong> \u3002</p><p><img src="https://img-blog.csdnimg.cn/img_convert/241ab8cc37571034fa984322b753c7ba.png" alt="watcher\u673A\u5236" loading="lazy"></p><h2 id="_7-zookeeper\u7684\u51E0\u4E2A\u5178\u578B\u5E94\u7528\u573A\u666F" tabindex="-1"><a class="header-anchor" href="#_7-zookeeper\u7684\u51E0\u4E2A\u5178\u578B\u5E94\u7528\u573A\u666F" aria-hidden="true">#</a> 7. Zookeeper\u7684\u51E0\u4E2A\u5178\u578B\u5E94\u7528\u573A\u666F</h2><p>\u524D\u9762\u8BF4\u4E86\u8FD9\u4E48\u591A\u7684\u7406\u8BBA\u77E5\u8BC6\uFF0C\u4F60\u53EF\u80FD\u542C\u5F97\u4E00\u5934\u96FE\u6C34\uFF0C\u8FD9\u4E9B\u73A9\u610F\u6709\u5565\u7528\uFF1F\u80FD\u5E72\u5565\u4E8B\uFF1F\u522B\u6025\uFF0C\u542C\u6211\u6162\u6162\u9053\u6765\u3002</p><p><img src="https://img-blog.csdnimg.cn/img_convert/9c9bd2a892e23e0b7582370c50117d8c.png" alt="" loading="lazy"></p><h3 id="_7-1-\u9009\u4E3B" tabindex="-1"><a class="header-anchor" href="#_7-1-\u9009\u4E3B" aria-hidden="true">#</a> 7.1. \u9009\u4E3B</h3><p>\u8FD8\u8BB0\u5F97\u4E0A\u9762\u6211\u4EEC\u7684\u6240\u8BF4\u7684\u4E34\u65F6\u8282\u70B9\u5417\uFF1F\u56E0\u4E3A <code>Zookeeper</code> \u7684\u5F3A\u4E00\u81F4\u6027\uFF0C\u80FD\u591F\u5F88\u597D\u5730\u5728\u4FDD\u8BC1 <strong>\u5728\u9AD8\u5E76\u53D1\u7684\u60C5\u51B5\u4E0B\u4FDD\u8BC1\u8282\u70B9\u521B\u5EFA\u7684\u5168\u5C40\u552F\u4E00\u6027</strong> (\u5373\u65E0\u6CD5\u91CD\u590D\u521B\u5EFA\u540C\u6837\u7684\u8282\u70B9)\u3002</p><p>\u5229\u7528\u8FD9\u4E2A\u7279\u6027\uFF0C\u6211\u4EEC\u53EF\u4EE5 <strong>\u8BA9\u591A\u4E2A\u5BA2\u6237\u7AEF\u521B\u5EFA\u4E00\u4E2A\u6307\u5B9A\u7684\u8282\u70B9</strong> \uFF0C\u521B\u5EFA\u6210\u529F\u7684\u5C31\u662F <code>master</code>\u3002</p><p>\u4F46\u662F\uFF0C\u5982\u679C\u8FD9\u4E2A <code>master</code> \u6302\u4E86\u600E\u4E48\u529E\uFF1F\uFF1F\uFF1F</p><p>\u4F60\u60F3\u60F3\u4E3A\u4EC0\u4E48\u6211\u4EEC\u8981\u521B\u5EFA\u4E34\u65F6\u8282\u70B9\uFF1F\u8FD8\u8BB0\u5F97\u4E34\u65F6\u8282\u70B9\u7684\u751F\u547D\u5468\u671F\u5417\uFF1F<code>master</code> \u6302\u4E86\u662F\u4E0D\u662F\u4EE3\u8868\u4F1A\u8BDD\u65AD\u4E86\uFF1F\u4F1A\u8BDD\u65AD\u4E86\u662F\u4E0D\u662F\u610F\u5473\u7740\u8FD9\u4E2A\u8282\u70B9\u6CA1\u4E86\uFF1F\u8FD8\u8BB0\u5F97 <code>watcher</code> \u5417\uFF1F\u6211\u4EEC\u662F\u4E0D\u662F\u53EF\u4EE5 <strong>\u8BA9\u5176\u4ED6\u4E0D\u662F <code>master</code> \u7684\u8282\u70B9\u76D1\u542C\u8282\u70B9\u7684\u72B6\u6001</strong> \uFF0C\u6BD4\u5982\u8BF4\u6211\u4EEC\u76D1\u542C\u8FD9\u4E2A\u4E34\u65F6\u8282\u70B9\u7684\u7236\u8282\u70B9\uFF0C\u5982\u679C\u5B50\u8282\u70B9\u4E2A\u6570\u53D8\u4E86\u5C31\u4EE3\u8868 <code>master</code> \u6302\u4E86\uFF0C\u8FD9\u4E2A\u65F6\u5019\u6211\u4EEC <strong>\u89E6\u53D1\u56DE\u8C03\u51FD\u6570\u8FDB\u884C\u91CD\u65B0\u9009\u4E3E</strong> \uFF0C\u6216\u8005\u6211\u4EEC\u76F4\u63A5\u76D1\u542C\u8282\u70B9\u7684\u72B6\u6001\uFF0C\u6211\u4EEC\u53EF\u4EE5\u901A\u8FC7\u8282\u70B9\u662F\u5426\u5DF2\u7ECF\u5931\u53BB\u8FDE\u63A5\u6765\u5224\u65AD <code>master</code> \u662F\u5426\u6302\u4E86\u7B49\u7B49\u3002</p><p><img src="https://img-blog.csdnimg.cn/img_convert/a94707028c5581c815f72fba0f50f43a.png" alt="\u9009\u4E3B" loading="lazy"></p><p>\u603B\u7684\u6765\u8BF4\uFF0C\u6211\u4EEC\u53EF\u4EE5\u5B8C\u5168 <strong>\u5229\u7528 \u4E34\u65F6\u8282\u70B9\u3001\u8282\u70B9\u72B6\u6001 \u548C <code>watcher</code> \u6765\u5B9E\u73B0\u9009\u4E3B\u7684\u529F\u80FD</strong>\uFF0C\u4E34\u65F6\u8282\u70B9\u4E3B\u8981\u7528\u6765\u9009\u4E3E\uFF0C\u8282\u70B9\u72B6\u6001\u548C<code>watcher</code> \u53EF\u4EE5\u7528\u6765\u5224\u65AD <code>master</code> \u7684\u6D3B\u6027\u548C\u8FDB\u884C\u91CD\u65B0\u9009\u4E3E\u3002</p><h3 id="_7-2-\u5206\u5E03\u5F0F\u9501" tabindex="-1"><a class="header-anchor" href="#_7-2-\u5206\u5E03\u5F0F\u9501" aria-hidden="true">#</a> 7.2. \u5206\u5E03\u5F0F\u9501</h3><p>\u5206\u5E03\u5F0F\u9501\u7684\u5B9E\u73B0\u65B9\u5F0F\u6709\u5F88\u591A\u79CD\uFF0C\u6BD4\u5982 <code>Redis</code> \u3001\u6570\u636E\u5E93 \u3001<code>zookeeper</code> \u7B49\u3002\u4E2A\u4EBA\u8BA4\u4E3A <code>zookeeper</code> \u5728\u5B9E\u73B0\u5206\u5E03\u5F0F\u9501\u8FD9\u65B9\u9762\u662F\u975E\u5E38\u975E\u5E38\u7B80\u5355\u7684\u3002</p><p>\u4E0A\u9762\u6211\u4EEC\u5DF2\u7ECF\u63D0\u5230\u8FC7\u4E86 <strong>zk\u5728\u9AD8\u5E76\u53D1\u7684\u60C5\u51B5\u4E0B\u4FDD\u8BC1\u8282\u70B9\u521B\u5EFA\u7684\u5168\u5C40\u552F\u4E00\u6027</strong>\uFF0C\u8FD9\u73A9\u610F\u4E00\u770B\u5C31\u77E5\u9053\u80FD\u5E72\u5565\u4E86\u3002\u5B9E\u73B0\u4E92\u65A5\u9501\u5457\uFF0C\u53C8\u56E0\u4E3A\u80FD\u5728\u5206\u5E03\u5F0F\u7684\u60C5\u51B5\u4E0B\uFF0C\u6240\u4EE5\u80FD\u5B9E\u73B0\u5206\u5E03\u5F0F\u9501\u5457\u3002</p><p>\u5982\u4F55\u5B9E\u73B0\u5462\uFF1F\u8FD9\u73A9\u610F\u5176\u5B9E\u8DDF\u9009\u4E3B\u57FA\u672C\u4E00\u6837\uFF0C\u6211\u4EEC\u4E5F\u53EF\u4EE5\u5229\u7528\u4E34\u65F6\u8282\u70B9\u7684\u521B\u5EFA\u6765\u5B9E\u73B0\u3002</p><p>\u9996\u5148\u80AF\u5B9A\u662F\u5982\u4F55\u83B7\u53D6\u9501\uFF0C\u56E0\u4E3A\u521B\u5EFA\u8282\u70B9\u7684\u552F\u4E00\u6027\uFF0C\u6211\u4EEC\u53EF\u4EE5\u8BA9\u591A\u4E2A\u5BA2\u6237\u7AEF\u540C\u65F6\u521B\u5EFA\u4E00\u4E2A\u4E34\u65F6\u8282\u70B9\uFF0C<strong>\u521B\u5EFA\u6210\u529F\u7684\u5C31\u8BF4\u660E\u83B7\u53D6\u5230\u4E86\u9501</strong> \u3002\u7136\u540E\u6CA1\u6709\u83B7\u53D6\u5230\u9501\u7684\u5BA2\u6237\u7AEF\u4E5F\u50CF\u4E0A\u9762\u9009\u4E3B\u7684\u975E\u4E3B\u8282\u70B9\u521B\u5EFA\u4E00\u4E2A <code>watcher</code> \u8FDB\u884C\u8282\u70B9\u72B6\u6001\u7684\u76D1\u542C\uFF0C\u5982\u679C\u8FD9\u4E2A\u4E92\u65A5\u9501\u88AB\u91CA\u653E\u4E86\uFF08\u53EF\u80FD\u83B7\u53D6\u9501\u7684\u5BA2\u6237\u7AEF\u5B95\u673A\u4E86\uFF0C\u6216\u8005\u90A3\u4E2A\u5BA2\u6237\u7AEF\u4E3B\u52A8\u91CA\u653E\u4E86\u9501\uFF09\u53EF\u4EE5\u8C03\u7528\u56DE\u8C03\u51FD\u6570\u91CD\u65B0\u83B7\u5F97\u9501\u3002</p><blockquote><p><code>zk</code> \u4E2D\u4E0D\u9700\u8981\u5411 <code>redis</code> \u90A3\u6837\u8003\u8651\u9501\u5F97\u4E0D\u5230\u91CA\u653E\u7684\u95EE\u9898\u4E86\uFF0C\u56E0\u4E3A\u5F53\u5BA2\u6237\u7AEF\u6302\u4E86\uFF0C\u8282\u70B9\u4E5F\u6302\u4E86\uFF0C\u9501\u4E5F\u91CA\u653E\u4E86\u3002\u662F\u4E0D\u662F\u5F88\u7B80\u5355\uFF1F</p></blockquote><p>\u90A3\u80FD\u4E0D\u80FD\u4F7F\u7528 <code>zookeeper</code> \u540C\u65F6\u5B9E\u73B0 <strong>\u5171\u4EAB\u9501\u548C\u72EC\u5360\u9501</strong> \u5462\uFF1F\u7B54\u6848\u662F\u53EF\u4EE5\u7684\uFF0C\u4E0D\u8FC7\u7A0D\u5FAE\u6709\u70B9\u590D\u6742\u800C\u5DF2\u3002</p><p>\u8FD8\u8BB0\u5F97 <strong>\u6709\u5E8F\u7684\u8282\u70B9</strong> \u5417\uFF1F</p><p>\u8FD9\u4E2A\u65F6\u5019\u6211\u89C4\u5B9A\u6240\u6709\u521B\u5EFA\u8282\u70B9\u5FC5\u987B\u6709\u5E8F\uFF0C\u5F53\u4F60\u662F\u8BFB\u8BF7\u6C42\uFF08\u8981\u83B7\u53D6\u5171\u4EAB\u9501\uFF09\u7684\u8BDD\uFF0C\u5982\u679C <strong>\u6CA1\u6709\u6BD4\u81EA\u5DF1\u66F4\u5C0F\u7684\u8282\u70B9\uFF0C\u6216\u6BD4\u81EA\u5DF1\u5C0F\u7684\u8282\u70B9\u90FD\u662F\u8BFB\u8BF7\u6C42</strong> \uFF0C\u5219\u53EF\u4EE5\u83B7\u53D6\u5230\u8BFB\u9501\uFF0C\u7136\u540E\u5C31\u53EF\u4EE5\u5F00\u59CB\u8BFB\u4E86\u3002<strong>\u82E5\u6BD4\u81EA\u5DF1\u5C0F\u7684\u8282\u70B9\u4E2D\u6709\u5199\u8BF7\u6C42</strong> \uFF0C\u5219\u5F53\u524D\u5BA2\u6237\u7AEF\u65E0\u6CD5\u83B7\u53D6\u5230\u8BFB\u9501\uFF0C\u53EA\u80FD\u7B49\u5F85\u524D\u9762\u7684\u5199\u8BF7\u6C42\u5B8C\u6210\u3002</p><p>\u5982\u679C\u4F60\u662F\u5199\u8BF7\u6C42\uFF08\u83B7\u53D6\u72EC\u5360\u9501\uFF09\uFF0C\u82E5 <strong>\u6CA1\u6709\u6BD4\u81EA\u5DF1\u66F4\u5C0F\u7684\u8282\u70B9</strong> \uFF0C\u5219\u8868\u793A\u5F53\u524D\u5BA2\u6237\u7AEF\u53EF\u4EE5\u76F4\u63A5\u83B7\u53D6\u5230\u5199\u9501\uFF0C\u5BF9\u6570\u636E\u8FDB\u884C\u4FEE\u6539\u3002\u82E5\u53D1\u73B0 <strong>\u6709\u6BD4\u81EA\u5DF1\u66F4\u5C0F\u7684\u8282\u70B9\uFF0C\u65E0\u8BBA\u662F\u8BFB\u64CD\u4F5C\u8FD8\u662F\u5199\u64CD\u4F5C\uFF0C\u5F53\u524D\u5BA2\u6237\u7AEF\u90FD\u65E0\u6CD5\u83B7\u53D6\u5230\u5199\u9501</strong> \uFF0C\u7B49\u5F85\u6240\u6709\u524D\u9762\u7684\u64CD\u4F5C\u5B8C\u6210\u3002</p><p>\u8FD9\u5C31\u5F88\u597D\u5730\u540C\u65F6\u5B9E\u73B0\u4E86\u5171\u4EAB\u9501\u548C\u72EC\u5360\u9501\uFF0C\u5F53\u7136\u8FD8\u6709\u4F18\u5316\u7684\u5730\u65B9\uFF0C\u6BD4\u5982\u5F53\u4E00\u4E2A\u9501\u5F97\u5230\u91CA\u653E\u5B83\u4F1A\u901A\u77E5\u6240\u6709\u7B49\u5F85\u7684\u5BA2\u6237\u7AEF\u4ECE\u800C\u9020\u6210 <strong>\u7F8A\u7FA4\u6548\u5E94</strong> \u3002\u6B64\u65F6\u4F60\u53EF\u4EE5\u901A\u8FC7\u8BA9\u7B49\u5F85\u7684\u8282\u70B9\u53EA\u76D1\u542C\u4ED6\u4EEC\u524D\u9762\u7684\u8282\u70B9\u3002</p><p>\u5177\u4F53\u600E\u4E48\u505A\u5462\uFF1F\u5176\u5B9E\u4E5F\u5F88\u7B80\u5355\uFF0C\u4F60\u53EF\u4EE5\u8BA9 <strong>\u8BFB\u8BF7\u6C42\u76D1\u542C\u6BD4\u81EA\u5DF1\u5C0F\u7684\u6700\u540E\u4E00\u4E2A\u5199\u8BF7\u6C42\u8282\u70B9\uFF0C\u5199\u8BF7\u6C42\u53EA\u76D1\u542C\u6BD4\u81EA\u5DF1\u5C0F\u7684\u6700\u540E\u4E00\u4E2A\u8282\u70B9</strong> \uFF0C\u611F\u5174\u8DA3\u7684\u5C0F\u4F19\u4F34\u53EF\u4EE5\u81EA\u5DF1\u53BB\u7814\u7A76\u4E00\u4E0B\u3002</p><h3 id="_7-3-\u547D\u540D\u670D\u52A1" tabindex="-1"><a class="header-anchor" href="#_7-3-\u547D\u540D\u670D\u52A1" aria-hidden="true">#</a> 7.3. \u547D\u540D\u670D\u52A1</h3><p>\u5982\u4F55\u7ED9\u4E00\u4E2A\u5BF9\u8C61\u8BBE\u7F6EID\uFF0C\u5927\u5BB6\u53EF\u80FD\u90FD\u4F1A\u60F3\u5230 <code>UUID</code>\uFF0C\u4F46\u662F <code>UUID</code> \u6700\u5927\u7684\u95EE\u9898\u5C31\u5728\u4E8E\u5B83\u592A\u957F\u4E86\u3002\u3002\u3002(\u592A\u957F\u4E0D\u4E00\u5B9A\u662F\u597D\u4E8B\uFF0C\u563F\u563F\u563F)\u3002\u90A3\u4E48\u5728\u6761\u4EF6\u5141\u8BB8\u7684\u60C5\u51B5\u4E0B\uFF0C\u6211\u4EEC\u80FD\u4E0D\u80FD\u4F7F\u7528 <code>zookeeper</code> \u6765\u5B9E\u73B0\u5462\uFF1F</p><p>\u6211\u4EEC\u4E4B\u524D\u63D0\u5230\u8FC7 <code>zookeeper</code> \u662F\u901A\u8FC7 <strong>\u6811\u5F62\u7ED3\u6784</strong> \u6765\u5B58\u50A8\u6570\u636E\u8282\u70B9\u7684\uFF0C\u90A3\u4E5F\u5C31\u662F\u8BF4\uFF0C\u5BF9\u4E8E\u6BCF\u4E2A\u8282\u70B9\u7684 <strong>\u5168\u8DEF\u5F84</strong>\uFF0C\u5B83\u5FC5\u5B9A\u662F\u552F\u4E00\u7684\uFF0C\u6211\u4EEC\u53EF\u4EE5\u4F7F\u7528\u8282\u70B9\u7684\u5168\u8DEF\u5F84\u4F5C\u4E3A\u547D\u540D\u65B9\u5F0F\u4E86\u3002\u800C\u4E14\u66F4\u91CD\u8981\u7684\u662F\uFF0C\u8DEF\u5F84\u662F\u6211\u4EEC\u53EF\u4EE5\u81EA\u5DF1\u5B9A\u4E49\u7684\uFF0C\u8FD9\u5BF9\u4E8E\u6211\u4EEC\u5BF9\u6709\u4E9B\u6709\u8BED\u610F\u7684\u5BF9\u8C61\u7684ID\u8BBE\u7F6E\u53EF\u4EE5\u66F4\u52A0\u4FBF\u4E8E\u7406\u89E3\u3002</p><h3 id="_7-4-\u96C6\u7FA4\u7BA1\u7406\u548C\u6CE8\u518C\u4E2D\u5FC3" tabindex="-1"><a class="header-anchor" href="#_7-4-\u96C6\u7FA4\u7BA1\u7406\u548C\u6CE8\u518C\u4E2D\u5FC3" aria-hidden="true">#</a> 7.4. \u96C6\u7FA4\u7BA1\u7406\u548C\u6CE8\u518C\u4E2D\u5FC3</h3><p>\u770B\u5230\u8FD9\u91CC\u662F\u4E0D\u662F\u89C9\u5F97 <code>zookeeper</code> \u5B9E\u5728\u662F\u592A\u5F3A\u5927\u4E86\uFF0C\u5B83\u600E\u4E48\u80FD\u8FD9\u4E48\u80FD\u5E72\uFF01</p><p>\u522B\u6025\uFF0C\u5B83\u80FD\u5E72\u7684\u4E8B\u60C5\u8FD8\u5F88\u591A\u5462\u3002\u53EF\u80FD\u6211\u4EEC\u4F1A\u6709\u8FD9\u6837\u7684\u9700\u6C42\uFF0C\u6211\u4EEC\u9700\u8981\u4E86\u89E3\u6574\u4E2A\u96C6\u7FA4\u4E2D\u6709\u591A\u5C11\u673A\u5668\u5728\u5DE5\u4F5C\uFF0C\u6211\u4EEC\u60F3\u5BF9\u96C6\u7FA4\u4E2D\u7684\u6BCF\u53F0\u673A\u5668\u7684\u8FD0\u884C\u65F6\u72B6\u6001\u8FDB\u884C\u6570\u636E\u91C7\u96C6\uFF0C\u5BF9\u96C6\u7FA4\u4E2D\u673A\u5668\u8FDB\u884C\u4E0A\u4E0B\u7EBF\u64CD\u4F5C\u7B49\u7B49\u3002</p><p>\u800C <code>zookeeper</code> \u5929\u7136\u652F\u6301\u7684 <code>watcher</code> \u548C \u4E34\u65F6\u8282\u70B9\u80FD\u5F88\u597D\u7684\u5B9E\u73B0\u8FD9\u4E9B\u9700\u6C42\u3002\u6211\u4EEC\u53EF\u4EE5\u4E3A\u6BCF\u6761\u673A\u5668\u521B\u5EFA\u4E34\u65F6\u8282\u70B9\uFF0C\u5E76\u76D1\u63A7\u5176\u7236\u8282\u70B9\uFF0C\u5982\u679C\u5B50\u8282\u70B9\u5217\u8868\u6709\u53D8\u52A8\uFF08\u6211\u4EEC\u53EF\u80FD\u521B\u5EFA\u5220\u9664\u4E86\u4E34\u65F6\u8282\u70B9\uFF09\uFF0C\u90A3\u4E48\u6211\u4EEC\u53EF\u4EE5\u4F7F\u7528\u5728\u5176\u7236\u8282\u70B9\u7ED1\u5B9A\u7684 <code>watcher</code> \u8FDB\u884C\u72B6\u6001\u76D1\u63A7\u548C\u56DE\u8C03\u3002</p><p><img src="https://img-blog.csdnimg.cn/img_convert/6115820219c35c68bcb2c9a855ebace3.png" alt="\u96C6\u7FA4\u7BA1\u7406" loading="lazy"></p><p>\u81F3\u4E8E\u6CE8\u518C\u4E2D\u5FC3\u4E5F\u5F88\u7B80\u5355\uFF0C\u6211\u4EEC\u540C\u6837\u4E5F\u662F\u8BA9 <strong>\u670D\u52A1\u63D0\u4F9B\u8005</strong> \u5728 <code>zookeeper</code> \u4E2D\u521B\u5EFA\u4E00\u4E2A\u4E34\u65F6\u8282\u70B9\u5E76\u4E14\u5C06\u81EA\u5DF1\u7684 <code>ip\u3001port\u3001\u8C03\u7528\u65B9\u5F0F</code> \u5199\u5165\u8282\u70B9\uFF0C\u5F53 <strong>\u670D\u52A1\u6D88\u8D39\u8005</strong> \u9700\u8981\u8FDB\u884C\u8C03\u7528\u7684\u65F6\u5019\u4F1A <strong>\u901A\u8FC7\u6CE8\u518C\u4E2D\u5FC3\u627E\u5230\u76F8\u5E94\u7684\u670D\u52A1\u7684\u5730\u5740\u5217\u8868(IP\u7AEF\u53E3\u4EC0\u4E48\u7684)</strong> \uFF0C\u5E76\u7F13\u5B58\u5230\u672C\u5730(\u65B9\u4FBF\u4EE5\u540E\u8C03\u7528)\uFF0C\u5F53\u6D88\u8D39\u8005\u8C03\u7528\u670D\u52A1\u65F6\uFF0C\u4E0D\u4F1A\u518D\u53BB\u8BF7\u6C42\u6CE8\u518C\u4E2D\u5FC3\uFF0C\u800C\u662F\u76F4\u63A5\u901A\u8FC7\u8D1F\u8F7D\u5747\u8861\u7B97\u6CD5\u4ECE\u5730\u5740\u5217\u8868\u4E2D\u53D6\u4E00\u4E2A\u670D\u52A1\u63D0\u4F9B\u8005\u7684\u670D\u52A1\u5668\u8C03\u7528\u670D\u52A1\u3002</p><p>\u5F53\u670D\u52A1\u63D0\u4F9B\u8005\u7684\u67D0\u53F0\u670D\u52A1\u5668\u5B95\u673A\u6216\u4E0B\u7EBF\u65F6\uFF0C\u76F8\u5E94\u7684\u5730\u5740\u4F1A\u4ECE\u670D\u52A1\u63D0\u4F9B\u8005\u5730\u5740\u5217\u8868\u4E2D\u79FB\u9664\u3002\u540C\u65F6\uFF0C\u6CE8\u518C\u4E2D\u5FC3\u4F1A\u5C06\u65B0\u7684\u670D\u52A1\u5730\u5740\u5217\u8868\u53D1\u9001\u7ED9\u670D\u52A1\u6D88\u8D39\u8005\u7684\u673A\u5668\u5E76\u7F13\u5B58\u5728\u6D88\u8D39\u8005\u672C\u673A\uFF08\u5F53\u7136\u4F60\u53EF\u4EE5\u8BA9\u6D88\u8D39\u8005\u8FDB\u884C\u8282\u70B9\u76D1\u542C\uFF0C\u6211\u8BB0\u5F97 <code>Eureka</code> \u4F1A\u5148\u8BD5\u9519\uFF0C\u7136\u540E\u518D\u66F4\u65B0\uFF09\u3002</p><p><img src="https://img-blog.csdnimg.cn/img_convert/0b5b3911a7c2dae23391d17c91416b29.png" alt="\u6CE8\u518C\u4E2D\u5FC3" loading="lazy"></p><h2 id="_8-\u603B\u7ED3" tabindex="-1"><a class="header-anchor" href="#_8-\u603B\u7ED3" aria-hidden="true">#</a> 8. \u603B\u7ED3</h2><p>\u770B\u5230\u8FD9\u91CC\u7684\u540C\u5B66\u5B9E\u5728\u662F\u592A\u6709\u8010\u5FC3\u4E86\u{1F44D}\u{1F44D}\u{1F44D}\uFF0C\u5982\u679C\u89C9\u5F97\u6211\u5199\u5F97\u4E0D\u9519\u7684\u8BDD\u70B9\u4E2A\u8D5E\u54C8\u3002</p><p>\u4E0D\u77E5\u9053\u5927\u5BB6\u662F\u5426\u8FD8\u8BB0\u5F97\u6211\u8BB2\u4E86\u4EC0\u4E48\u{1F612}\u3002</p><p><img src="https://img-blog.csdnimg.cn/img_convert/b0bde9f3979b8f19187e6c374ad98993.png" alt="" loading="lazy"></p><p>\u8FD9\u7BC7\u6587\u7AE0\u4E2D\u6211\u5E26\u5927\u5BB6\u5165\u95E8\u4E86 <code>zookeeper</code> \u8FD9\u4E2A\u5F3A\u5927\u7684\u5206\u5E03\u5F0F\u534F\u8C03\u6846\u67B6\u3002\u73B0\u5728\u6211\u4EEC\u6765\u7B80\u5355\u68B3\u7406\u4E00\u4E0B\u6574\u7BC7\u6587\u7AE0\u7684\u5185\u5BB9\u3002</p><ul><li><p>\u5206\u5E03\u5F0F\u4E0E\u96C6\u7FA4\u7684\u533A\u522B</p></li><li><p><code>2PC</code> \u3001<code>3PC</code> \u4EE5\u53CA <code>paxos</code> \u7B97\u6CD5\u8FD9\u4E9B\u4E00\u81F4\u6027\u6846\u67B6\u7684\u539F\u7406\u548C\u5B9E\u73B0\u3002</p></li><li><p><code>zookeeper</code> \u4E13\u95E8\u7684\u4E00\u81F4\u6027\u7B97\u6CD5 <code>ZAB</code> \u539F\u5B50\u5E7F\u64AD\u534F\u8BAE\u7684\u5185\u5BB9\uFF08<code>Leader</code> \u9009\u4E3E\u3001\u5D29\u6E83\u6062\u590D\u3001\u6D88\u606F\u5E7F\u64AD\uFF09\u3002</p></li><li><p><code>zookeeper</code> \u4E2D\u7684\u4E00\u4E9B\u57FA\u672C\u6982\u5FF5\uFF0C\u6BD4\u5982 <code>ACL</code>\uFF0C\u6570\u636E\u8282\u70B9\uFF0C\u4F1A\u8BDD\uFF0C<code>watcher</code>\u673A\u5236\u7B49\u7B49\u3002</p></li><li><p><code>zookeeper</code> \u7684\u5178\u578B\u5E94\u7528\u573A\u666F\uFF0C\u6BD4\u5982\u9009\u4E3B\uFF0C\u6CE8\u518C\u4E2D\u5FC3\u7B49\u7B49\u3002</p><p>\u5982\u679C\u5FD8\u4E86\u53EF\u4EE5\u56DE\u53BB\u770B\u770B\u518D\u6B21\u7406\u89E3\u4E00\u4E0B\uFF0C\u5982\u679C\u6709\u7591\u95EE\u548C\u5EFA\u8BAE\u6B22\u8FCE\u63D0\u51FA\u{1F91D}\u{1F91D}\u{1F91D}\u3002</p></li></ul>',169);function b(_,f){const c=n("ExternalLinkIcon");return r(),t("div",null,[g,e("blockquote",null,[e("p",null,[e("a",i,[l,a(c)]),h])]),m])}var k=d(p,[["render",b],["__file","zookeeper-plus.html.vue"]]);export{k as default};
