import * as vue from 'vue';
import { VNode } from 'vue';
import { LocaleConfig } from '@vuepress/core';
import { WalineInitOptions, WalineLocale } from '@waline/client';
import { Author, ConvertLocaleConfig } from 'vuepress-shared';

interface BaseCommentOptions {
    /**
     * Default author
     *
     * 默认作者
     */
    author?: Author;
    /**
     * Whether enable comment by default
     *
     * 是否默认启用评论
     *
     * @default true
     */
    comment?: boolean;
    /**
     * The delay of dom operation, in ms
     *
     * If the theme you are using has a switching animation, it is recommended to configure this option to `Switch animation duration + 200`
     *
     * 进行 DOM 操作的延时，单位 ms
     *
     * 如果你使用的主题有切换动画，建议配置此选项为 `切换动画时长 + 200`
     *
     * @default 500
     */
    delay?: number;
}

declare type WalineLocaleData = Partial<WalineLocale>;
declare type WalineLocaleConfig = ConvertLocaleConfig<WalineLocaleData>;
interface WalineOptions extends BaseCommentOptions, Omit<WalineInitOptions, "el" | "comment"> {
    provider: "Waline";
    /**
     * 是否启用访问量
     *
     * Whether enable page views count by default
     *
     * @default true
     */
    pageview?: boolean;
    /**
     * Locale config for waline
     */
    walineLocales?: LocaleConfig<WalineLocaleData>;
}

declare const walineOption: WalineOptions;
declare const enableWaline: boolean;
declare const walineLocales: WalineLocaleConfig;
declare const _default: vue.DefineComponent<{}, () => VNode, {}, {}, {}, vue.ComponentOptionsMixin, vue.ComponentOptionsMixin, vue.EmitsOptions, string, vue.VNodeProps & vue.AllowedComponentProps & vue.ComponentCustomProps, Readonly<vue.ExtractPropTypes<{}>>, {}>;

export { _default as default, enableWaline, walineLocales, walineOption };
