import { BasePageFrontMatter, Author, ConvertLocaleConfig } from 'vuepress-shared';
import { LocaleConfig, PluginFunction } from '@vuepress/core';
import { WalineLocale, WalineInitOptions } from '@waline/client';

interface CommentPluginFrontmatter extends BasePageFrontMatter {
    /**
     * 是否启用评论
     *
     * Whether Enable Comment
     *
     * @default true
     */
    comment?: boolean;
    /**
     * @description Only available when using valine
     *
     * 是否启用访问量
     *
     * Whether enable pageviews
     *
     * @default true
     */
    pageview?: boolean;
}

interface BaseCommentOptions {
    /**
     * Default author
     *
     * 默认作者
     */
    author?: Author;
    /**
     * Whether enable comment by default
     *
     * 是否默认启用评论
     *
     * @default true
     */
    comment?: boolean;
    /**
     * The delay of dom operation, in ms
     *
     * If the theme you are using has a switching animation, it is recommended to configure this option to `Switch animation duration + 200`
     *
     * 进行 DOM 操作的延时，单位 ms
     *
     * 如果你使用的主题有切换动画，建议配置此选项为 `切换动画时长 + 200`
     *
     * @default 500
     */
    delay?: number;
}

interface GiscusOptions extends BaseCommentOptions {
    provider: "Giscus";
    /**
     * The name of repository to store discussions.
     *
     * 存放评论的仓库
     */
    repo: string;
    /**
     * The ID of repository to store discussions.
     *
     * 仓库 ID
     */
    repoId: string;
    /**
     * The name of the discussion category.
     *
     * 讨论分类
     */
    category: string;
    /**
     * The ID of the discussion category.
     *
     * 分类 ID
     */
    categoryId: string;
    /**
     * Page - discussion mapping.
     *
     * 页面 ↔️ discussion 映射关系
     *
     * @default "pathname"
     */
    mapping?: string;
    /**
     * Whether enable reactions or not
     *
     * 是否启用主帖子上的反应
     *
     * @default true
     */
    reactionsEnabled?: boolean;
    /**
     * Input position
     *
     * 输入框的位置
     *
     * @default 'top'
     */
    inputPosition?: "top" | "bottom";
}

interface DisableCommentOptions extends BaseCommentOptions {
    provider?: "None";
    comment?: never;
}

interface TwikooInitOptions {
    /**
     * Environment ID for tencloud or Link for Vercel
     *
     * 腾讯云环境链接或 Vercel Link
     */
    envId: string;
    /**
     * Tencloud region
     *
     * 腾讯云区域
     *
     * @default 'ap-shanghai'
     */
    region?: string;
}
interface TwikooOptions extends BaseCommentOptions, TwikooInitOptions {
    provider: "Twikoo";
}

declare type WalineLocaleData = Partial<WalineLocale>;
declare type WalineLocaleConfig = ConvertLocaleConfig<WalineLocaleData>;
interface WalineOptions extends BaseCommentOptions, Omit<WalineInitOptions, "el" | "comment"> {
    provider: "Waline";
    /**
     * 是否启用访问量
     *
     * Whether enable page views count by default
     *
     * @default true
     */
    pageview?: boolean;
    /**
     * Locale config for waline
     */
    walineLocales?: LocaleConfig<WalineLocaleData>;
}

/**
 * 评论选项
 *
 * Comment options
 */
declare type CommentOptions = GiscusOptions | TwikooOptions | WalineOptions | DisableCommentOptions;

/** Comment Plugin */
declare const commentPlugin: (options: CommentOptions, legacy?: boolean) => PluginFunction;

/**
 * Default locale config for Waline
 */
declare const walineLocales: WalineLocaleConfig;

export { CommentOptions, CommentPluginFrontmatter, GiscusOptions, TwikooInitOptions, TwikooOptions, WalineLocaleConfig, WalineLocaleData, WalineOptions, commentPlugin, commentPlugin as default, walineLocales };
